INSERT INTO `v1_1e2c80c695028e957f3da6864046812c72b8472ef82dc3da2fed990a0eb15abb` VALUES (0, 3000, 1, 270, 0, 16, "讐鼠・ステゴロのチュウザ", 315200, 0, 1.6, 315201, 0, "【物理】ネズミ一家の頼れる若頭。かつて拳一つ\nで漢を見せた猛き闘鼠は、一家の危機を救うため、\nヒカリモノに手を染めて抗争に身を投じる。\n\n・ユニオンバーストは、前から１番目のキャラを\n　中心とした範囲内に物理大ダメージ。\n・前から１番目のキャラに物理中ダメージを与え、\n　物理防御力を中ダウンさせる。\n・前から１番目のキャラに物理小ダメージ。");
