INSERT INTO `v1_1e2c80c695028e957f3da6864046812c72b8472ef82dc3da2fed990a0eb15abb` VALUES (0, 1, 0, "ドラゴンもどき", 1.8, 0, 0, "【ＭＯＤＥ２】偽竜の呼びかけに森の魔物たちが\n集結する。彼を守るべく死力を尽くす。\n\n・ゴーレム、トレント、ポイズンポッド\n　を倒すと、次のＭＯＤＥに移行する。\n\n・ドラゴンもどきは、目の前の敵１キャラへの\n　物理攻撃を行う。", 16, 308600, 308605, 1000, 270);
