INSERT INTO `v1_1e2c80c695028e957f3da6864046812c72b8472ef82dc3da2fed990a0eb15abb` VALUES (0, 1, 0, "黒の王ジャバウォック", 2.1, 0, 0, "【物理】世界を歪ませ狭界より顕現した黒の王。\n暗黒の瘴気を纏い不思議の国を蹂躙せんとする。\n\n・ユニオンバーストは、敵全体にキャラの最大Ｈ\n　Ｐに応じた魔法割合ダメージを与え、毒状態に\n　し、さらにノックバックさせる。\n　このスキルの状態異常はダメージを無効化して\n　も付与される。\n・前から２キャラに物理中ダメージを与え、物理\n　防御力とＴＰをダウンさせる。", 16, 311100, 311100, 1000, 270);
INSERT INTO `v1_1e2c80c695028e957f3da6864046812c72b8472ef82dc3da2fed990a0eb15abb` VALUES (0, 1, 0, "黒の王ジャバウォック", 2.1, 0, 0, "【物理】世界を歪ませ狭界より顕現した黒の王。\n暗黒の瘴気を纏い不思議の国を蹂躙せんとする。\n\n・ユニオンバーストは、敵全体にキャラの最大Ｈ\n　Ｐに応じた魔法割合ダメージを与え、毒状態に\n　し、さらにノックバックさせる。\n　このスキルの状態異常はダメージを無効化して\n　も付与される。\n・前から２キャラに物理中ダメージを与え、物理\n　防御力とＴＰを中ダウンさせる。", 16, 311100, 311101, 1000, 270);
INSERT INTO `v1_1e2c80c695028e957f3da6864046812c72b8472ef82dc3da2fed990a0eb15abb` VALUES (0, 1, 0, "黒の王ジャバウォック", 2.1, 0, 0, "【物理】世界を歪ませ狭界より顕現した黒の王。\n暗黒の瘴気を纏い不思議の国を蹂躙せんとする。\n\n・ユニオンバーストは、敵全体にキャラの最大Ｈ\n　Ｐに応じた魔法割合ダメージを与え、毒状態に\n　し、さらにノックバックさせる。\n　このスキルの状態異常はダメージを無効化して\n　も付与される。\n・前から２キャラに物理中ダメージを与え、物理\n　防御力とＴＰを中ダウンさせる。\n・前方範囲内のキャラに魔法中ダメージを与え、\n　魔法防御力を大ダウンさせる。\n　さらにキャラのＨＰが50％未満の場合、行動\n　速度をダウンさせる。", 16, 311100, 311102, 1000, 270);
INSERT INTO `v1_1e2c80c695028e957f3da6864046812c72b8472ef82dc3da2fed990a0eb15abb` VALUES (0, 1, 0, "黒の王ジャバウォック", 2.1, 0, 0, "【物理】世界を歪ませ狭界より顕現した黒の王。\n暗黒の瘴気を纏い不思議の国を蹂躙せんとする。\n\n・ユニオンバーストは、敵全体にキャラの最大Ｈ\n　Ｐに応じた魔法割合ダメージを与え、毒状態に\n　し、さらにノックバックさせる。\n　このスキルの状態異常はダメージを無効化して\n　も付与される。\n・前から２キャラに物理中ダメージを与え、物理\n　防御力とＴＰを中ダウンさせる。\n・前方範囲内のキャラに魔法中ダメージを与え、\n　魔法防御力を大ダウンさせる。\n　さらにキャラのＨＰが50％未満の場合、行動\n　速度をダウンさせる。\n・黒の王ジャバウォック自身のＨＰが35％を切\n　ると、さらに攻撃が激しく凶暴になる。", 16, 311100, 311103, 1000, 270);
INSERT INTO `v1_1e2c80c695028e957f3da6864046812c72b8472ef82dc3da2fed990a0eb15abb` VALUES (0, 1, 0, "黒の王ジャバウォック", 2.1, 0, 0, "【ＭＯＤＥ１】瘴黒の幕を身に纏い、黒の王が再\n臨する。闇と混沌を集約した障壁は、救世主の光\nの一射をも耐えるという。\n\n・バリアを張り、敵の攻撃を受け付けなくなる。\n　バリアはダメージを与えるたびに徐々に耐久性\n　を失っていき、2400000ダメージを与え\n　るとバリアは破壊される。\n・ユニオンバーストは、敵全体にキャラの最大Ｈ\n　Ｐに応じた魔法割合ダメージを与え、毒状態に\n　し、さらにノックバックさせる。\n　このスキルの状態異常はダメージを無効化して\n　も付与される。\n・前方範囲内のキャラに魔法中ダメージを与え、\n　魔法防御力を大ダウンさせる。", 16, 311101, 311104, 1000, 270);
INSERT INTO `v1_1e2c80c695028e957f3da6864046812c72b8472ef82dc3da2fed990a0eb15abb` VALUES (0, 1, 0, "黒の王ジャバウォック", 2.1, 0, 0, "【ＭＯＤＥ２】リノ、アユミ、ヒヨリのシャドウ\nを召喚。心の闇が具現化し、自己の弱さを露見さ\nせる。希望の心を取り戻せるか？今、問われる。\n\n・目の前の敵１キャラに物理ダメージを与える。", 16, 311100, 311105, 1000, 270);
INSERT INTO `v1_1e2c80c695028e957f3da6864046812c72b8472ef82dc3da2fed990a0eb15abb` VALUES (0, 1, 0, "黒の王ジャバウォック", 2.1, 0, 0, "【ＭＯＤＥ３】黒の王が終の力を解放し、世界は\n常闇へと堕ちていく。本の殆どの頁は黒に染まり\n救世主の物語は終焉を迎えようとする。\n\n・ユニオンバーストは、敵全体にキャラの最大Ｈ\n　Ｐに応じた魔法割合ダメージを与え、毒状態に\n　し、さらにノックバックさせる。\n　このスキルの状態異常はダメージを無効化して\n　も付与される。\n・前から２キャラに物理中ダメージを与え、物理\n　防御力とＴＰをダウンさせる。\n・前方範囲内のキャラに魔法中ダメージを与え、\n　魔法防御力を大ダウンさせる。\n　さらにキャラのＨＰが50％未満の場合、行動\n　速度を大ダウンさせる。\n・バトル開始直後に、さらに攻撃が激しく\n　凶暴になる。", 16, 311100, 311106, 1000, 270);
INSERT INTO `v1_1e2c80c695028e957f3da6864046812c72b8472ef82dc3da2fed990a0eb15abb` VALUES (0, 1, 0, "フラワーシュバリエ", 2.8, 0, 0, "【物理】大輪の花に擬態する魔物。\nその美しさに惹かれた、不用意な\n獲物の前に絶望の光が花開く。\n\n・前方３キャラに物理大ダメージを\n　与え、さらに敵全体に物理大\n　ダメージを与える。\n\n・前方２キャラに物理小ダメージを\n　与え、さらに自分の物理攻撃力を\n　中アップさせる。\n\n・前から１番目のキャラに物理中\n　ダメージを与え、物理防御力を\n　中ダウンさせる。\n\n・前から２番目のキャラに物理中\n　ダメージを与え、物理防御力を\n　中ダウンさせる。", 16, 311200, 311200, 3000, 270);
INSERT INTO `v1_1e2c80c695028e957f3da6864046812c72b8472ef82dc3da2fed990a0eb15abb` VALUES (0, 1, 0, "フラワーシュバリエ", 2.8, 0, 0, "【物理】大輪の花に擬態する魔物。\nその美しさに惹かれた、不用意な\n獲物の前に絶望の光が花開く。\n\n・前方３キャラに物理大ダメージを\n　与え、さらに敵全体に物理大\n　ダメージを与える。\n\n・前方２キャラに物理小ダメージを\n　与え、さらに自分の物理攻撃力を\n　中アップさせる。\n\n・前から１番目のキャラに物理中\n　ダメージを与え、物理防御力を\n　中ダウンさせる。\n\n・前から２番目のキャラに物理中\n　ダメージを与え、物理防御力を\n　中ダウンさせる。", 16, 311200, 311201, 3000, 270);
INSERT INTO `v1_1e2c80c695028e957f3da6864046812c72b8472ef82dc3da2fed990a0eb15abb` VALUES (0, 1, 0, "フラワーシュバリエ", 2.8, 0, 0, "【物理】大輪の花に擬態する魔物。その美しさに\n惹かれた、不用意な獲物の前に絶望の光が花開く。\n\n【特殊効果】\n・全スキルダメージＴＰ回復不可\n【ユニオンバースト】\n・前から１番目の敵と周囲に物理ダメージ\n・敵全体に魔法ダメージ（飛行効果無視）\n・与えた物理ダメージが高いほど：魔法ダメージ\n　量アップ\n【スキル１】\n・自身に物理攻撃力アップ\n・自身に物理／魔法防御力アップ\n・自身にＴＰ回復\n【通常攻撃】\n・前から１番目の敵と周囲の敵１体に物理\n　ダメージ", 16, 311202, 311202, 3000, 270);
INSERT INTO `v1_1e2c80c695028e957f3da6864046812c72b8472ef82dc3da2fed990a0eb15abb` VALUES (0, 1, 0, "フラワーシュバリエ", 2.8, 0, 0, "【物理】大輪の花に擬態する魔物。その美しさに\n惹かれた、不用意な獲物の前に絶望の光が花開く。\n\n【特殊効果】\n・全スキルダメージＴＰ回復不可\n【ユニオンバースト】\n・前から１番目の敵と周囲に物理ダメージ\n・敵全体に魔法ダメージ（飛行効果無視）\n・与えた物理ダメージが高いほど：魔法ダメージ\n　量アップ\n【スキル１】\n・自身に物理攻撃力アップ\n・自身に物理／魔法防御力アップ\n・自身にＴＰ回復\n【通常攻撃】\n・前から１番目の敵と周囲の敵１体に物理\n　ダメージ", 16, 311203, 311203, 3000, 270);
INSERT INTO `v1_1e2c80c695028e957f3da6864046812c72b8472ef82dc3da2fed990a0eb15abb` VALUES (0, 1, 0, "フラワーシュバリエ", 2.8, 0, 0, "【物理】大輪の花に擬態する魔物。その美しさに\n惹かれた、不用意な獲物の前に絶望の光が花開く。\n\n・環境効果【百花繚乱】を発動する。このスキル\n　の効果は敵、味方すべてに適用される。一定時\n　間が経過するごとに、敵、味方全体のＴＰを中\n　回復させる。\n・バトル開始時、自身の物理防御力と魔法防御力\n　を中アップさせる。この防御力アップ効果で上\n　昇した防御力は、バトル中に受ける防御力ダウ\n　ンスキルの影響を受けない。\n・バトル開始時、自身にＴＰダウン100％カッ\n　ト効果を付与する。\n・バトル開始時、敵全体のＨＰ吸収を特大ダウン\n　させる。\n・自身が一度の行動で受けるダメージ値を減衰さ\n　せる。ダメージ値が上昇するほど減衰率も上昇\n　する。\n・バトル開始時、敵全体にＴＰ上昇上限効果を付\n　与する。\n・フラワーシュバリエから受けるすべてのダメー\n　ジで発生する、パーティキャラのＴＰ回復量を\n　中ダウンさせる。\n・ユニオンバーストは、最も近い敵１キャラを中\n　心にした小範囲に物理特大ダメージを与える。\n　その後、敵全体にこのスキルで与えたダメージ\n　量に応じた物理ダメージを与える。このスキル\n　による全体ダメージでは、ＴＰが回復しない。\n・自身の物理攻撃力と物理防御力と魔法防御力を\n　特大アップさせ、ＴＰを中回復する。\n・通常攻撃は、最も近い敵１キャラを中心にした\n　小範囲内の敵最大２キャラに物理特大ダメージ\n　を与える。", 16, 311204, 311204, 3000, 270);
INSERT INTO `v1_1e2c80c695028e957f3da6864046812c72b8472ef82dc3da2fed990a0eb15abb` VALUES (0, 2, 0, "覚醒・浮気星", 2.8, 0, 0, "", 16, 311300, 311300, 3000, 270);
INSERT INTO `v1_1e2c80c695028e957f3da6864046812c72b8472ef82dc3da2fed990a0eb15abb` VALUES (0, 2, 0, "覚醒・浮気星", 2.8, 0, 0, "【魔法】ぽっかり空いた心のスキマに取り憑く\n星の精霊。過剰な自意識とピュアな下心で相手\nを惑わせる。\n\n・ユニオンバーストは、敵全体に浮気星が保持し\n　ている【ふた心】の数に応じた魔法割合ダメー\n　ジを与える。\n　また、浮気星の残りＨＰが50％未満の場合、\n　さらに与えるダメージがアップする。\n　スキル発動後、浮気星が保持している\n　【ふた心】の数は０になる。\n・一番後ろの敵を中心として、一定範囲に\n　魔法小ダメージを与え、さらに誘惑状態にして\n　物理攻撃力と魔法攻撃力を小アップさせる。\n　このスキルでダメージを与えた敵の数だけ、\n　浮気星に一定時間【ふた心】を追加する。\n・前方範囲内の敵をノックバックさせ、\n　魔法小ダメージを与える。\n　さらに誘惑状態にして物理攻撃力と魔法攻撃力\n　を小アップさせる。\n　このスキルでダメージを与えた敵の数だけ、\n　浮気星に一定時間【ふた心】を追加する。", 16, 311300, 311301, 3000, 270);
INSERT INTO `v1_1e2c80c695028e957f3da6864046812c72b8472ef82dc3da2fed990a0eb15abb` VALUES (0, 2, 0, "覚醒・浮気星", 3.0, 0, 0, "【魔法】ぽっかり空いた心のスキマに取り憑く\n星の精霊。過剰な自意識とピュアな下心で相手\nを惑わせる。\n\n・ユニオンバーストは、敵全体に浮気星が保持し\n　ている【ふた心】の数に応じた魔法割合ダメー\n　ジを与える。\n　また、浮気星の残りＨＰが50％未満の場合、\n　さらに与えるダメージがアップする。\n　スキル発動後、浮気星が保持している\n　【ふた心】の数は０になる。\n・一番後ろの敵を中心として、一定範囲に\n　魔法小ダメージを与え、さらに誘惑状態にして\n　物理攻撃力と魔法攻撃力を小アップさせる。\n　このスキルでダメージを与えた敵の数だけ、\n　浮気星に一定時間【ふた心】を追加する。\n・前方範囲内の敵をノックバックさせ、\n　魔法小ダメージを与える。\n　さらに誘惑状態にして物理攻撃力を小アップ、\n　魔法攻撃力を中アップさせる。\n　このスキルでダメージを与えた敵の数だけ、\n　浮気星に一定時間【ふた心】を追加する。", 16, 311300, 311302, 3000, 270);
