INSERT INTO `v1_1e2c80c695028e957f3da6864046812c72b8472ef82dc3da2fed990a0eb15abb` VALUES (0, 2, 0, "ミソラ", 2.0, 0, 0, "【魔法】ミソラクエスト最後の関門。「退治でき\nなかったら…ぜーんいん。ミナゴロシです♪」\n\n・ユニオンバーストは、敵全体にキャラの最大\n　ＨＰに応じた魔法割合ダメージを与える。\n・前から１番目、２番目、３番目の敵に魔法小ダ\n　メージを与える。\n・敵全体の魔法防御力を中ダウンさせ、火傷状態\n　を付与する。敵のＨＰが50％未満の場合、混\n　乱状態を付与する。", 16, 313702, 313702, 3000, 270);
