INSERT INTO `v1_1e2c80c695028e957f3da6864046812c72b8472ef82dc3da2fed990a0eb15abb` VALUES (0, 1, 0, "ちょっと苦手な宿題", 1.0, 0, 0, "誰にでもある苦手科目の宿題。ついつい後回しに\nしがちで、溜まってしまうとなかなか大変。\nここで立ち止まるわけにはいかないぞ！\n\n・バトル開始後、大量の宿題が降り注ぎ、\n　敵全体に物理中ダメージ。このスキル\n　によるダメージでは、キャラのＴＰが\n　回復しない。\n・バトル中、一定時間が経過するごとに\n　モンスターが押し寄せてくる。", 16, 314002, 314002, 3000, 270);
