INSERT INTO `v1_1e2c80c695028e957f3da6864046812c72b8472ef82dc3da2fed990a0eb15abb` VALUES (0, 1, 0, "ハイパー・ダッシュジョー", 1.6, 0, 0, "【物理】ジオ・ゲヘナに名を轟かせる大盗賊『グ\nロゴ団』の頭領。最新型の戦闘車輛と融合し、脅\n威的な機動力を以って裏切り者へと天誅を下す。\n\n・ユニオンバーストは、敵全体に魔法大ダメージ\n　と物理大ダメージを与える。\n・最も近い敵１キャラに魔法特大ダメージを与え\n　る。さらに敵全体に魔法大ダメージを与え、火\n　傷状態を付与する。このスキルにより付与され\n　た火傷のダメージでは、ＴＰが回復しない。\n・敵全体に物理大ダメージを与え、ＴＰを小ダウ\n　ンさせる。\n・最も近い敵１キャラに物理特大ダメージを与え\n　る。", 16, 322100, 322100, 3000, 270);
