INSERT INTO `v1_1e8435cc408adf368b8de7b5edac3aca1c7638e2df9a540ed333f960e4ca700d` VALUES ("これ、何と言う？", 5, "ツマンセル", 5, 12531, 12475, 12523, 5, 5, 1218, 5, 0, 1, 2, 12484, 12510, "ツーマンセル", "軍隊や警察において使われる言葉で、「二人一組」のこと", "ツーマンセル", 3);
