INSERT INTO `v1_1e8435cc408adf368b8de7b5edac3aca1c7638e2df9a540ed333f960e4ca700d` VALUES ("これ、何と言う？", 5, "サイレンサ", 5, 12524, 12531, 12469, 5, 5, 1220, 5, 0, 1, 2, 12469, 12452, "", "スパイ映画でもよく見る、銃の発砲音を抑えるために銃口につける装置。「サプレッサー」とも", "サイレンサー", 2);
