INSERT INTO `v1_1e8435cc408adf368b8de7b5edac3aca1c7638e2df9a540ed333f960e4ca700d` VALUES ("これ、何と言う？", 5, "ヘラクレス", 5, 12463, 12524, 12473, 5, 5, 6063, 5, 0, 6, 2, 12504, 12521, "", "カブトムシの中で最大のサイズを誇る種として、子どもたちからも絶大な人気を誇る昆虫", "ヘラクレスオオカブト", 1);
INSERT INTO `v1_1e8435cc408adf368b8de7b5edac3aca1c7638e2df9a540ed333f960e4ca700d` VALUES ("これ、何と言う？", 5, "アブラムシ", 5, 12521, 12512, 12471, 5, 5, 6064, 5, 0, 6, 2, 12450, 12502, "", "昔の子どもたちがこの虫の体液を髪に塗り、テカテカさせて遊んでいたことに由来する虫の名前", "アブラムシ", 1);
INSERT INTO `v1_1e8435cc408adf368b8de7b5edac3aca1c7638e2df9a540ed333f960e4ca700d` VALUES ("これ、何と言う？", 4, "泣きぼくろ", 4, 12412, 12367, 12429, 4, 6, 6065, 4, 0, 6, 2, 27875, 12365, "", "目のすぐ下のあたりにあるほくろの通称", "泣きぼくろ", 1);
INSERT INTO `v1_1e8435cc408adf368b8de7b5edac3aca1c7638e2df9a540ed333f960e4ca700d` VALUES ("これ、何と言う？", 5, "シルババク", 5, 12496, 12496, 12463, 5, 5, 6066, 5, 0, 6, 2, 12471, 12523, "シルバーバック", "年を重ねたオスのゴリラの背中の毛が白くなる現象。群れの中のボス個体によく見られる", "シルバーバック", 3);
INSERT INTO `v1_1e8435cc408adf368b8de7b5edac3aca1c7638e2df9a540ed333f960e4ca700d` VALUES ("これ、何と言う？", 5, "カメレオン", 5, 12524, 12458, 12531, 5, 5, 6067, 5, 0, 6, 2, 12459, 12513, "", "身体の色を自在に変化させることでおなじみの爬虫類。演技の幅が広い俳優や声優のたとえにも", "カメレオン", 1);
INSERT INTO `v1_1e8435cc408adf368b8de7b5edac3aca1c7638e2df9a540ed333f960e4ca700d` VALUES ("これ、何と言う？", 5, "オニヤンマ", 5, 12516, 12531, 12510, 5, 5, 6068, 5, 0, 6, 2, 12458, 12491, "", "非常に大きなトンボの一種。「鬼」の服装とよく似た、黒と黄色のシマシマ模様が特徴的", "オニヤンマ", 1);
INSERT INTO `v1_1e8435cc408adf368b8de7b5edac3aca1c7638e2df9a540ed333f960e4ca700d` VALUES ("これ、何と言う？", 5, "グルミング", 5, 12511, 12531, 12464, 5, 5, 6069, 5, 0, 6, 2, 12464, 12523, "グルーミング", "猫などの動物の習性として知られる「毛づくろい」。英語でなんと言う？", "グルーミング", 3);
INSERT INTO `v1_1e8435cc408adf368b8de7b5edac3aca1c7638e2df9a540ed333f960e4ca700d` VALUES ("これ、何と言う？", 5, "マングース", 5, 12464, 12540, 12473, 5, 5, 6070, 5, 0, 6, 2, 12510, 12531, "", "一般に「ハブ」の天敵というイメージを持たれている、体長数十センチほどの哺乳類", "マングース", 1);
INSERT INTO `v1_1e8435cc408adf368b8de7b5edac3aca1c7638e2df9a540ed333f960e4ca700d` VALUES ("これ、何と言う？", 5, "ギンヤンマ", 5, 12516, 12531, 12510, 5, 5, 6071, 5, 0, 6, 2, 12462, 12531, "", "トンボの一種。身体は黄緑色だが、よく見るとキレイな銀色の模様があることからこう呼ばれる", "ギンヤンマ", 2);
INSERT INTO `v1_1e8435cc408adf368b8de7b5edac3aca1c7638e2df9a540ed333f960e4ca700d` VALUES ("これ、何と言う？", 5, "カモミール", 5, 12511, 12540, 12523, 5, 5, 6072, 5, 0, 6, 2, 12459, 12514, "", "「カミツレ」とも呼ばれるハーブの一種。りんごのような香りがあり、お茶としてよく好まれる", "カモミール", 3);
INSERT INTO `v1_1e8435cc408adf368b8de7b5edac3aca1c7638e2df9a540ed333f960e4ca700d` VALUES ("これ、何と言う？", 5, "ホタルイカ", 5, 12523, 12452, 12459, 5, 5, 6073, 5, 0, 6, 2, 12507, 12479, "", "指先ほどのサイズの小さなイカ。青白く発光する性質があり、また食用としても好まれる", "ホタルイカ", 2);
INSERT INTO `v1_1e8435cc408adf368b8de7b5edac3aca1c7638e2df9a540ed333f960e4ca700d` VALUES ("これ、何と言う？", 5, "キンメダイ", 5, 12513, 12480, 12452, 5, 5, 6074, 5, 0, 6, 2, 12461, 12531, "", "高級魚や縁起物としておなじみのタイの一種。名前は、目が金色に輝くことから", "キンメダイ", 1);
INSERT INTO `v1_1e8435cc408adf368b8de7b5edac3aca1c7638e2df9a540ed333f960e4ca700d` VALUES ("これ、何と言う？", 5, "グソクムシ", 5, 12463, 12512, 12471, 5, 5, 6075, 5, 0, 6, 2, 12464, 12477, "", "深海に生息する甲殻類。姿はダンゴムシに似ていて、大きなものは体長50cmを超える", "ダイオウグソクムシ", 3);
INSERT INTO `v1_1e8435cc408adf368b8de7b5edac3aca1c7638e2df9a540ed333f960e4ca700d` VALUES ("これ、何と言う？", 5, "チンアナゴ", 5, 12450, 12490, 12468, 5, 5, 6076, 5, 0, 6, 2, 12481, 12531, "", "水族館でも人気なアナゴの一種。砂底から身体を出し、群れで同じ方向をじっと向く姿が特徴的", "チンアナゴ", 2);
INSERT INTO `v1_1e8435cc408adf368b8de7b5edac3aca1c7638e2df9a540ed333f960e4ca700d` VALUES ("これ、何と言う？", 5, "ラフレシア", 5, 12524, 12471, 12450, 5, 5, 6077, 5, 0, 6, 2, 12521, 12501, "", "「世界最大の花」としておなじみの、熱帯地域に生息する花。強い悪臭を放つことでも有名", "ラフレシア", 1);
INSERT INTO `v1_1e8435cc408adf368b8de7b5edac3aca1c7638e2df9a540ed333f960e4ca700d` VALUES ("これ、何と言う？", 5, "ベニテング", 5, 12486, 12531, 12464, 5, 5, 6078, 5, 0, 6, 2, 12505, 12491, "", "真っ赤なカサに白い点のある、キノコの一種。毒があり、食べると幻覚・腹痛などの症状が出る", "ベニテングタケ", 3);
INSERT INTO `v1_1e8435cc408adf368b8de7b5edac3aca1c7638e2df9a540ed333f960e4ca700d` VALUES ("これ、何と言う？", 5, "コノハズク", 5, 12495, 12474, 12463, 5, 5, 6079, 5, 0, 6, 2, 12467, 12494, "", "「フクロウ」の一種。身の危険を感じたときに身体を細く縮めて、木の枝に擬態する習性がある", "コノハズク", 3);
INSERT INTO `v1_1e8435cc408adf368b8de7b5edac3aca1c7638e2df9a540ed333f960e4ca700d` VALUES ("これ、何と言う？", 5, "サンショウ", 5, 12471, 12519, 12454, 5, 5, 6080, 5, 0, 6, 2, 12469, 12531, "", "両生類の一種。防衛のために出す粘液の匂いが、和食に使うとあるスパイスに似ているのが由来", "サンショウウオ", 3);
INSERT INTO `v1_1e8435cc408adf368b8de7b5edac3aca1c7638e2df9a540ed333f960e4ca700d` VALUES ("これ、何と言う？", 5, "フキノトウ", 5, 12494, 12488, 12454, 5, 5, 6081, 5, 0, 6, 2, 12501, 12461, "", "「蕗（フキ）」が大きく成長する前の、つぼみの部分。春の山菜として、天ぷらなどで好まれる", "フキノトウ", 3);
INSERT INTO `v1_1e8435cc408adf368b8de7b5edac3aca1c7638e2df9a540ed333f960e4ca700d` VALUES ("これ、何と言う？", 5, "サトウキビ", 5, 12454, 12461, 12499, 5, 5, 6082, 5, 0, 6, 2, 12469, 12488, "", "砂糖の原料となる、イネ科の植物", "サトウキビ", 1);
