INSERT INTO `v1_1e8435cc408adf368b8de7b5edac3aca1c7638e2df9a540ed333f960e4ca700d` VALUES ("これ、何と言う？", 5, "オットセイ", 5, 12488, 12475, 12452, 5, 5, 6202, 5, 0, 6, 2, 12458, 12483, "", "姿が似ていることで「アシカ」と混同されがちな哺乳類。分類上も、「アシカ科」に含まれる", "オットセイ", 2);
INSERT INTO `v1_1e8435cc408adf368b8de7b5edac3aca1c7638e2df9a540ed333f960e4ca700d` VALUES ("これ、何と言う？", 5, "アジアゾウ", 5, 12450, 12478, 12454, 5, 5, 6203, 5, 0, 6, 2, 12450, 12472, "", "「象」の種類、1つは「アフリカゾウ」で、もう一つは？", "アジアゾウ", 1);
INSERT INTO `v1_1e8435cc408adf368b8de7b5edac3aca1c7638e2df9a540ed333f960e4ca700d` VALUES ("これ、何と言う？", 5, "ベニシジミ", 5, 12471, 12472, 12511, 5, 5, 6204, 5, 0, 6, 2, 12505, 12491, "", "ちょうちょの種類の一つ。色は紅色で、また羽のシルエットが「とある貝類」に似ている", "ベニシジミ", 3);
INSERT INTO `v1_1e8435cc408adf368b8de7b5edac3aca1c7638e2df9a540ed333f960e4ca700d` VALUES ("これ、何と言う？", 5, "ジュウサン", 5, 12454, 12469, 12531, 5, 5, 6205, 5, 0, 6, 2, 12472, 12517, "", "テントウムシの一種。身体に13個の点々模様がある", "ジュウサンホシテントウ", 2);
INSERT INTO `v1_1e8435cc408adf368b8de7b5edac3aca1c7638e2df9a540ed333f960e4ca700d` VALUES ("これ、何と言う？", 5, "ニジュウヤ", 5, 12517, 12454, 12516, 5, 5, 6206, 5, 0, 6, 2, 12491, 12472, "", "テントウムシの一種。身体に28個の点々模様がある", "ニジュウヤホシテントウ", 3);
INSERT INTO `v1_1e8435cc408adf368b8de7b5edac3aca1c7638e2df9a540ed333f960e4ca700d` VALUES ("これ、何と言う？", 5, "アニサキス", 5, 12469, 12461, 12473, 5, 5, 6207, 5, 0, 6, 2, 12450, 12491, "", "生の魚を食べる際にはよく注意する必要がある、有名な寄生虫。白い糸くずのような姿", "アニサキス", 2);
INSERT INTO `v1_1e8435cc408adf368b8de7b5edac3aca1c7638e2df9a540ed333f960e4ca700d` VALUES ("これ、何と言う？", 4, "わたりどり", 4, 12426, 12393, 12426, 4, 4, 6208, 4, 0, 6, 2, 12431, 12383, "", "季節の移ろいに合わせて長距離を移動する鳥たちの総称。ハクチョウなどが有名", "わたりどり", 1);
INSERT INTO `v1_1e8435cc408adf368b8de7b5edac3aca1c7638e2df9a540ed333f960e4ca700d` VALUES ("これ、何と言う？", 5, "フンボルト", 5, 12508, 12523, 12488, 5, 5, 6209, 5, 0, 6, 2, 12501, 12531, "", "ペンギンの一種で、身体を横切る黒いラインが特徴的。動物園で見るペンギンはこれが最も多い", "フンボルトペンギン", 3);
INSERT INTO `v1_1e8435cc408adf368b8de7b5edac3aca1c7638e2df9a540ed333f960e4ca700d` VALUES ("これ、何と言う？", 5, "エボシドリ", 5, 12471, 12489, 12522, 5, 5, 6210, 5, 0, 6, 2, 12456, 12508, "", "鳥の一種。その名の通り、頭の羽が「烏帽子」を被っているようなシルエットをしている", "エボシドリ", 3);
INSERT INTO `v1_1e8435cc408adf368b8de7b5edac3aca1c7638e2df9a540ed333f960e4ca700d` VALUES ("これ、何と言う？", 5, "ネザランド", 5, 12521, 12531, 12489, 5, 5, 6211, 5, 0, 6, 2, 12493, 12470, "ネザーランドドワーフ", "ウサギの種類として最もよく知られたもの。身体は小さめで、耳がピンと立っている", "ネザーランドドワーフ", 3);
INSERT INTO `v1_1e8435cc408adf368b8de7b5edac3aca1c7638e2df9a540ed333f960e4ca700d` VALUES ("これ、何と言う？", 5, "ホーランド", 5, 12521, 12531, 12489, 5, 5, 6212, 5, 0, 6, 2, 12507, 12540, "", "ウサギの種類としてよく知られた一つ。最大の特徴は、垂れ下がった大きな耳", "ホーランドロップ", 3);
INSERT INTO `v1_1e8435cc408adf368b8de7b5edac3aca1c7638e2df9a540ed333f960e4ca700d` VALUES ("これ、何と言う？", 5, "フェレット", 5, 12524, 12483, 12488, 5, 5, 6213, 5, 0, 6, 2, 12501, 12455, "", "ペットとしてもよく親しまれるイタチの一種。長い胴体に短い脚の独特なフォルムをしている", "フェレット", 2);
INSERT INTO `v1_1e8435cc408adf368b8de7b5edac3aca1c7638e2df9a540ed333f960e4ca700d` VALUES ("これ、何と言う？", 5, "マントヒヒ", 5, 12488, 12498, 12498, 5, 5, 6214, 5, 0, 6, 2, 12510, 12531, "", "大別すると猿の仲間にあたる動物で、マントを羽織っているように見える特徴的な体毛を持つ", "マントヒヒ", 2);
INSERT INTO `v1_1e8435cc408adf368b8de7b5edac3aca1c7638e2df9a540ed333f960e4ca700d` VALUES ("これ、何と言う？", 5, "シロナガス", 5, 12490, 12460, 12473, 5, 5, 6215, 5, 0, 6, 2, 12471, 12525, "", "最も大きい動物としてよく知られる種類のクジラ", "シロナガスクジラ", 2);
INSERT INTO `v1_1e8435cc408adf368b8de7b5edac3aca1c7638e2df9a540ed333f960e4ca700d` VALUES ("これ、何と言う？", 5, "シロイルカ", 5, 12452, 12523, 12459, 5, 5, 6216, 5, 0, 6, 2, 12471, 12525, "", "普通のイルカほど口が尖っておらず、のほほんとした顔立ちで人気の真っ白なイルカ", "シロイルカ", 3);
INSERT INTO `v1_1e8435cc408adf368b8de7b5edac3aca1c7638e2df9a540ed333f960e4ca700d` VALUES ("これ、何と言う？", 4, "しろうさぎ", 4, 12358, 12373, 12366, 4, 4, 6217, 4, 0, 6, 2, 12375, 12429, "", "神話に登場する、有名な動物", "いなばのしろうさぎ", 2);
INSERT INTO `v1_1e8435cc408adf368b8de7b5edac3aca1c7638e2df9a540ed333f960e4ca700d` VALUES ("これ、何と言う？", 5, "スコピオン", 5, 12500, 12458, 12531, 5, 5, 6218, 5, 0, 6, 2, 12473, 12467, "さそり＝スコーピオン", "カタカナでの呼び方は？", "さそり＝スコーピオン", 1);
INSERT INTO `v1_1e8435cc408adf368b8de7b5edac3aca1c7638e2df9a540ed333f960e4ca700d` VALUES ("これ、何と言う？", 4, "ちからこぶ", 4, 12425, 12371, 12406, 4, 4, 6219, 4, 0, 6, 2, 12385, 12363, "", "腕を曲げて力を込めた時に、二の腕のポッコリと盛り上がる部分の俗称", "ちからこぶ", 2);
INSERT INTO `v1_1e8435cc408adf368b8de7b5edac3aca1c7638e2df9a540ed333f960e4ca700d` VALUES ("これ、何と言う？", 4, "さるかに合", 6, 12363, 12395, 21512, 4, 4, 6220, 4, 0, 6, 2, 12373, 12427, "", "昔ばなしの一つ。とある生き物が、自分の親の仇を討つために、様々な仲間を集めて仕返しをする", "さるかに合戦", 2);
INSERT INTO `v1_1e8435cc408adf368b8de7b5edac3aca1c7638e2df9a540ed333f960e4ca700d` VALUES ("これ、何と言う？", 5, "チンパンジ", 5, 12497, 12531, 12472, 5, 5, 6221, 5, 0, 6, 2, 12481, 12531, "", "よく親しまれた替え歌の歌詞。ある動物の名前が入る", "♪サル、ゴリラ、チンパンジー", 1);
INSERT INTO `v1_1e8435cc408adf368b8de7b5edac3aca1c7638e2df9a540ed333f960e4ca700d` VALUES ("これ、何と言う？", 5, "アオイソメ", 5, 12452, 12477, 12513, 5, 5, 6222, 5, 0, 6, 2, 12450, 12458, "", "釣り人には釣り餌として非常におなじみの、ミミズのような虫", "アオイソメ", 3);
INSERT INTO `v1_1e8435cc408adf368b8de7b5edac3aca1c7638e2df9a540ed333f960e4ca700d` VALUES ("これ、何と言う？", 5, "鋼メンタル", 5, 12531, 12479, 12523, 5, 6, 6223, 5, 0, 6, 2, 37628, 12513, "", "精神力が強く、何があってもへこたれない人の俗的な呼び方。「とある金属」にたとえられる", "鋼メンタル", 3);
INSERT INTO `v1_1e8435cc408adf368b8de7b5edac3aca1c7638e2df9a540ed333f960e4ca700d` VALUES ("これ、何と言う？", 5, "マゾヒスト", 5, 12498, 12473, 12488, 5, 5, 6224, 5, 6224, 6, 1, 12510, 12478, "", "クウカに代表される「ドM」という言葉、この「M」は何という言葉の略？", "マゾヒスト", 3);
INSERT INTO `v1_1e8435cc408adf368b8de7b5edac3aca1c7638e2df9a540ed333f960e4ca700d` VALUES ("これ、何と言う？", 5, "コアトルス", 5, 12488, 12523, 12473, 5, 5, 6225, 5, 0, 6, 2, 12467, 12450, "", "太古に実在した、非常に大きな翼竜。神話に登場する神様から名付けられた", "ケツァルコアトルス", 3);
INSERT INTO `v1_1e8435cc408adf368b8de7b5edac3aca1c7638e2df9a540ed333f960e4ca700d` VALUES ("これ、何と言う？", 5, "アナツバメ", 5, 12484, 12496, 12513, 5, 5, 6226, 5, 0, 6, 2, 12450, 12490, "", "ほら穴の中に巣を作ることが多いツバメで、高級食材である「ツバメの巣」はこの種のもの", "アナツバメ", 3);
INSERT INTO `v1_1e8435cc408adf368b8de7b5edac3aca1c7638e2df9a540ed333f960e4ca700d` VALUES ("これ、何と言う？", 5, "タンチョウ", 5, 12481, 12519, 12454, 5, 5, 6227, 5, 0, 6, 2, 12479, 12531, "", "鳥の「ツル」で、最もなじみの深い種。頭のてっぺん（頂）が、丹色であることに由来する", "タンチョウヅル", 3);
INSERT INTO `v1_1e8435cc408adf368b8de7b5edac3aca1c7638e2df9a540ed333f960e4ca700d` VALUES ("これ、何と言う？", 5, "ルリタテハ", 5, 12479, 12486, 12495, 5, 5, 6228, 5, 0, 6, 2, 12523, 12522, "", "ちょうちょの種類の一つ。キレイな瑠璃色の羽を立てた状態で休むのが特徴", "ルリタテハ", 3);
INSERT INTO `v1_1e8435cc408adf368b8de7b5edac3aca1c7638e2df9a540ed333f960e4ca700d` VALUES ("これ、何と言う？", 5, "プリンセス", 5, 12531, 12475, 12473, 5, 5, 6229, 5, 6229, 6, 1, 12503, 12522, "", "特定の相手の能力\nを高める権能を持\nった存在", "プリンセスナイト", 1);
INSERT INTO `v1_1e8435cc408adf368b8de7b5edac3aca1c7638e2df9a540ed333f960e4ca700d` VALUES ("このパネルの「プリコネ読み」は？", 5, "ネコむすめ", 4, 12416, 12377, 12417, 4, 5, 6230, 4, 6230, 6, 1, 12493, 12467, "", "", "ネコむすめ", 3);
INSERT INTO `v1_1e8435cc408adf368b8de7b5edac3aca1c7638e2df9a540ed333f960e4ca700d` VALUES ("これ、何と言う？", 6, "絶滅危惧種", 6, 21361, 24807, 31278, 6, 6, 6231, 6, 0, 6, 2, 32118, 28357, "", "数が減少し、絶滅の危機に晒されている生物", "絶滅危惧種", 2);
INSERT INTO `v1_1e8435cc408adf368b8de7b5edac3aca1c7638e2df9a540ed333f960e4ca700d` VALUES ("これ、何と言う？", 5, "アブラゼミ", 5, 12521, 12476, 12511, 5, 5, 6232, 5, 0, 6, 2, 12450, 12502, "", "よく知られたセミの一種。「ジージー」という鳴き声が、揚げ物料理をするときの音に似ている", "アブラゼミ", 1);
INSERT INTO `v1_1e8435cc408adf368b8de7b5edac3aca1c7638e2df9a540ed333f960e4ca700d` VALUES ("これ、何と言う？", 5, "アキレス腱", 6, 12524, 12473, 33137, 5, 5, 6233, 5, 0, 6, 2, 12450, 12461, "", "足首の後ろ側にある大きな腱で、神話上のとある英雄の、唯一の弱点として描かれている", "アキレス腱", 1);
INSERT INTO `v1_1e8435cc408adf368b8de7b5edac3aca1c7638e2df9a540ed333f960e4ca700d` VALUES ("これ、何と言う？", 5, "ナルシスト", 5, 12471, 12473, 12488, 5, 5, 6234, 5, 6234, 6, 1, 12490, 12523, "", "自己愛が非常に強く、自分に陶酔した人、英語にすると？", "ナルシスト", 1);
INSERT INTO `v1_1e8435cc408adf368b8de7b5edac3aca1c7638e2df9a540ed333f960e4ca700d` VALUES ("左のキャラは右のキャラを何と呼ぶ？", 4, "だんちょう", 4, 12385, 12423, 12358, 4, 4, 6235, 4, 6235, 6, 1, 12384, 12435, "", "", "だんちょう", 3);
