INSERT INTO `v1_1e8435cc408adf368b8de7b5edac3aca1c7638e2df9a540ed333f960e4ca700d` VALUES ("これ、何と言う？", 4, "やたがらす", 4, 12364, 12425, 12377, 4, 4, 6237, 4, 0, 6, 2, 12420, 12383, "", "「古事記」などに登場する、三本足の神聖なカラス", "やたがらす", 3);
INSERT INTO `v1_1e8435cc408adf368b8de7b5edac3aca1c7638e2df9a540ed333f960e4ca700d` VALUES ("これ、何と言う？", 4, "さごじょう", 4, 12376, 12423, 12358, 4, 4, 6238, 4, 0, 6, 2, 12373, 12372, "", "「西遊記」で孫悟空と行動を共にする、河童の姿のキャラクター", "さごじょう", 2);
INSERT INTO `v1_1e8435cc408adf368b8de7b5edac3aca1c7638e2df9a540ed333f960e4ca700d` VALUES ("これ、何と言う？", 5, "ディーラー", 5, 12540, 12521, 12540, 5, 5, 6239, 5, 0, 6, 2, 12487, 12451, "", "カジノで、ルーレットを回したりカードを配ったりとゲームの進行を行う役目", "ディーラー", 2);
INSERT INTO `v1_1e8435cc408adf368b8de7b5edac3aca1c7638e2df9a540ed333f960e4ca700d` VALUES ("これ、何と言う？", 5, "エンプレス", 5, 12503, 12524, 12473, 5, 5, 6240, 5, 0, 6, 2, 12456, 12531, "", "「皇帝」は「エンペラー」。では「女帝」は？", "エンプレス", 3);
INSERT INTO `v1_1e8435cc408adf368b8de7b5edac3aca1c7638e2df9a540ed333f960e4ca700d` VALUES ("これ、何と言う？", 5, "ガンスミス", 5, 12473, 12511, 12473, 5, 5, 6241, 5, 0, 6, 2, 12460, 12531, "", "銃のメンテナンスを職業として行う人のこと", "ガンスミス", 3);
INSERT INTO `v1_1e8435cc408adf368b8de7b5edac3aca1c7638e2df9a540ed333f960e4ca700d` VALUES ("これ、何と言う？", 5, "ハイビスカ", 5, 12499, 12473, 12459, 5, 5, 6242, 5, 0, 6, 1, 12495, 12452, "", "熱帯の地域を象徴する花で、カオリの故郷でも親しまれる。赤や黄色の鮮やかな花びらをつける", "ハイビスカス", 1);
INSERT INTO `v1_1e8435cc408adf368b8de7b5edac3aca1c7638e2df9a540ed333f960e4ca700d` VALUES ("これ、何と言う？", 5, "オウィスプ", 5, 12451, 12473, 12503, 5, 5, 6243, 5, 0, 6, 2, 12458, 12454, "ウィル・オ・ウィスプ", "「ひとだま」と呼ばれる超常現象、英語で言うと？", "ウィル・オ・ウィスプ", 3);
INSERT INTO `v1_1e8435cc408adf368b8de7b5edac3aca1c7638e2df9a540ed333f960e4ca700d` VALUES ("これ、何と言う？", 5, "ニシキゴイ", 5, 12461, 12468, 12452, 5, 5, 6244, 5, 0, 6, 2, 12491, 12471, "", "魚の「鯉」の中でも観賞用として好まれる種で、高級な織物の柄によく似ていることが由来", "ニシキゴイ", 2);
INSERT INTO `v1_1e8435cc408adf368b8de7b5edac3aca1c7638e2df9a540ed333f960e4ca700d` VALUES ("これ、何と言う？", 5, "レサパンダ", 5, 12497, 12531, 12480, 5, 5, 6245, 5, 0, 6, 2, 12524, 12469, "レッサーパンダ", "「小さなパンダ」が由来の、動物園の人気者", "レッサーパンダ", 1);
INSERT INTO `v1_1e8435cc408adf368b8de7b5edac3aca1c7638e2df9a540ed333f960e4ca700d` VALUES ("これ、何と言う？", 5, "アナコンダ", 5, 12467, 12531, 12480, 5, 5, 6246, 5, 0, 6, 2, 12450, 12490, "", "ヘビの一種で、大きなものでは数メートルもある。映画で恐ろしいヘビとして描かれることが多い", "アナコンダ", 1);
INSERT INTO `v1_1e8435cc408adf368b8de7b5edac3aca1c7638e2df9a540ed333f960e4ca700d` VALUES ("これ、何と言う？", 5, "タランチュ", 5, 12531, 12481, 12517, 5, 5, 6247, 5, 0, 6, 2, 12479, 12521, "", "「毒グモの代名詞」としてよく知られる大型のクモ。しかし、ペットとして好む人も多い", "タランチュラ", 1);
INSERT INTO `v1_1e8435cc408adf368b8de7b5edac3aca1c7638e2df9a540ed333f960e4ca700d` VALUES ("これ、何と言う？", 5, "スイギュウ", 5, 12462, 12517, 12454, 5, 5, 6248, 5, 0, 6, 2, 12473, 12452, "", "「牛」の一種で、地域によって家畜としてもなじみ深い動物。その名の通り、水辺を好む", "スイギュウ", 1);
INSERT INTO `v1_1e8435cc408adf368b8de7b5edac3aca1c7638e2df9a540ed333f960e4ca700d` VALUES ("これ、何と言う？", 5, "ハムスター", 5, 12473, 12479, 12540, 5, 5, 6249, 5, 0, 6, 2, 12495, 12512, "", "ネズミの仲間にあたる動物で、ヒマワリの種などを口いっぱいに頬張る姿が象徴的", "ハムスター", 1);
INSERT INTO `v1_1e8435cc408adf368b8de7b5edac3aca1c7638e2df9a540ed333f960e4ca700d` VALUES ("これ、何と言う？", 5, "ハリネズミ", 5, 12493, 12474, 12511, 5, 5, 6250, 5, 0, 6, 2, 12495, 12522, "", "身を守るためのトゲトゲの体毛が非常に特徴的な動物。愛好家も多く、英名は「ヘッジホッグ」", "ハリネズミ", 1);
INSERT INTO `v1_1e8435cc408adf368b8de7b5edac3aca1c7638e2df9a540ed333f960e4ca700d` VALUES ("これ、何と言う？", 5, "ヒョウモン", 5, 12454, 12514, 12531, 5, 5, 6251, 5, 0, 6, 2, 12498, 12519, "", "猛毒を持つタコ。蛍光色のヒョウ柄の模様が、恐ろしさを助長している", "ヒョウモンダコ", 3);
INSERT INTO `v1_1e8435cc408adf368b8de7b5edac3aca1c7638e2df9a540ed333f960e4ca700d` VALUES ("これ、何と言う？", 5, "カメレオン", 5, 12524, 12458, 12531, 5, 5, 6252, 5, 6252, 6, 1, 12459, 12513, "", "ネネカといつも一緒にいる動物", "カメレオン", 3);
