INSERT INTO `v1_1f3ed0bf56f6bf2dece0b2e118fc681f190cb21d2d20b1a729553d63998e603c` VALUES ("エリコ", 1, 100, 0.0, 7, 10.85, "訓練成功、おめでとうございます\n褒めるべき時にはしっかり褒めるのも\n教官の務めですものね。それとも、\n罵られる方が好みですか？　クスクス", "2030/12/17 14:59:59", 24, 10142, "2024/03/31 12:00:00", 129901, 0.0, 100);
INSERT INTO `v1_1f3ed0bf56f6bf2dece0b2e118fc681f190cb21d2d20b1a729553d63998e603c` VALUES ("エリコ", 1, 101, 0.0, 2, 11.15, "厳しい訓練も、終わってみれば\n短く感じるものですわね。\n正直、私は暴れ足りないくらい…\nいえ、何でもありませんわ。クスクス", "2030/12/17 14:59:59", 24, 10142, "2024/03/31 12:00:00", 129901, 0.0, 101);
INSERT INTO `v1_1f3ed0bf56f6bf2dece0b2e118fc681f190cb21d2d20b1a729553d63998e603c` VALUES ("エリコ", 7, 102, 0.0, 5, 6.4, "行く手を阻む忌々しい邪魔者を\n蹴散らして、見事ゴールイン…！\nまるで私とあのおかたの人生を\n象徴するような道のりでした…", "2030/12/17 14:59:59", 24, 10142, "2024/03/31 12:00:00", 129901, 0.0, 102);
INSERT INTO `v1_1f3ed0bf56f6bf2dece0b2e118fc681f190cb21d2d20b1a729553d63998e603c` VALUES ("エリコ", 3, 103, 0.0, 3, 0.0, "まったくもって不甲斐ない結果です\nしかし、挽回の機会はまだありますわ\n次はゴミ虫の地位から脱却できるよう\n死力を尽くしなさい。いいですね？", "2030/12/17 14:59:59", 24, 10142, "2024/03/31 12:00:00", 129901, 0.0, 103);
INSERT INTO `v1_1f3ed0bf56f6bf2dece0b2e118fc681f190cb21d2d20b1a729553d63998e603c` VALUES ("エリコ", 2, 104, 0.0, 8, 2.43, "クスクス…ああご心配なさらず\nなんとも情けない成績を残す輩には\nどんな罰が相応しいか、考えている\nだけですので…クスクスクス", "2030/12/17 14:59:59", 24, 10142, "2024/03/31 12:00:00", 129901, 0.0, 104);
INSERT INTO `v1_1f3ed0bf56f6bf2dece0b2e118fc681f190cb21d2d20b1a729553d63998e603c` VALUES ("エリコ", 4, 105, 0.0, 3, 9.08, "私が教官として認められなければ、\nあのおかたを失望させてしまう…\nそれだけはあってはなりません\nあなた方も、わかっていますわね？", "2030/12/17 14:59:59", 24, 10142, "2024/03/31 12:00:00", 129901, 0.0, 105);
INSERT INTO `v1_1f3ed0bf56f6bf2dece0b2e118fc681f190cb21d2d20b1a729553d63998e603c` VALUES ("エリコ", 6, 106, 0.0, 6, 0.0, "まずまずの結果ではありますが、\nこの程度で満足してもらっては\n困りますわ。エリコ班の一員として、\nより高みを目指していただきますので", "2030/12/17 14:59:59", 24, 10142, "2024/03/31 12:00:00", 129901, 0.0, 106);
INSERT INTO `v1_1f3ed0bf56f6bf2dece0b2e118fc681f190cb21d2d20b1a729553d63998e603c` VALUES ("エリコ", 3, 107, 0.0, 8, 10.6, "私の指導を受けながら、この結果は…\nもっと真剣に考えねばなりませんね\nあなた方が怠けずにいるには、\nどんなしつけが必要かを…クスクス", "2030/12/17 14:59:59", 24, 10142, "2024/03/31 12:00:00", 129901, 0.0, 107);
INSERT INTO `v1_1f3ed0bf56f6bf2dece0b2e118fc681f190cb21d2d20b1a729553d63998e603c` VALUES ("エリコ", 6, 108, 0.0, 4, 7.54, "ひとまず訓練をこなしたご褒美か、\n実力を出しきれたとは言えない罰か…\nみなさんにどちらを与えるべきか\n少し悩む結果ですわね", "2030/12/17 14:59:59", 24, 10142, "2024/03/31 12:00:00", 129901, 0.0, 108);
INSERT INTO `v1_1f3ed0bf56f6bf2dece0b2e118fc681f190cb21d2d20b1a729553d63998e603c` VALUES ("エリコ", 2, 109, 0.0, 7, 11.5, "なかなかいい戦いぶりでしたわ\nこれもあなた方の頑張りと、何より\n優秀な教官である私の教育の賜物\nそうですわね？　クスクスクス…", "2030/12/17 14:59:59", 24, 10142, "2024/03/31 12:00:00", 129901, 0.0, 109);
INSERT INTO `v1_1f3ed0bf56f6bf2dece0b2e118fc681f190cb21d2d20b1a729553d63998e603c` VALUES ("エリコ", 2, 110, 0.0, 1, 8.83, "これだけ進めたのならば合格点ですわ\nでも、もっと高みを目指したいと\n思っているのなら…さらなる訓練にも\n付き合って差し上げましょう", "2030/12/17 14:59:59", 24, 10142, "2024/03/31 12:00:00", 129901, 0.0, 110);
INSERT INTO `v1_1f3ed0bf56f6bf2dece0b2e118fc681f190cb21d2d20b1a729553d63998e603c` VALUES ("エリコ", 1, 111, 0.0, 2, 6.68, "未熟で頼りなかったあなた方が、\n見違えるように成長しました\nなるほど…これこそが、教官冥利に\n尽きるという気持ちなのですね", "2030/12/17 14:59:59", 24, 10142, "2024/03/31 12:00:00", 129901, 0.0, 111);
INSERT INTO `v1_1f3ed0bf56f6bf2dece0b2e118fc681f190cb21d2d20b1a729553d63998e603c` VALUES ("エリコ", 6, 112, 0.0, 2, 8.8, "まさかあなた方が、私の予想すら\n超える力を発揮するとは…驚きました\nもはやゴミ虫などではありません\n一人前の騎士として胸を張りなさい", "2030/12/17 14:59:59", 24, 10142, "2024/03/31 12:00:00", 129901, 0.0, 112);
INSERT INTO `v1_1f3ed0bf56f6bf2dece0b2e118fc681f190cb21d2d20b1a729553d63998e603c` VALUES ("エリコ", 2, 113, 0.0, 1, 8.9, "クスクス、見事な働きです\nみなさんならできると信じていました\n他でもない私の指導を受けた、\nエリコ班の優秀な新兵なのですから", "2030/12/17 14:59:59", 24, 10142, "2024/03/31 12:00:00", 129901, 0.0, 113);
INSERT INTO `v1_1f3ed0bf56f6bf2dece0b2e118fc681f190cb21d2d20b1a729553d63998e603c` VALUES ("エリコ", 2, 114, 0.0, 5, 3.7, "なんてすばらしい成績…！\n今日という日は『あのおかたを想い\nエリコが限界を超えた記念日』として\n歴史に刻まれることでしょう…！", "2030/12/17 14:59:59", 24, 10142, "2024/03/31 12:00:00", 129901, 0.0, 114);
INSERT INTO `v1_1f3ed0bf56f6bf2dece0b2e118fc681f190cb21d2d20b1a729553d63998e603c` VALUES ("マコト", 1, 200, 0.0, 2, 7.6, "よし、いい訓練になったな\nおまえたちもよく動けてたし、\nちゃんと成長できてると思うぜ\nその調子で頑張れよっ", "2030/12/17 14:59:59", 24, 10142, "2024/03/31 12:00:00", 129801, 0.0, 200);
INSERT INTO `v1_1f3ed0bf56f6bf2dece0b2e118fc681f190cb21d2d20b1a729553d63998e603c` VALUES ("マコト", 2, 201, 0.0, 1, 2.25, "ひとまずゴールおめでとう！\nでも、努力は継続してこそだからな\nおまえたちにやる気があるなら、\n何度だって付き合ってやるよ", "2030/12/17 14:59:59", 24, 10142, "2024/03/31 12:00:00", 129801, 0.0, 201);
INSERT INTO `v1_1f3ed0bf56f6bf2dece0b2e118fc681f190cb21d2d20b1a729553d63998e603c` VALUES ("マコト", 2, 202, 0.0, 1, 3.28, "行軍訓練はこれにて完了！\n…なーんて思ってないよな？\n学んだことが体に染みつくまで\n地道に繰り返すのが練習ってもんさ", "2030/12/17 14:59:59", 24, 10142, "2024/03/31 12:00:00", 129801, 0.0, 202);
INSERT INTO `v1_1f3ed0bf56f6bf2dece0b2e118fc681f190cb21d2d20b1a729553d63998e603c` VALUES ("マコト", 4, 203, 0.0, 1, 2.98, "みんなに無理をさせちまったか…\nでもこれで諦めることはないぜ\n次はきっとうまくいく。そう信じて\n鍛え続ければ、道は開けるはずだ！", "2030/12/17 14:59:59", 24, 10142, "2024/03/31 12:00:00", 129801, 0.0, 203);
INSERT INTO `v1_1f3ed0bf56f6bf2dece0b2e118fc681f190cb21d2d20b1a729553d63998e603c` VALUES ("マコト", 4, 204, 0.0, 1, 3.0, "こいつは悔しい結果だな…\nま、反省点はいろいろあったし\nひとつずつ乗り越えていこう\nあたしたちで力を合わせて、なっ！", "2030/12/17 14:59:59", 24, 10142, "2024/03/31 12:00:00", 129801, 0.0, 204);
INSERT INTO `v1_1f3ed0bf56f6bf2dece0b2e118fc681f190cb21d2d20b1a729553d63998e603c` VALUES ("マコト", 1, 205, 0.0, 2, 10.3, "おまえたちはまだ未熟な訓練兵、\n失敗を必要以上に悔やむことはねえよ\nでも実戦じゃこうはいかないからな\n次は気合を入れ直していけよっ！", "2030/12/17 14:59:59", 24, 10142, "2024/03/31 12:00:00", 129801, 0.0, 205);
INSERT INTO `v1_1f3ed0bf56f6bf2dece0b2e118fc681f190cb21d2d20b1a729553d63998e603c` VALUES ("マコト", 6, 206, 0.0, 1, 2.93, "これがおまえたちの全力？\nいやいや、そんなはずはないよなっ\nおまえらならもっと強くなれる！\nあたしが言うんだ、間違いないぜ！", "2030/12/17 14:59:59", 24, 10142, "2024/03/31 12:00:00", 129801, 0.0, 206);
INSERT INTO `v1_1f3ed0bf56f6bf2dece0b2e118fc681f190cb21d2d20b1a729553d63998e603c` VALUES ("マコト", 3, 207, 0.0, 1, 9.3, "おまえたちの実力を考えりゃ、\nベストを尽くせたとは言えない成績だ\nなら、次やるべきことも決まってる\n気持ちを切り替えて、鍛え直すんだ！", "2030/12/17 14:59:59", 24, 10142, "2024/03/31 12:00:00", 129801, 0.0, 207);
INSERT INTO `v1_1f3ed0bf56f6bf2dece0b2e118fc681f190cb21d2d20b1a729553d63998e603c` VALUES ("マコト", 4, 208, 0.0, 1, 6.9, "まだまだ課題がある内容だったな～\n無駄な動き、判断の遅れ…\nそういうのをしっかり直していけば、\n記録ももっと伸びるはずだっ", "2030/12/17 14:59:59", 24, 10142, "2024/03/31 12:00:00", 129801, 0.0, 208);
INSERT INTO `v1_1f3ed0bf56f6bf2dece0b2e118fc681f190cb21d2d20b1a729553d63998e603c` VALUES ("マコト", 2, 209, 0.0, 2, 0.0, "ここまでいけりゃ、合格点！\nむしろ、最初の頼りなさを思えば\nすげえ成長してるんじゃねえか？\n胸を張れる結果だと思うぜっ！", "2030/12/17 14:59:59", 24, 10142, "2024/03/31 12:00:00", 129801, 0.0, 209);
INSERT INTO `v1_1f3ed0bf56f6bf2dece0b2e118fc681f190cb21d2d20b1a729553d63998e603c` VALUES ("マコト", 1, 210, 0.0, 2, 5.95, "なかなかハードな訓練だったけど、\nみんなしっかりついてこられたな\n地道に続けた基礎トレの成果が\n形になってる、その証だぜっ", "2030/12/17 14:59:59", 24, 10142, "2024/03/31 12:00:00", 129801, 0.0, 210);
INSERT INTO `v1_1f3ed0bf56f6bf2dece0b2e118fc681f190cb21d2d20b1a729553d63998e603c` VALUES ("マコト", 2, 211, 0.0, 1, 6.73, "今回の訓練もよく頑張ったな！\nしっかり休んで次に備えろよ！\nなんつーか、教え子が成長してくのを\n見るのは楽しいもんだな…", "2030/12/17 14:59:59", 24, 10142, "2024/03/31 12:00:00", 129801, 0.0, 211);
INSERT INTO `v1_1f3ed0bf56f6bf2dece0b2e118fc681f190cb21d2d20b1a729553d63998e603c` VALUES ("マコト", 2, 212, 0.0, 2, 0.0, "おまえら、すげえじゃねえか！\nこれならもう、ひよっこ新兵なんて\n言えねえな。きっと騎士団でも\n大活躍間違いなしだぜ！", "2030/12/17 14:59:59", 24, 10142, "2024/03/31 12:00:00", 129801, 0.0, 212);
INSERT INTO `v1_1f3ed0bf56f6bf2dece0b2e118fc681f190cb21d2d20b1a729553d63998e603c` VALUES ("マコト", 1, 213, 0.0, 2, 6.65, "敵との戦い方、状況判断の正確さ\n何より根性…どれも最高だったぞ！\nうかうかしてたら、教官のあたしまで\n追い抜かれちまうかもな…へへっ", "2030/12/17 14:59:59", 24, 10142, "2024/03/31 12:00:00", 129801, 0.0, 213);
INSERT INTO `v1_1f3ed0bf56f6bf2dece0b2e118fc681f190cb21d2d20b1a729553d63998e603c` VALUES ("マコト", 6, 214, 0.0, 1, 3.05, "あたしの指導が良かった？\nいやいや、ここまで強くなれたのは、\n他でもないおまえたち自身が努力した\nおかげだよ。改めて…おめでとうっ！", "2030/12/17 14:59:59", 24, 10142, "2024/03/31 12:00:00", 129801, 0.0, 214);
