INSERT INTO `v1_1f3ed0bf56f6bf2dece0b2e118fc681f190cb21d2d20b1a729553d63998e603c` VALUES ("エリコ", 7, "2024/03/31 12:00:00", 24, "訓練成功、おめでとうございます\n褒めるべき時にはしっかり褒めるのも\n教官の務めですものね。それとも、\n罵られる方が好みですか？　クスクス", 100, 100, 0.0, 10142, "2030/12/17 14:59:59", 10.85, 129901, 1, 0.0);
INSERT INTO `v1_1f3ed0bf56f6bf2dece0b2e118fc681f190cb21d2d20b1a729553d63998e603c` VALUES ("エリコ", 2, "2024/03/31 12:00:00", 24, "厳しい訓練も、終わってみれば\n短く感じるものですわね。\n正直、私は暴れ足りないくらい…\nいえ、何でもありませんわ。クスクス", 101, 101, 0.0, 10142, "2030/12/17 14:59:59", 11.15, 129901, 1, 0.0);
INSERT INTO `v1_1f3ed0bf56f6bf2dece0b2e118fc681f190cb21d2d20b1a729553d63998e603c` VALUES ("エリコ", 5, "2024/03/31 12:00:00", 24, "行く手を阻む忌々しい邪魔者を\n蹴散らして、見事ゴールイン…！\nまるで私とあのおかたの人生を\n象徴するような道のりでした…", 102, 102, 0.0, 10142, "2030/12/17 14:59:59", 6.4, 129901, 7, 0.0);
INSERT INTO `v1_1f3ed0bf56f6bf2dece0b2e118fc681f190cb21d2d20b1a729553d63998e603c` VALUES ("エリコ", 3, "2024/03/31 12:00:00", 24, "まったくもって不甲斐ない結果です\nしかし、挽回の機会はまだありますわ\n次はゴミ虫の地位から脱却できるよう\n死力を尽くしなさい。いいですね？", 103, 103, 0.0, 10142, "2030/12/17 14:59:59", 0.0, 129901, 3, 0.0);
INSERT INTO `v1_1f3ed0bf56f6bf2dece0b2e118fc681f190cb21d2d20b1a729553d63998e603c` VALUES ("エリコ", 8, "2024/03/31 12:00:00", 24, "クスクス…ああご心配なさらず\nなんとも情けない成績を残す輩には\nどんな罰が相応しいか、考えている\nだけですので…クスクスクス", 104, 104, 0.0, 10142, "2030/12/17 14:59:59", 2.43, 129901, 2, 0.0);
INSERT INTO `v1_1f3ed0bf56f6bf2dece0b2e118fc681f190cb21d2d20b1a729553d63998e603c` VALUES ("エリコ", 3, "2024/03/31 12:00:00", 24, "私が教官として認められなければ、\nあのおかたを失望させてしまう…\nそれだけはあってはなりません\nあなた方も、わかっていますわね？", 105, 105, 0.0, 10142, "2030/12/17 14:59:59", 9.08, 129901, 4, 0.0);
INSERT INTO `v1_1f3ed0bf56f6bf2dece0b2e118fc681f190cb21d2d20b1a729553d63998e603c` VALUES ("エリコ", 6, "2024/03/31 12:00:00", 24, "まずまずの結果ではありますが、\nこの程度で満足してもらっては\n困りますわ。エリコ班の一員として、\nより高みを目指していただきますので", 106, 106, 0.0, 10142, "2030/12/17 14:59:59", 0.0, 129901, 6, 0.0);
INSERT INTO `v1_1f3ed0bf56f6bf2dece0b2e118fc681f190cb21d2d20b1a729553d63998e603c` VALUES ("エリコ", 8, "2024/03/31 12:00:00", 24, "私の指導を受けながら、この結果は…\nもっと真剣に考えねばなりませんね\nあなた方が怠けずにいるには、\nどんなしつけが必要かを…クスクス", 107, 107, 0.0, 10142, "2030/12/17 14:59:59", 10.6, 129901, 3, 0.0);
INSERT INTO `v1_1f3ed0bf56f6bf2dece0b2e118fc681f190cb21d2d20b1a729553d63998e603c` VALUES ("エリコ", 4, "2024/03/31 12:00:00", 24, "ひとまず訓練をこなしたご褒美か、\n実力を出しきれたとは言えない罰か…\nみなさんにどちらを与えるべきか\n少し悩む結果ですわね", 108, 108, 0.0, 10142, "2030/12/17 14:59:59", 7.54, 129901, 6, 0.0);
INSERT INTO `v1_1f3ed0bf56f6bf2dece0b2e118fc681f190cb21d2d20b1a729553d63998e603c` VALUES ("エリコ", 7, "2024/03/31 12:00:00", 24, "なかなかいい戦いぶりでしたわ\nこれもあなた方の頑張りと、何より\n優秀な教官である私の教育の賜物\nそうですわね？　クスクスクス…", 109, 109, 0.0, 10142, "2030/12/17 14:59:59", 11.5, 129901, 2, 0.0);
INSERT INTO `v1_1f3ed0bf56f6bf2dece0b2e118fc681f190cb21d2d20b1a729553d63998e603c` VALUES ("エリコ", 1, "2024/03/31 12:00:00", 24, "これだけ進めたのならば合格点ですわ\nでも、もっと高みを目指したいと\n思っているのなら…さらなる訓練にも\n付き合って差し上げましょう", 110, 110, 0.0, 10142, "2030/12/17 14:59:59", 8.83, 129901, 2, 0.0);
INSERT INTO `v1_1f3ed0bf56f6bf2dece0b2e118fc681f190cb21d2d20b1a729553d63998e603c` VALUES ("エリコ", 2, "2024/03/31 12:00:00", 24, "未熟で頼りなかったあなた方が、\n見違えるように成長しました\nなるほど…これこそが、教官冥利に\n尽きるという気持ちなのですね", 111, 111, 0.0, 10142, "2030/12/17 14:59:59", 6.68, 129901, 1, 0.0);
INSERT INTO `v1_1f3ed0bf56f6bf2dece0b2e118fc681f190cb21d2d20b1a729553d63998e603c` VALUES ("エリコ", 2, "2024/03/31 12:00:00", 24, "まさかあなた方が、私の予想すら\n超える力を発揮するとは…驚きました\nもはやゴミ虫などではありません\n一人前の騎士として胸を張りなさい", 112, 112, 0.0, 10142, "2030/12/17 14:59:59", 8.8, 129901, 6, 0.0);
INSERT INTO `v1_1f3ed0bf56f6bf2dece0b2e118fc681f190cb21d2d20b1a729553d63998e603c` VALUES ("エリコ", 1, "2024/03/31 12:00:00", 24, "クスクス、見事な働きです\nみなさんならできると信じていました\n他でもない私の指導を受けた、\nエリコ班の優秀な新兵なのですから", 113, 113, 0.0, 10142, "2030/12/17 14:59:59", 8.9, 129901, 2, 0.0);
INSERT INTO `v1_1f3ed0bf56f6bf2dece0b2e118fc681f190cb21d2d20b1a729553d63998e603c` VALUES ("エリコ", 5, "2024/03/31 12:00:00", 24, "なんてすばらしい成績…！\n今日という日は『あのおかたを想い\nエリコが限界を超えた記念日』として\n歴史に刻まれることでしょう…！", 114, 114, 0.0, 10142, "2030/12/17 14:59:59", 3.7, 129901, 2, 0.0);
INSERT INTO `v1_1f3ed0bf56f6bf2dece0b2e118fc681f190cb21d2d20b1a729553d63998e603c` VALUES ("マコト", 2, "2024/03/31 12:00:00", 24, "よし、いい訓練になったな\nおまえたちもよく動けてたし、\nちゃんと成長できてると思うぜ\nその調子で頑張れよっ", 200, 200, 0.0, 10142, "2030/12/17 14:59:59", 7.6, 129801, 1, 0.0);
INSERT INTO `v1_1f3ed0bf56f6bf2dece0b2e118fc681f190cb21d2d20b1a729553d63998e603c` VALUES ("マコト", 1, "2024/03/31 12:00:00", 24, "ひとまずゴールおめでとう！\nでも、努力は継続してこそだからな\nおまえたちにやる気があるなら、\n何度だって付き合ってやるよ", 201, 201, 0.0, 10142, "2030/12/17 14:59:59", 2.25, 129801, 2, 0.0);
INSERT INTO `v1_1f3ed0bf56f6bf2dece0b2e118fc681f190cb21d2d20b1a729553d63998e603c` VALUES ("マコト", 1, "2024/03/31 12:00:00", 24, "行軍訓練はこれにて完了！\n…なーんて思ってないよな？\n学んだことが体に染みつくまで\n地道に繰り返すのが練習ってもんさ", 202, 202, 0.0, 10142, "2030/12/17 14:59:59", 3.28, 129801, 2, 0.0);
INSERT INTO `v1_1f3ed0bf56f6bf2dece0b2e118fc681f190cb21d2d20b1a729553d63998e603c` VALUES ("マコト", 1, "2024/03/31 12:00:00", 24, "みんなに無理をさせちまったか…\nでもこれで諦めることはないぜ\n次はきっとうまくいく。そう信じて\n鍛え続ければ、道は開けるはずだ！", 203, 203, 0.0, 10142, "2030/12/17 14:59:59", 2.98, 129801, 4, 0.0);
INSERT INTO `v1_1f3ed0bf56f6bf2dece0b2e118fc681f190cb21d2d20b1a729553d63998e603c` VALUES ("マコト", 1, "2024/03/31 12:00:00", 24, "こいつは悔しい結果だな…\nま、反省点はいろいろあったし\nひとつずつ乗り越えていこう\nあたしたちで力を合わせて、なっ！", 204, 204, 0.0, 10142, "2030/12/17 14:59:59", 3.0, 129801, 4, 0.0);
INSERT INTO `v1_1f3ed0bf56f6bf2dece0b2e118fc681f190cb21d2d20b1a729553d63998e603c` VALUES ("マコト", 2, "2024/03/31 12:00:00", 24, "おまえたちはまだ未熟な訓練兵、\n失敗を必要以上に悔やむことはねえよ\nでも実戦じゃこうはいかないからな\n次は気合を入れ直していけよっ！", 205, 205, 0.0, 10142, "2030/12/17 14:59:59", 10.3, 129801, 1, 0.0);
INSERT INTO `v1_1f3ed0bf56f6bf2dece0b2e118fc681f190cb21d2d20b1a729553d63998e603c` VALUES ("マコト", 1, "2024/03/31 12:00:00", 24, "これがおまえたちの全力？\nいやいや、そんなはずはないよなっ\nおまえらならもっと強くなれる！\nあたしが言うんだ、間違いないぜ！", 206, 206, 0.0, 10142, "2030/12/17 14:59:59", 2.93, 129801, 6, 0.0);
INSERT INTO `v1_1f3ed0bf56f6bf2dece0b2e118fc681f190cb21d2d20b1a729553d63998e603c` VALUES ("マコト", 1, "2024/03/31 12:00:00", 24, "おまえたちの実力を考えりゃ、\nベストを尽くせたとは言えない成績だ\nなら、次やるべきことも決まってる\n気持ちを切り替えて、鍛え直すんだ！", 207, 207, 0.0, 10142, "2030/12/17 14:59:59", 9.3, 129801, 3, 0.0);
INSERT INTO `v1_1f3ed0bf56f6bf2dece0b2e118fc681f190cb21d2d20b1a729553d63998e603c` VALUES ("マコト", 1, "2024/03/31 12:00:00", 24, "まだまだ課題がある内容だったな～\n無駄な動き、判断の遅れ…\nそういうのをしっかり直していけば、\n記録ももっと伸びるはずだっ", 208, 208, 0.0, 10142, "2030/12/17 14:59:59", 6.9, 129801, 4, 0.0);
INSERT INTO `v1_1f3ed0bf56f6bf2dece0b2e118fc681f190cb21d2d20b1a729553d63998e603c` VALUES ("マコト", 2, "2024/03/31 12:00:00", 24, "ここまでいけりゃ、合格点！\nむしろ、最初の頼りなさを思えば\nすげえ成長してるんじゃねえか？\n胸を張れる結果だと思うぜっ！", 209, 209, 0.0, 10142, "2030/12/17 14:59:59", 0.0, 129801, 2, 0.0);
INSERT INTO `v1_1f3ed0bf56f6bf2dece0b2e118fc681f190cb21d2d20b1a729553d63998e603c` VALUES ("マコト", 2, "2024/03/31 12:00:00", 24, "なかなかハードな訓練だったけど、\nみんなしっかりついてこられたな\n地道に続けた基礎トレの成果が\n形になってる、その証だぜっ", 210, 210, 0.0, 10142, "2030/12/17 14:59:59", 5.95, 129801, 1, 0.0);
INSERT INTO `v1_1f3ed0bf56f6bf2dece0b2e118fc681f190cb21d2d20b1a729553d63998e603c` VALUES ("マコト", 1, "2024/03/31 12:00:00", 24, "今回の訓練もよく頑張ったな！\nしっかり休んで次に備えろよ！\nなんつーか、教え子が成長してくのを\n見るのは楽しいもんだな…", 211, 211, 0.0, 10142, "2030/12/17 14:59:59", 6.73, 129801, 2, 0.0);
INSERT INTO `v1_1f3ed0bf56f6bf2dece0b2e118fc681f190cb21d2d20b1a729553d63998e603c` VALUES ("マコト", 2, "2024/03/31 12:00:00", 24, "おまえら、すげえじゃねえか！\nこれならもう、ひよっこ新兵なんて\n言えねえな。きっと騎士団でも\n大活躍間違いなしだぜ！", 212, 212, 0.0, 10142, "2030/12/17 14:59:59", 0.0, 129801, 2, 0.0);
INSERT INTO `v1_1f3ed0bf56f6bf2dece0b2e118fc681f190cb21d2d20b1a729553d63998e603c` VALUES ("マコト", 2, "2024/03/31 12:00:00", 24, "敵との戦い方、状況判断の正確さ\n何より根性…どれも最高だったぞ！\nうかうかしてたら、教官のあたしまで\n追い抜かれちまうかもな…へへっ", 213, 213, 0.0, 10142, "2030/12/17 14:59:59", 6.65, 129801, 1, 0.0);
INSERT INTO `v1_1f3ed0bf56f6bf2dece0b2e118fc681f190cb21d2d20b1a729553d63998e603c` VALUES ("マコト", 1, "2024/03/31 12:00:00", 24, "あたしの指導が良かった？\nいやいや、ここまで強くなれたのは、\n他でもないおまえたち自身が努力した\nおかげだよ。改めて…おめでとうっ！", 214, 214, 0.0, 10142, "2030/12/17 14:59:59", 3.05, 129801, 6, 0.0);
