INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100101, 0, 3, 3, 0, "リズムを意識して…\nわんつーわんつー！\nうん、調子はバッチリ", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100101, 0, 4, 4, 0, "ここで特訓していい？\n物は壊さないように\n気をつけるね！", 1, 1, 1);
