INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100202, 0, 1, 89, 1, "おはよう\nよく眠れた？　今日も\n一日、頑張ろうね♪", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100202, 0, 2, 90, 2, "お腹空いちゃった…\nそうだ。騎士クンも\n一緒にお昼食べない？", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100202, 0, 3, 91, 3, "もうこんな時間…\nそろそろ寝なくちゃ\nおやすみなさい", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100202, 0, 1, 92, 0, "よし…\nこれなら\n褒めてもらえるかな？", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100202, 0, 2, 93, 0, "がんばることも\n必要だけど\nお休みも大切だよね", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100202, 0, 3, 94, 0, "かわいい～♪\n騎士クンは\nこういうのって好き？", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100202, 0, 4, 95, 0, "戦う前にもこうやって\n落ち着けたら\nいいのに", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100202, 0, 5, 96, 0, "あの…！\nよ、良かったら\n一緒に…！", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100202, 0, 6, 97, 0, "騎士クンと一緒に\n冒険するなら\n体力がないと…！", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100202, 0, 7, 98, 0, "最近服のサイズが\n合わないんだけど…\n食べ過ぎなのかな？", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100202, 0, 8, 99, 0, "…やっぱり\n月刊恋愛指南は\nためになる…", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100301, 0, 1, 101, 0, "装備の手入れでも\nしようかな…", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100301, 0, 2, 102, 0, "次に鍛えるべきは\n肉体面か精神面か\nどちらにしよう…？", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100301, 0, 3, 103, 0, "ツムギが作ってくれた\nこの服…一体\nいつサイズを…？", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100301, 0, 4, 104, 0, "仲間なんていらない\nそう思ってた頃も\nあったな…", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100301, 0, 5, 105, 0, "考えごとをしていると\nつい部屋中を\n歩いてしまうな", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100301, 0, 101, 106, 0, "彼は\nどこまで強くなるのか\n私も負けられないな", 4, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100301, 0, 102, 107, 0, "私が一本を取られる\nなんてね…願い事は\nもう決まったのかな？", 8, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100301, 0, 1, 108, 0, "ああ、キミだったのか\n人の気配がしたから\n誰かと思ったよ", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100301, 0, 2, 109, 0, "斬りかかるつもりなら\nちゃんとかかってきて\n…呼んだだけ？", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100301, 0, 3, 110, 0, "なに？　私に相談？\n戦いについてなら\n喜んで", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100301, 0, 4, 111, 0, "今のが突き？\n軽い！　もっと鋭く！\nさあ、もう一回！", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100301, 0, 101, 112, 0, "ひゃっ！　ど、どこを\n触ってるの！？\n…油断した", 4, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100301, 0, 102, 113, 0, "キミにはこれからも\n私のそばに…\nいや、なんでもない", 8, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100301, 0, 1, 114, 1, "おはよう\n今日も充実した\n一日にしていこう", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100301, 0, 2, 115, 2, "そろそろ\nお茶の時間か\nキミも一緒にどう？", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100301, 0, 3, 116, 3, "遅くまで大変だね\nまだ仕事があるなら\n手伝おうか？", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100301, 0, 1, 117, 0, "戦いを経てもなお\n美しくあるように…\nこれでよし♪", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100301, 0, 2, 118, 0, "戦ってばかりで\n休まないと、また\nユイに怒られるな", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100301, 0, 3, 119, 0, "ヒヨリみたいに\n練習相手に…\nいや、やめておこう", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100301, 0, 4, 120, 0, "これはいい…\n戦いの前の精神統一に\nもってこいじゃないか", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100301, 0, 5, 121, 0, "時々は落ち着いた\n時間を過ごすのも\n悪くない…", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100301, 0, 6, 122, 0, "彼に負けないよう\n私も更に強くなろう\n共に戦うために！", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100301, 0, 7, 123, 0, "食事は大切だけど\n食べ過ぎには\n注意しておこう…", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100301, 0, 8, 124, 0, "ユイ…！\nこ、こんな本を\n読んでいたのか…！", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100302, 0, 1, 126, 0, "海だけじゃなくて\n川でも釣りがしたいな\n道具も新調しよう", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100302, 0, 2, 127, 0, "ツムギが作ってくれた\nこの服…一体\nいつサイズを…？", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100302, 0, 3, 128, 0, "彼は\nどこまで強くなるのか\n私も負けられないな", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100302, 0, 4, 129, 0, "そろそろ修行の\n内容を新しくしよう\nより厳しいものにね", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100302, 0, 5, 130, 0, "強くなって…誰にも\n妨げられない自由な\n生き方がしたい", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100302, 0, 101, 131, 0, "まさか風邪を引くとは\n彼の看病のおかげで\nすっかり良く\nなったけど", 8, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100302, 0, 102, 132, 0, "私が一本を取られる\nなんてね…願い事は\nもう決まったのかな？", 8, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100302, 0, 1, 133, 0, "どうしたの？\nなんでもない？\nそれなら良かった", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100302, 0, 2, 134, 0, "あんまり突っつくと\nお返しするよ？\n覚悟は出来てる？", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100302, 0, 3, 135, 0, "ねぇ、今度また\n釣りに行かない？\n大きいのを狙おう", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100302, 0, 4, 136, 0, "修行がしたいの？\nそれを早く言ってよ\nすぐに出発しよう！", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100302, 0, 101, 137, 0, "ひょっとして\n今のも一本だなんて\n言わないよね？", 8, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100302, 0, 102, 138, 0, "キミにはこれからも\n私のそばに…\nいや、なんでもない", 8, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100302, 0, 1, 139, 1, "今日は仕事？\nそれとも修行？\n良ければ付き合うよ", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100302, 0, 2, 140, 2, "休息も時には\n必要だと、仲間から\n教わったよ", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100302, 0, 3, 141, 3, "遅くまで大変だね\nまだ仕事があるなら\n手伝おうか？", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100302, 0, 1, 142, 0, "髪の長さを少し\n変えてみようかな…\nわ、私は何を…！", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100302, 0, 2, 143, 0, "休息があってこそ\n万全な状態で\n戦えるんだ", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100302, 0, 3, 144, 0, "ヒヨリみたいに\n練習相手に…\nいや、やめておこう", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100302, 0, 4, 145, 0, "体と同様に\n心もほぐれるね…\nはぁ…", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100302, 0, 5, 146, 0, "休む時は休む\nヒヨリもそろそろ\n覚えて欲しいな…", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100302, 0, 6, 147, 0, "彼に負けないよう\n私も更に強くなろう\n共に戦うために！", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100302, 0, 7, 148, 0, "修行のせいか\n最近食べる量が\n増えてきたような…", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100302, 0, 8, 149, 0, "ユイ…！\nこ、こんな本を\n読んでいたのか…！", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100401, 0, 1, 151, 0, "お宝の予感！\n部屋中を\n探検しちゃうぞ～", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100401, 0, 2, 152, 0, "落とし穴…どこに\n作ったんだっけ？", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100401, 0, 3, 153, 0, "うえ～ん！\n…嘘泣きしたら\n相手は油断するかな", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100401, 0, 4, 154, 0, "ハニートラップって\nすごく甘いお菓子で\nイタズラするのかな？", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100401, 0, 5, 155, 0, "かくれんぼする人\nこの指、と～まれ！", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100401, 0, 101, 156, 0, "ミソギ以外の人に\nイタズラされちゃ\nダメだよ！", 4, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100401, 0, 102, 157, 0, "ミソギと遊ぶ？\nミソギに遊ばれる？", 8, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100401, 0, 1, 158, 0, "いいこと思いついた！\nい、イタズラとかじゃ\nないよ～", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100401, 0, 2, 159, 0, "うわっ！？\n　にいちゃんが、\nミソギにイタズラ？", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100401, 0, 3, 160, 0, "にいちゃんの\n通りそうなところに\nバナナの皮を…", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100401, 0, 4, 161, 0, "にいちゃんのオデコと\nホッペと鼻と目と耳に\n落書きを…", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100401, 0, 101, 162, 0, "ここ、ミソギ探検隊の\n基地にしようよ！", 4, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100401, 0, 102, 163, 0, "だ～れだ…って？\nにいちゃんの声くらい\nわかるよ！\n大好きだもん♪", 8, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100401, 0, 1, 164, 1, "にいちゃん、おはよ！\n目覚まし時計を\n止めておいたのに、\nよく起きられたね～♪", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100401, 0, 2, 165, 2, "にいちゃんが\n昼寝したら、\nイタズラし放題♪", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100401, 0, 3, 166, 3, "夜の探検も\nおもしろそう！\nねぇ出かけちゃダメ？", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100401, 0, 1, 167, 0, "イタズラには\nお色気も必要…\nうん、完璧！", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100401, 0, 2, 168, 0, "座ったら、ブーッって\nいう音が出たら、\nおもしろいな～♪", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100401, 0, 3, 169, 0, "かわいい！\nミソギ探検隊の\nマスコットにしよう♪", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100401, 0, 4, 170, 0, "吸うとくしゃみが\n止まらなくなるお香を\nここに…くしゅっ！", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100401, 0, 5, 171, 0, "にひひ…イタズラの\n証拠、ここで\n燃やしちゃおう～！", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100401, 0, 6, 172, 0, "探検家は\n体力勝負だよー！", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100401, 0, 7, 173, 0, "みんなに分けて\nあげようかな～\n激辛ソースも\n入れちゃうけどね！", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100401, 0, 8, 174, 0, "こういう所に\n宝の地図が\n隠れてるんだよね～", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100402, 0, 1, 176, 0, "お宝の予感！\n部屋中を\n探検しちゃうぞ～", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100402, 0, 2, 177, 0, "うぅ、探検に出たい！\nじっとしてなんか\nいられないよ～！", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100402, 0, 3, 178, 0, "うえ～ん！\n…嘘泣きしたら\n相手は油断するかな", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100402, 0, 4, 179, 0, "ハニートラップって\nすごく甘いお菓子で\nイタズラするのかな？", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100402, 0, 5, 180, 0, "かくれんぼする人\nこの指、と～まれ！", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100402, 0, 101, 181, 0, "にいちゃんのオデコと\nホッペと鼻と目と耳に\n落書きを…", 8, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100402, 0, 102, 182, 0, "にいちゃんが喜ぶ\nイタズラ、何かない\nかな～？", 8, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100402, 0, 1, 183, 0, "ミソギがしかけた\nイタズラ\n見つけられるかな？", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100402, 0, 2, 184, 0, "探検する？\n冒険する？\nそれとも…\nイタズラされる？", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100402, 0, 3, 185, 0, "将来の夢は探検家！\n探したい物はないけど", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100402, 0, 4, 186, 0, "にいちゃんに宿題！\n落とし穴を10個\n掘ること！", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100402, 0, 101, 187, 0, "だ～れだ…って？\nにいちゃんの声くらい\nわかるよ！\n大好きだもん♪", 8, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100402, 0, 102, 188, 0, "ここ、ミソギ探検隊の\n基地にしようよ！", 8, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100402, 0, 1, 189, 1, "にいちゃん\n探検に行こうよ！\n今からなら遭難しても\n夜には帰れるよ！", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100402, 0, 2, 190, 2, "にいちゃんが昼寝したら\nイタズラし放題♪", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100402, 0, 3, 191, 3, "夜の探検も\nおもしろそう！\nねぇ出かけちゃダメ？", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100402, 0, 1, 192, 0, "イタズラには\nお色気も必要…\nうん、完璧！", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100402, 0, 2, 193, 0, "ゆっくりまったりして\n次のイタズラを\n考えよ～！", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100402, 0, 3, 194, 0, "かわいい！\nミソギ探検隊の\nマスコットにしよう♪", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100402, 0, 4, 195, 0, "吸うとくしゃみが\n止まらなくなるお香を\nここに…くしゅっ！", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100402, 0, 5, 196, 0, "にひひ…イタズラの\n証拠、ここで\n燃やしちゃおう～！", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100402, 0, 6, 197, 0, "探検家は\n体力勝負だよー！", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100402, 0, 7, 198, 0, "探検のおやつに\nぴったりかも～！", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100402, 0, 8, 199, 0, "こういう所に\n宝の地図が\n隠れてるんだよね～", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100601, 0, 1, 201, 0, "音楽は全部好きだけど\n一番好きなのは\nやっぱりジャズかな", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100601, 0, 2, 202, 0, "はぁ、はぁ…\n一人でもいっぱい\nしないと…\n特訓…", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100601, 0, 3, 203, 0, "お姉ちゃんには\nもっと自信を\n持ってほしいなぁ", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100601, 0, 4, 204, 0, "お姉ちゃんのマネ\nしちゃおうかな？", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100601, 0, 5, 205, 0, "双子って、\n好みのタイプも\n似ちゃうのかな？", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100601, 0, 101, 206, 0, "お兄ちゃんと私は\n相性抜群です♪", 4, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100601, 0, 102, 207, 0, "もう待ちきれない…\nお兄ちゃん、\n早く帰って来て！", 8, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100601, 0, 1, 208, 0, "あんっ…着替えよう\nと思ったのに\nお兄ちゃんのエッチ！", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100601, 0, 2, 209, 0, "うふふ、お兄ちゃん！\n今日は何を\nするんですか？", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100601, 0, 3, 210, 0, "そんなところ\n触っちゃダメ♪", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100601, 0, 4, 211, 0, "お兄ちゃん、イジワル\nしないでください…", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100601, 0, 101, 212, 0, "お兄ちゃん、もっと\n上級編のテクニック、\n教えてくださいね！", 4, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100601, 0, 102, 213, 0, "コーチ、\n今日も特訓\nお願いします！", 8, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100601, 0, 1, 214, 1, "おはようございます\n昨夜は楽しかった\nですね、うふふ♪", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100601, 0, 2, 215, 2, "こんな明るい時間から\nす、するんですか…\n二人っきりの特訓…", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100601, 0, 3, 216, 3, "夜はこれからですよ\nお兄ちゃん♪", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100601, 0, 1, 217, 0, "世界一、かわいく\nなるんだから♪", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100601, 0, 2, 218, 0, "気持ちいい♪\nもう立てないよ～", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100601, 0, 3, 219, 0, "かわいい♪\nアカリのライバル\nだね", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100601, 0, 4, 220, 0, "はぁ…気持ちいい、\n私、このままダメに\nなっちゃいそう…", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100601, 0, 5, 221, 0, "はぁ…アカリ、\nとろけちゃう…", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100601, 0, 6, 222, 0, "はぁ…はぁはぁ、\n私…もう…ダメ\n限界です…！", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100601, 0, 7, 223, 0, "わぁ、おいしい～\nけど食べ過ぎは美容に\nよくないですよね", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100601, 0, 8, 224, 0, "この楽譜、\nサックスで吹いて\nみたいなぁ♪", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100602, 0, 1, 226, 0, "このごろ一人でいると\nお兄ちゃんのこと\nばかり考えちゃう…", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100602, 0, 2, 227, 0, "はぁ、はぁ…もっと\nいっぱいしなくちゃ\n…練習", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100602, 0, 3, 228, 0, "このお部屋、なんだか\n落ち着きます…\nお兄ちゃんが\nいるから？", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100602, 0, 4, 229, 0, "みんなかわいいな～\n世界一かわいくなる\nのは、難しいな…", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100602, 0, 5, 230, 0, "双子って、\n好みのタイプも\n似ちゃうのかな？", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100602, 0, 101, 231, 0, "いつか世界でいちばん\nかわいくなって\nお兄ちゃんと…", 8, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100602, 0, 102, 232, 0, "もう待ちきれない…\nお兄ちゃん、\n早く帰って来て！", 8, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100602, 0, 1, 233, 0, "あんっ…着替えよう\nと思ったのに\nお兄ちゃんのエッチ！", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100602, 0, 2, 234, 0, "二人きりの秘密の特訓\n今度はいつですか？\n待ち遠しいです…", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100602, 0, 3, 235, 0, "え？　ここでするの？\nみんなの前で恥ずか\nしいな…筋トレ", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100602, 0, 4, 236, 0, "お兄ちゃん、イジワル\nしないでください…", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100602, 0, 101, 237, 0, "お兄ちゃんといると\nわ、私…ドキドキ\nしてきちゃう…", 8, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100602, 0, 102, 238, 0, "コーチ、\n今日も特訓\nお願いします！", 8, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100602, 0, 1, 239, 1, "おはようございます\n昨夜は楽しかった\nですね、うふふ♪", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100602, 0, 2, 240, 2, "お兄ちゃんに\n食べてもらいたいなぁ\n手作りのお昼ごはん", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100602, 0, 3, 241, 3, "お風呂入ってきますね\nそのあとは…\n二人でい～っぱい\n遊びましょうね", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100602, 0, 1, 242, 0, "お姉ちゃんのマネ\nしちゃおうかな？", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100602, 0, 2, 243, 0, "気持ちいい♪\nもう立てないよ～", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100602, 0, 3, 244, 0, "かわいい♪\nアカリのライバル\nだね", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100602, 0, 4, 245, 0, "はぁ…気持ちいい、\n私、このままダメに\nなっちゃいそう…", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100602, 0, 5, 246, 0, "あったか～い…\nまるでお兄ちゃんが\n隣にいる時みたい", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100602, 0, 6, 247, 0, "汗をかくと\n気持ちいいですよね", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100602, 0, 7, 248, 0, "お兄ちゃんの特訓に\n付き合うために\nたっぷり\n食べておかないと♪", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100602, 0, 8, 249, 0, "この楽譜\nサックスで吹いて\nみたいなぁ♪", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100701, 0, 1, 251, 0, "プリン、プリン、\nプリン、プリン、\nプリン、プリン～♪", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100701, 0, 2, 252, 0, "う～ら～め～し～や～\nなの～♪", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100701, 0, 3, 253, 0, "プリンがないなら\n死んだほうがマシなの\nあ、もう死んでたの～", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100701, 0, 4, 254, 0, "幽霊の正体見たり、\nミヤコなの～", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100701, 0, 5, 255, 0, "ミヤコがいきなり\n消えたら、みんな\n驚くかもなの～♪", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100701, 0, 101, 256, 0, "アイツがいて、\nプリンもある。\nきっとここが天国なの", 4, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100701, 0, 102, 257, 0, "アイツはプリンセス\nナイトじゃなくて\nプリンナイトなの～", 8, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100701, 0, 1, 258, 0, "姿を消して、オマエを\n見張ってやるの～\nむふふ～♪", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100701, 0, 2, 259, 0, "もし成仏した先に\nプリンがなかったら\n地獄なの～…", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100701, 0, 3, 260, 0, "右にプリン左にプリン\n後ろにもプリン♪\n前にはオマエなの～！", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100701, 0, 4, 261, 0, "死んだ人には\nお供え物をするの～\nミヤコにも、プリンを\n毎日お供えするの～", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100701, 0, 101, 262, 0, "たくさんオマエと\n遊べて、\n成仏しそうなくらい\nうれしいの～！", 4, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100701, 0, 102, 263, 0, "オマエのこと\n気に入ったからずっと\nとり憑いてやるの～", 8, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100701, 0, 1, 264, 1, "う～　眠いの…\n二度目の二度寝を\nするの…", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100701, 0, 2, 265, 2, "お昼ご飯はプリンなの\n３時のおやつも、\nプリンなの～", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100701, 0, 3, 266, 3, "ディナーは豪華に、\nプリンのフルコース\nなの～♪", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100701, 0, 1, 267, 0, "体が透けちゃうから\nうまく使えないの～", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100701, 0, 2, 268, 0, "まったりなの～\nあとはプリンがあれば\n完璧なの～", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100701, 0, 3, 269, 0, "む～…かわいいけど\nプリンは分けて\nあげないの～", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100701, 0, 4, 270, 0, "は～　いい気分なの…\n成仏しそうなの…", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100701, 0, 5, 271, 0, "も、もしかして\nミヤコ、ここで\n火葬されちゃうの？", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100701, 0, 6, 272, 0, "幽霊には、体力も\n筋力も関係ないの～！", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100701, 0, 7, 273, 0, "ぱくぱくモグモグ…\nミヤコ、幽霊だけど、\nお腹はすくの～", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100701, 0, 8, 274, 0, "プリンの写真集が\nあればいいのに\nなの～", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100702, 0, 1, 276, 0, "うらめしや～…\n裏の飯より\nプリンがいいの～", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100702, 0, 2, 277, 0, "プリン♪　プリン♪\nプリン♪　なの～♪　", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100702, 0, 3, 278, 0, "世界中のケーキ屋が\nプリンのおいしさを\n競い合えばいいの～！\n審査員はミヤコなの～", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100702, 0, 4, 279, 0, "買ってきたプリンには\n全部ミヤコの名前を\n書いておくの～", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100702, 0, 5, 280, 0, "プリンがないなら\n死んだほうがマシなの\nあ、もう死んでたの～ ", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100702, 0, 101, 281, 0, "アイツがいて\nプリンもある\nきっとここが天国なの", 8, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100702, 0, 102, 282, 0, "アイツはプリンセス\nナイトじゃなくて\nプリンナイトなの～", 8, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100702, 0, 1, 283, 0, "右にプリン左にプリン\n後ろにもプリン♪\n前にはオマエなの～！", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100702, 0, 2, 284, 0, "もし成仏した先に\nプリンがなかったら\n地獄なの～…", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100702, 0, 3, 285, 0, "今日はオマエと\nプリンを買いに行くの\n強制なの～　", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100702, 0, 4, 286, 0, "死んだ人には\nお供え物をするの～\nミヤコにも、プリンを\n毎日お供えするの～", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100702, 0, 101, 287, 0, "たくさんオマエと\n遊べて\n成仏しそうなくらい\nうれしいの～！ ", 8, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100702, 0, 102, 288, 0, "ミヤコのプリン\nオマエにだったら\n分けてあげるの～ ", 8, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100702, 0, 1, 289, 1, "う～　眠いの…\n二度目の二度寝を\nするの… ", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100702, 0, 2, 290, 2, "う～ら～め～し～や～\n…明るい時間だと\n驚かせないの～ ", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100702, 0, 3, 291, 3, "ひゅ～どろどろ～ん☆\n真っ暗なうちに\nいたずらしまくるの～♪", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100702, 0, 1, 292, 0, "体が透けちゃうから\nうまく使えないの～ ", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100702, 0, 2, 293, 0, "落ち着くの～\nいまプリン食べたら\nきっといつもより\nおいしいの～♪ ", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100702, 0, 3, 294, 0, "む～…かわいいけど\nプリンは分けて\nあげないの～　", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100702, 0, 4, 295, 0, "は～　いい気分なの…\n成仏しそうなの…", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100702, 0, 5, 296, 0, "も、もしかして\nミヤコ、ここで\n火葬されちゃうの？ ", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100702, 0, 6, 297, 0, "幽霊には、体力も\n筋力も関係ないの～！　", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100702, 0, 7, 298, 0, "おいしいの～\nプリン味だったら\nもっとおいしいの～ ", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100702, 0, 8, 299, 0, "プリンの写真集が\nあればいいのに\nなの～ ", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100801, 0, 1, 301, 0, "もし太陽が滅んでも\nボクの美しさで大地を\n照らしてあげる！", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100801, 0, 2, 302, 0, "う～ん、この部屋には\nボクの肖像画や\n彫像が必要だね", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100801, 0, 3, 303, 0, "美の女神と愛の女神が\nボクを作る時、本気\n出しちゃったんだね♪", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100801, 0, 4, 304, 0, "ボクの誕生とともに\n全ての美しさは過去の\n遺物になったんだよ！", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100801, 0, 5, 305, 0, "うわっ昨日より可愛く\nなってる。明日は\nどうなっちゃう\nのかな？", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100801, 0, 101, 306, 0, "このボクがいるだけで\nあの人への\nおもてなしだよね", 4, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100801, 0, 102, 307, 0, "ボクの可愛さは、\nふふ…あの人の\nためのものさ", 8, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100801, 0, 1, 308, 0, "ねぇ～、ボクって\n可愛い？\nふふ、知ってる☆", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100801, 0, 2, 309, 0, "呼んだ？\n可愛いボクと話したい\n気持ちはわかるよ", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100801, 0, 3, 310, 0, "ふふ、ボクに触ると\nキミも美しく\nなっちゃうかもよ？", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100801, 0, 4, 311, 0, "どうかな、\n今日のボク。\n美しい？　可愛い？\nねえ、どっち？", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100801, 0, 101, 312, 0, "あの人も今、\n可愛いボクのこと\n考えてるかな…", 4, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100801, 0, 102, 313, 0, "美しい…\nキミの瞳に写るボクは\nより一層きれいだ", 8, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100801, 0, 1, 314, 1, "美しいボクの\n美しい一日が\n始まるよ！", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100801, 0, 2, 315, 2, "さーて、午後からも\n可愛く美しく\n華麗に優雅に♪", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100801, 0, 3, 316, 3, "バラの花びら\nいっぱい浮かべた\nお風呂に入ってから\n寝ようっと♪", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100801, 0, 1, 317, 0, "鏡よ、鏡。この世で\n一番可愛いボクに\nお似合いの人はだれ？", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100801, 0, 2, 318, 0, "ボクが座ったソファは\n「美しいソファ」に\nランクアップした！", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100801, 0, 3, 319, 0, "わあ、可愛い！\n…まあ、ボクほど\nじゃないけどさ！", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100801, 0, 4, 320, 0, "服を羽織るように\n香りをまとうボク…\n美しい、美しすぎる！", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100801, 0, 5, 321, 0, "気安く触ったら\n火傷する…\n炎はボクに似ているね", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100801, 0, 6, 322, 0, "運動は苦手なのに\n運動するボクの姿は\n可愛いなんて…罪♪", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100801, 0, 7, 323, 0, "ボクが食べると、\n食べ物まで\n可愛いなあ…", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100801, 0, 8, 324, 0, "ボクが触った瞬間に、\nこの本は美を\n携えてしまうんだ", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100802, 0, 1, 326, 0, "ボクの美しさは\nみんなのために\nあるんだよ！", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100802, 0, 2, 327, 0, "う～ん、この部屋には\nボクの肖像画や\n彫像が必要だね", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100802, 0, 3, 328, 0, "これ以上美しくなると\nボクをめぐって\n大陸中で戦争が\n始まっちゃう！", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100802, 0, 4, 329, 0, "ボクの誕生とともに\n全ての美しさは過去の\n遺物になったんだよ！", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100802, 0, 5, 330, 0, "ボクの可愛さについて\nみんな話し合わない\nのかな…？", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100802, 0, 101, 331, 0, "このボクがいるだけで\nあの人への\nおもてなしだよね", 8, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100802, 0, 102, 332, 0, "あの人も今\n可愛いボクのこと\n考えてるかな…", 8, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100802, 0, 1, 333, 0, "美の女神と愛の女神が\nボクを作る時、本気\n出しちゃったんだね♪", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100802, 0, 2, 334, 0, "ボクの美しさに嫉妬\nしたって意味ないよね\n次元が違うんだから", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100802, 0, 3, 335, 0, "ボクの化粧品使う？\n一緒に可愛くなろうよ", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100802, 0, 4, 336, 0, "ボクがいるだけで\nこの部屋は美の神殿♪\nランドソルの新たな\n観光名所になるね！", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100802, 0, 101, 337, 0, "呼んだ？\n可愛いボクと話したい\n気持ちはわかるよ", 8, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100802, 0, 102, 338, 0, "美しい…\nキミの瞳に写るボクは\nより一層綺麗だ", 8, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100802, 0, 1, 339, 1, "朝日と共に\nボクの美しさも\n周りを照らすよ", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100802, 0, 2, 340, 2, "ボクの美しさを\n維持するために\nお昼ご飯食べなくちゃ", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100802, 0, 3, 341, 3, "バラの花びらいっぱい\n浮かべたお風呂に\n入ってから\n寝ようっと♪", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100802, 0, 1, 342, 0, "鏡よ、鏡。この世で\n一番可愛いボクに\nお似合いの人はだれ？", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100802, 0, 2, 343, 0, "ボクが座ったソファは\n「美しいソファ」に\nランクアップした！", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100802, 0, 3, 344, 0, "可愛いものを\n可愛いって言える\nボクって可愛いよね！", 1, 3, 1);
