INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100802, 0, 6, 347, 0, "運動は苦手なのに\n運動するボクの姿は\n可愛いなんて…罪♪", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100802, 0, 7, 348, 0, "ボクが食べると\n食べ物まで\n可愛いなあ…", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100802, 0, 8, 349, 0, "ボクが触った瞬間に\nこの本は美を\n携えてしまうんだ", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100901, 0, 1, 351, 0, "「第１０部２０章\n終焉戦争、開幕」\n…これでよし！", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100901, 0, 2, 352, 0, "今度シグルドに\n私の魔法を一つ\n伝授してやるか", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100901, 0, 3, 353, 0, "ここでは風が\n吹いてこないな…\n換気したい！", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100901, 0, 4, 354, 0, "そうだ、ナナカに\nこの間借りた本を\n返さないと…", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100901, 0, 5, 355, 0, "まずは包帯をとって\n手から龍の形の\n炎を出して…", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100901, 0, 101, 356, 0, "シグルド…\n今頃何処で何を\nしているのだろう…", 4, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100901, 0, 102, 357, 0, "最近、彼の姿が\nまぶしく見える…\nどうして…？", 8, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100901, 0, 1, 358, 0, "辞書を知らないか？\n…探している\n理由は聞くな", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100901, 0, 2, 359, 0, "そうだ！\n貴様の技にも\n名前をつけてやろう！", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100901, 0, 3, 360, 0, "私に触れても力を\n奪われないのは\n貴様くらいだぞ？", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100901, 0, 4, 361, 0, "何事だ？\nまさか我が瞳に\n魅入られたのか？", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100901, 0, 101, 362, 0, "貴様がそばにいると\nとても安心できる…\n…不思議だ", 4, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100901, 0, 102, 363, 0, "い、いきなり目の前に\n現れるな！　ドキドキ\nするだろうがぁ！", 8, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100901, 0, 1, 364, 1, "「覚醒」めたか…\n行くぞ、悪を討つ\n戦いに出発だ！", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100901, 0, 2, 365, 2, "昼間は「ヤツら」も\n襲ってはこない…\n束の間の休息だ", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100901, 0, 3, 366, 3, "どうした？　夜はまだ\nこれから…ふぁ…\nね、眠くなどないぞ！", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100901, 0, 1, 367, 0, "何か足りないな…\n暗黒騎士の紋章を\n作ろうかな", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100901, 0, 2, 368, 0, "くっ…！\n私を眠りの国へ\n誘うつもりか！", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100901, 0, 3, 369, 0, "このようなものに\n懐柔されるものか！\n…されるものか！", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100901, 0, 4, 370, 0, "これを使えば\nもっとそれっぽい\n雰囲気が出そうだ", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100901, 0, 5, 371, 0, "これが我らの求めた\n安寧か…\n悪くない…", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100901, 0, 6, 372, 0, "この程度\n前世での激闘に\n比べれば…！", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100901, 0, 7, 373, 0, "最近金欠でな…\nいやぁありがたい…", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100901, 0, 8, 374, 0, "ここにも\n前世の記憶が\n眠っていたとは…", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100902, 0, 1, 376, 0, "「第１３部１章\n閃光のシグルド」\n…ついに登場だな", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100902, 0, 2, 377, 0, "何故だ…何故\nこうもあっさり金欠に\n逆戻りするのだ！？", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100902, 0, 3, 378, 0, "やはり転生者という\n設定はカッコイイ…\n天才だな、私！", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100902, 0, 4, 379, 0, "そうだ、ナナカに\nこの間借りた本を\n返さないと…", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100902, 0, 5, 380, 0, "そろそろ今日の分の\n魔力を溜めないと\nまた魔力切れが…", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100902, 0, 101, 381, 0, "最近奴がそばに\nいるだけで、鼓動が\n早くなるのは何故だ？", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100902, 0, 102, 382, 0, "シ、シグルド…！\nだめだ…妄想でも\n上手く言えない…", 8, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100902, 0, 1, 383, 0, "なんだ？　新たな\n賞金の情報を\n持ってきたのか？", 8, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100902, 0, 2, 384, 0, "名乗りやポーズは\nその強度で相手を\n圧倒する技術だ", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100902, 0, 3, 385, 0, "いいか、魔法の\n詠唱中は絶対に\n声をかけるなよ？", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100902, 0, 4, 386, 0, "おお、ちょうど今\n貴様専用の設定集を\n作成中だぞ！", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100902, 0, 101, 387, 0, "貴様がそばにいると\nとても安心できる…\n…不思議だ", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100902, 0, 102, 388, 0, "い、いきなり目の前に\n現れるな！　ドキドキ\nするだろうがぁ！", 8, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100902, 0, 1, 389, 1, "夜更かしのせいか\nまだ眠気が…いや！\nこれは組織の罠だ！", 8, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100902, 0, 2, 390, 2, "平穏な時に\n出来るだけ魔力を\n溜めておかねば…", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100902, 0, 3, 391, 3, "夜になると、力が\n増した気分になるな\n…なりませんか？", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100902, 0, 1, 392, 0, "何か足りないな…\n暗黒騎士の紋章を\n作ろうかな", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100902, 0, 2, 393, 0, "お、おのれ\nヒュプノス…\n私は負けないぞ…", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100902, 0, 3, 394, 0, "くっくっくっ…\n貴様は特別団員に\nしてやろう", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100902, 0, 4, 395, 0, "これを使えば\nもっとそれっぽい\n雰囲気が出そうだ", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100902, 0, 5, 396, 0, "これが我らの求めた\n安寧か…\n悪くない…", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100902, 0, 6, 397, 0, "新たな戦いに備えて\n魔力だけでなく\n体力も鍛えねば！", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100902, 0, 7, 398, 0, "食事は魔力に\n変換する体力の\n元になるのだ！", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (100902, 0, 8, 399, 0, "フフフ…\nこれを覚えれば\nもっと強く…！", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101001, 0, 1, 401, 0, "王子はんほんまに\nはんなりしてて、\n王子はんって感じやわ", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101001, 0, 2, 402, 0, "この絵本の王子はん、\n王子はんみたいで\nええなぁ♪", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101001, 0, 3, 403, 0, "うちがガラスの靴を\n落としたら、王子はん\n見つけてくれるやろか", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101001, 0, 4, 404, 0, "マホマホ王国\n次期国王の王子はん\n…見てみたいわ～", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101001, 0, 5, 405, 0, "王子はんのために\n糸を紡いで、\n編み物でもしまひょか", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101001, 0, 101, 406, 0, "マホ姫と王子はん、\n絵本の世界より\nベストカップルや", 4, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101001, 0, 102, 407, 0, "王子はんと\n一緒にいると\n胸がせわしのー\nうごいてかなん", 8, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101001, 0, 1, 408, 0, "うちら並んでたら\nお雛様みたいに\n見えると思いまへん？", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101001, 0, 2, 409, 0, "王子はんを\n国に連れて帰ったら\nみんな大歓迎して\nくれるやろなぁ", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101001, 0, 3, 410, 0, "王子はんは、\n絵本から出てきた人\nみたいやね～", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101001, 0, 4, 411, 0, "うちの話し方\nのんびりしてます\nやろか？", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101001, 0, 101, 412, 0, "マホ姫の手は\nつべたいから、\n王子はんが温めて？", 4, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101001, 0, 102, 413, 0, "うちがもし悪い魔法に\nかかったら、王子はん\n口づけしてくれはる？", 8, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101001, 0, 1, 414, 1, "朝は小鳥はんも\nおなかがすいてるから\nよう飛び回りはるねえ", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101001, 0, 2, 415, 2, "お茶請けはお大福が\nよろしおすけど\nクッキーもええなあ", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101001, 0, 3, 416, 3, "うち、普段から\n夢見がちやけど…\nほんに眠い時間やわぁ", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101001, 0, 1, 417, 0, "王子はんに\n会いに行くなら、\nおめかしせな\nあかんなぁ", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101001, 0, 2, 418, 0, "はんなりした\nええお椅子やわぁ\nほっこりしますなあ", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101001, 0, 3, 419, 0, "かいらしもんが\nいっぱいで、\nマホマホ王国みたい\nやわぁ", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101001, 0, 4, 420, 0, "心地ええどす。まるで\nお花畑にいるようやね", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101001, 0, 5, 421, 0, "ぽかぽかどす。\n心まであったかく\nなりますわぁ", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101001, 0, 6, 422, 0, "はぁはぁ、お姫はんは\n激しい運動は\nしぃひんもんどす～", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101001, 0, 7, 423, 0, "もぐもぐ…あんまり\n速う食べられへんけど\n美味しいわぁ", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101001, 0, 8, 424, 0, "絵本、絵本～\n幸せやわぁ♪", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101002, 0, 1, 426, 0, "王子はんにとって\n自慢の姫になるえ～", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101002, 0, 2, 427, 0, "カボチャの馬車に\nガラスの靴…\n欲しいなぁ", 1, 1, 1);
