INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101002, 0, 4, 429, 0, "マホマホ王国\n次期国王の王子はん\n…見てみたいわ～", 1, 1, 1);
