INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101002, 0, 3, 441, 3, "夜に飛んだり鳴いたり\nする鳥はんもおるんよ\n昼は寝てはるんかねぇ", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101002, 0, 1, 442, 0, "王子はんに\n会いに行くなら\nおめかしせな\nあかんなぁ", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101002, 0, 2, 443, 0, "王子はんも一緒に\nゆっくりしていっても\nええんよ？", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101002, 0, 3, 444, 0, "かいらしい子やねぇ\nうちとお友だちに\nなってくれるん？", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101002, 0, 4, 445, 0, "癒しアイテムって\nほんま、好っきゃわぁ", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101002, 0, 5, 446, 0, "うちのお城にも\nほしいわぁ。帰ったら\nさっそく準備せな\nあかんなぁ", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101002, 0, 6, 447, 0, "部屋の中で運動\nできるのも、なかなか\n面白いもんやわぁ", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101002, 0, 7, 448, 0, "おいひいわ～\nマホマホ王国には\nない味やわぁ", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101002, 0, 8, 449, 0, "しっかり知識を\n身につけんと\n魔法も使えへん\nからなぁ", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101101, 0, 1, 451, 0, "１、２、３、４…\nあれ？　いま、\n何段目まで\n編んだっけ？", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101101, 0, 2, 452, 0, "おやつのケーキは\n苺かチョコ…\nどっちも食べよう！", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101101, 0, 3, 453, 0, "い、いけない！\n縫い針落としちゃった", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101101, 0, 4, 454, 0, "ケーキをクレープで\n巻いたら…\n大ヒット商品の予感！", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101101, 0, 5, 455, 0, "「どうだ！妹暮らし」\nってことわざ\nなかったっけ？", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101101, 0, 101, 456, 0, "お兄ちゃんに、\nお洋服でも\n作ろうかな？", 4, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101101, 0, 102, 457, 0, "ここがお兄ちゃんの\n部屋…\nドキドキしちゃう～", 8, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101101, 0, 1, 458, 0, "ねぇ、おにいちゃん\n一緒に、スイーツでも\n食べにいかない？", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101101, 0, 2, 459, 0, "クリームついてた！？\nさっき食べた、\nケーキかも…！", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101101, 0, 3, 460, 0, "勉強、見てくれるん\nですか？　やった～♪", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101101, 0, 4, 461, 0, "服がほつれてますよ？\n直してあげます！", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101101, 0, 101, 462, 0, "私をお嫁さんに\nしたいなら、予約は\nお早目に、ですよ♪", 4, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101101, 0, 102, 463, 0, "ねぇねぇお兄ちゃん！\n…えへへ、\n呼んでみただけ♪", 8, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101101, 0, 1, 464, 1, "今日も１日\n勉強がんばります！\n…少しは遊ぼうかな", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101101, 0, 2, 465, 2, "お昼休憩に\n甘いものが\n食べたいです～", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101101, 0, 3, 466, 3, "ねむいです～\nでももうちょっと\n勉強しないと～…", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101101, 0, 1, 467, 0, "この服、かわいい♪\n今度作って\nみよっと～！", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101101, 0, 2, 468, 0, "勉強で疲れたから\n休憩しよ～っと", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101101, 0, 3, 469, 0, "かわいい♪\n私が服を作って\nあげよっか？", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101101, 0, 4, 470, 0, "リラックスできます～\nお兄ちゃんが隣に\nいるみたい！", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101101, 0, 5, 471, 0, "のんびりしたら、\nまた勉強しよっと！", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101101, 0, 6, 472, 0, "運動すると勉強も\nはかどるよね！", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101101, 0, 7, 473, 0, "おいし～！\nお兄ちゃんにも\nおすそ上げしますね！", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101101, 0, 8, 474, 0, "お兄ちゃんの記憶を\n戻す方法、どこかに\n書いてないかな？", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101102, 0, 1, 476, 0, "ケーキの食べ過ぎで\nお腹が…うぅ、\n因果横暴だよー…", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101102, 0, 2, 477, 0, "１、２、３、４…\nあれ？　いま、\n何段目まで\n編んだっけ？", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101102, 0, 3, 478, 0, "い、いけない！\n縫い針落としちゃった", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101102, 0, 4, 479, 0, "笑う時には服着ます！", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101102, 0, 5, 480, 0, "「どうだ！妹暮らし」\nってことわざ\nなかったっけ？", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101102, 0, 101, 481, 0, "お兄ちゃんに、\nお洋服でも\n作ろうかな？", 8, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101102, 0, 102, 482, 0, "お兄ちゃんが帰って\nきたら、何して遊ぼう\nかな～？", 8, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101102, 0, 1, 483, 0, "勉強、見てくれるん\nですか？　やった～♪", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101102, 0, 2, 484, 0, "クリームついてた！？\nさっき食べた、\nケーキかも…！", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101102, 0, 3, 485, 0, "おにいちゃん！\nそろそろ、おやつに\nしませんか？", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101102, 0, 4, 486, 0, "勉強は…\n明日にしようっと", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101102, 0, 101, 487, 0, "妹だけど、大人な\nレディですからね！", 8, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101102, 0, 102, 488, 0, "な、ナデナデとか\n恥ずかしいな～", 8, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101102, 0, 1, 489, 1, "今日も１日\n勉強がんばります！\n…少しは遊ぼうかな", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101102, 0, 2, 490, 2, "お昼休憩に\n甘いものが\n食べたいです～", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101102, 0, 3, 491, 3, "お兄ちゃんは\n夜強いんですね\n私は眠くて…", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101102, 0, 1, 492, 0, "服装や髪形が変わると\n気分も変わるよね", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101102, 0, 2, 493, 0, "ここで寝ちゃったら\n受験勉強が～…\nぐーー", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101102, 0, 3, 494, 0, "かわいい♪\n私が服を作って\nあげよっか？", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101102, 0, 4, 495, 0, "いまならどんな試験も\n合格できそうです！", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101102, 0, 5, 496, 0, "のんびりしたら\nまた勉強しよっと！", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101102, 0, 6, 497, 0, "お兄ちゃんを\n守るためには\n毎日、鍛えなくちゃ！", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101102, 0, 7, 498, 0, "おいし～！\nお兄ちゃんにも\nおすそ上げしますね！", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101102, 0, 8, 499, 0, "この本の服\nかわいいなぁ♪\n私に作れるかな？", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101201, 0, 1, 501, 0, "う～ん、今なら\nどこでだって\n眠れちゃいそう…", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101201, 0, 2, 502, 0, "たくさん寝すぎて、\n夢の中か現実か\nわかんなく\nなっちゃうんだよね～", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101201, 0, 3, 503, 0, "…はっ！？\nね、寝てないよ？\nホントだよっ", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101201, 0, 4, 504, 0, "ぐっすり眠って\nパワーを\nフルチャージっ☆", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101201, 0, 5, 505, 0, "もし、超能力のことが\nバレたら…ブルブル", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101201, 0, 101, 506, 0, "テレパシーで\nキミの心を覗けたら\n…なんてね！", 4, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101201, 0, 102, 507, 0, "キミを抱き枕に…\nじょ、冗談冗談！", 8, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101201, 0, 1, 508, 0, "ん～、なになに？\n一緒にお昼寝する？", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101201, 0, 2, 509, 0, "忘れ物しちゃった。\nま、いっか！\nえ、ダメ？", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101201, 0, 3, 510, 0, "おおっ、相談ごと？\nなにかな\nなにかな～？", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101201, 0, 4, 511, 0, "う～ん、もしかして\nキミにも仲良しになる\n超能力があるのかも！", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101201, 0, 101, 512, 0, "むむ～…どう？\nテレパシーで、私の\n気持ちを送ったよ！", 4, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101201, 0, 102, 513, 0, "えっ？　私とキミとの\n相性？　むむ～…\nうん、バッチリだよ☆", 8, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101201, 0, 1, 514, 1, "まだこんな時間？\nベッドに行って、\n二度寝しなくちゃ～", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101201, 0, 2, 515, 2, "適度なお昼寝は\n健康にいいんだって！\n８時間くらいかな？", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101201, 0, 3, 516, 3, "どんなにお昼寝しても\n夜は眠いんだ～", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101201, 0, 1, 517, 0, "ん～、また寝癖…\nいっつも\n寝てるからかなぁ？", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101201, 0, 2, 518, 0, "一休み、一休み…\nどうせなら百休み\nくらいしようかな…", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101201, 0, 3, 519, 0, "これ…可愛い！\nシオリンに\n届けてあげたい！", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101201, 0, 4, 520, 0, "ん～…気分爽快！\nそれじゃ、おやすみ～\n…すやぁ…", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101201, 0, 5, 521, 0, "気持ちいい…\nはっ！　ダメダメ！\nまた寝るとこだった！", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101201, 0, 6, 522, 0, "眠くならないように、\n運動しよっと！", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101201, 0, 7, 523, 0, "お腹いっぱいになると\n眠くなるよね～", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101201, 0, 8, 524, 0, "睡眠学習って\n本を読みながら\n寝るんだっけ？", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101202, 0, 1, 526, 0, "う～ん、今なら\nどこでだって\n眠れちゃいそう…", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101202, 0, 2, 527, 0, "もっといっぱい\n超能力使えたら\nいいのにな～", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101202, 0, 3, 528, 0, "ねえねえ、\n今から人助け、\n付き合ってほしいなー", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101202, 0, 4, 529, 0, "ぐっすり眠って\nパワーを\nフルチャージっ☆", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101202, 0, 5, 530, 0, "人助け、頑張ったら\nなんだか、眠く…\n…はっ、ダメダメ！", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101202, 0, 101, 531, 0, "キミと一緒なら、\nなんだって出来る！\n間違いない！", 8, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101202, 0, 102, 532, 0, "キミを抱き枕に…\nじょ、冗談冗談！", 8, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101202, 0, 1, 533, 0, "忘れ物しちゃった\nま、いっか！\nえ、ダメ？", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101202, 0, 2, 534, 0, "なに？　困ってる？\nちょうど良かった！\n今は体力満タンだよ☆", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101202, 0, 3, 535, 0, "一緒に\nお出かけしたいなー\nデートしよっ！", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101202, 0, 4, 536, 0, "うんうん、そうだねっ\n困ってる人がいたら\n見過ごせないよねっ！", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101202, 0, 101, 537, 0, "むむ～…どう？\nテレパシーで、私の\n気持ちを送ったよ！", 8, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101202, 0, 102, 538, 0, "う～だんだん眠く…\nねぇ一緒に寝よ？", 8, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101202, 0, 1, 539, 1, "キミって早起きだね！\n私は寝ようかな～", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101202, 0, 2, 540, 2, "適度なお昼寝は\n健康にいいんだって！\n８時間くらいかな？", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101202, 0, 3, 541, 3, "どんなにお昼寝しても\n夜は眠いんだ～", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101202, 0, 1, 542, 0, "ん～、また寝癖…\nいっつも\n寝てるからかなぁ？", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101202, 0, 2, 543, 0, "一休み、一休み…\nどうせなら百休み\nくらいしようかな…", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101202, 0, 3, 544, 0, "これ…可愛い！\nシオリンに\n届けてあげたい！", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101202, 0, 4, 545, 0, "ん～…気分爽快！\nそれじゃ、おやすみ～\n…すやぁ…", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101202, 0, 5, 546, 0, "うう～ん、なんだか\nいつもより眠ーく\nなってきたよ…", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101202, 0, 6, 547, 0, "よーし\n困ってる人のために\nパワーアップ☆", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101202, 0, 7, 548, 0, "食べてすぐ寝ると\n牛になるんだっけ？\nまぁ、いいかぁ～", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101202, 0, 8, 549, 0, "睡眠学習って\n本を読みながら\n寝るんだっけ？", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101601, 0, 1, 551, 0, "モデルは楽しいよ！\nファンの子みんな\n応援してくれるしー！", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101601, 0, 2, 552, 0, "今日のおやつは\n駄菓子に決定～！\nいただきまーす！", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101601, 0, 3, 553, 0, "んー、今日のおやつは\n何にしよう？\nやっぱアメ玉かな！", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101601, 0, 4, 554, 0, "ここで撮影会、\nしようかな～♪", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101601, 0, 5, 555, 0, "７かける８はぁ\n７＋７＋７＋７＋…\nん、何個数えたっけ？", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101601, 0, 101, 556, 0, "やば…\nこのままじゃ、うち\nキュン死にかも…", 4, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101601, 0, 102, 557, 0, "好きなことして\nヒデサイもいて\nうちって超～\nリア充だよね♪", 8, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101601, 0, 1, 558, 0, "うちの写真撮るの？\nどんなポーズ？", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101601, 0, 2, 559, 0, "ネイルの勉強中～♪\nうまくなったら、\nヒデサイにしてあげる", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101601, 0, 3, 560, 0, "宿題見てくんない？\nひとりじゃムリ～", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101601, 0, 4, 561, 0, "みてこのネイル♪\n超エモくな～い？\nヒデサイもする～？", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101601, 0, 101, 562, 0, "新しい美容液で\n肌まじスベスベ～\nヒデサイも\n触ってみる？", 4, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101601, 0, 102, 563, 0, "ヒデサイと会ってると\n顔熱くなるんだよね\n熱なんかないのに…", 8, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101601, 0, 1, 564, 1, "早起きとか\nつらたんだよねー\n昼すぎまで寝てたい", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101601, 0, 2, 565, 2, "撮影にむけて\nダイエットちうだから\nお昼ぬいてるんだー", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101601, 0, 3, 566, 3, "お腹すいたー\nでもこの時間に\n食べたら絶対に\n太るやつだよねー", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101601, 0, 1, 567, 0, "どんなシーンにも合う\nきれいめわがまま\nちゃんとコーデだよ♪", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101601, 0, 2, 568, 0, "ちょっち疲れたかも？\nガン寝しちゃおっかな", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101601, 0, 3, 569, 0, "きゃわたーん☆\n君もおしゃれしよっか", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101601, 0, 4, 570, 0, "いいにおいじゃん！\nこれと同じにおいの\n香水ないかな？", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101601, 0, 5, 571, 0, "あったまるー\nこれ、サイツヨに\n癒しじゃない？", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101601, 0, 6, 572, 0, "あはは、なんか\nウォーキングの練習\nしてるみたーい！", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101601, 0, 7, 573, 0, "体形維持しないと\nいけないけど\nついつい食べちゃう！", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101601, 0, 8, 574, 0, "全然読めない～\nそだ、ヒデサイに\n読んでもらおっと！", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101602, 0, 1, 576, 0, "そろそろ\n仕事の時間だ～\n出かける準備\nしよっと！", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101602, 0, 2, 577, 0, "今日のおやつは\n駄菓子に決定～！\nいただきまーす！", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101602, 0, 3, 578, 0, "ヒデサイを見習って\nうちも少し勉強して\nみよっかな…　", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101602, 0, 4, 579, 0, "体形維持とか\n気にしてるんだよ～", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101602, 0, 5, 580, 0, "７かける８はぁ\n７＋７＋７＋７＋…\nん、何個数えたっけ？", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101602, 0, 101, 581, 0, "やば…\nこのままじゃ、うち\nキュン死にかも…　", 8, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101602, 0, 102, 582, 0, "ヒデサイと会ってると\n顔熱くなるんだよね\n熱なんかないのに…", 8, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101602, 0, 1, 583, 0, "うちの写真撮るの？\nどんなポーズ？", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101602, 0, 2, 584, 0, "ネイルの勉強中～♪\nうまくなったら、\nヒデサイに\nしてあげる♪", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101602, 0, 3, 585, 0, "この雑誌の服\nガチめにほしぃ～", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101602, 0, 4, 586, 0, "うちのコーデで\nヒデサイをオシャンに\nしてあげるね♪", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101602, 0, 101, 587, 0, "新しい美容液で\n肌まじスベスベ～\nヒデサイも\n触ってみる？", 8, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101602, 0, 102, 588, 0, "好きなことして\nヒデサイもいて\nうちって超～\nリア充だよね♪", 8, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101602, 0, 1, 589, 1, "今日は早起き\nできたんだー\nメイクもばっち\nきまってるっしょ？", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101602, 0, 2, 590, 2, "撮影にむけて\nダイエットちうだから\nお昼ぬいてるんだー", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101602, 0, 3, 591, 3, "お腹すいたー\nでもこの時間に\n食べたら絶対に\n太るやつだよねー", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101602, 0, 1, 592, 0, "この服ね～\n今年のトレンドなんだ♪\nかわたんでしょ！", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101602, 0, 2, 593, 0, "ちょっち疲れたかも？\nガン寝しちゃおっかな", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101602, 0, 3, 594, 0, "うわー\nこのぬいぐるみ\nもきゅすぎでしょ～！", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101602, 0, 4, 595, 0, "いいにおーい♪\nファンのみんなにも\n教えてあげよー♪", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101602, 0, 5, 596, 0, "あったまるー\nこれ、サイツヨに\n癒しじゃない？", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101602, 0, 6, 597, 0, "食べた分は\n消費しないとね～", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101602, 0, 7, 598, 0, "体形維持しないと\nいけないけど\nついつい食べちゃう！", 1, 3, 1);
