INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101701, 0, 1, 601, 0, "やっさー\n踊ってると、どんどん\n楽しくなるさー！", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101701, 0, 2, 602, 0, "このお菓子、でーじ\nおいしいねー！\nキミも食べる？", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101701, 0, 3, 603, 0, "ハイターイ！　元気な\nあいさつが一番さ～！", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101701, 0, 4, 604, 0, "あまった食材があれば\nチャンプルーにして\n食べたいさー！ ", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101701, 0, 5, 605, 0, "さぁ、みんなで歌って\n踊るさ～♪", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101701, 0, 101, 606, 0, "毎日、最高に幸せさ～\nキミのおかげだねー", 4, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101701, 0, 102, 607, 0, "キミへの気持ちを\n踊りで表現\nしてみるさ～", 8, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101701, 0, 1, 608, 0, "海でひと泳ぎ\nしたいさ～\nキミもどう？", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101701, 0, 2, 609, 0, "あぎひゃっ！\n急に声かけたら、\nビックリするさ～", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101701, 0, 3, 610, 0, "踊りなら、\n私が手とり足とり\n教えてあげるさ～！", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101701, 0, 4, 611, 0, "空手の型の\n練習するさー\nキミもどう？", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101701, 0, 101, 612, 0, "よ～し、手をつないで\n踊るさ～！\n恥ずかしがること\nないさ～", 4, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101701, 0, 102, 613, 0, "キミと一緒だから、\n毎日楽しいこと\nばっかりさー", 8, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101701, 0, 1, 614, 1, "ハイターイ！\n今日も一日、\nがんばるさー！", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101701, 0, 2, 615, 2, "午後も頑張るために\nお昼ご飯は\nてびちにするさー！", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101701, 0, 3, 616, 3, "故郷では見えない星が\n見られるんだねえ。\nなんか不思議さぁ", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101701, 0, 1, 617, 0, "夢中で踊ってたら、\n髪の毛ぼさぼさに\nなってるさ～！？", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101701, 0, 2, 618, 0, "よく食べてよく踊って\nよく休む。\nそれが元気の秘訣さ～", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101701, 0, 3, 619, 0, "ちゅらかーぎーねー！\nキミはどこから\n来たのー？", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101701, 0, 4, 620, 0, "いい香り～、って\n私もカオリだったさ！\nなんだか照れるね☆", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101701, 0, 5, 621, 0, "故郷はあんまり暖房を\n使わなかったから、\n新鮮さー！", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101701, 0, 6, 622, 0, "汗をかくのは\n気持ちいいさー！\nどんどん行くよー！", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101701, 0, 7, 623, 0, "うーん、おいしいさ～\nゴーヤっぽいものを\n混ぜたいさ～", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101701, 0, 8, 624, 0, "おおー、故郷の民話！\nこんなとこで見るとは\n懐かしいさぁ～！", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101702, 0, 1, 626, 0, "焦らなくても\nなんくるないさ～\nなるようになるさ～", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101702, 0, 2, 627, 0, "さぁ、みんなで歌って\n踊るさ～♪", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101702, 0, 3, 628, 0, "元気がないときは\n私のところに来るさー", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101702, 0, 4, 629, 0, "あまった食材があれば\nチャンプルーにして\n食べたいさー！ ", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101702, 0, 5, 630, 0, "ノリノリな音楽と\n踊りで、疲れを\n吹き飛ばすさ～！", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101702, 0, 101, 631, 0, "毎日、最高に幸せさ～\nキミのおかげだねー", 8, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101702, 0, 102, 632, 0, "幸せすぎて踊らずには\nいられないさ～", 8, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101702, 0, 1, 633, 0, "海でひと泳ぎ\nしたいさ～\nキミもどう？", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101702, 0, 2, 634, 0, "ん？　どうしたの～？\nヒマなら一緒に\n踊ろうさ～", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101702, 0, 3, 635, 0, "キミと一緒だから\n毎日楽しいこと\nばっかりさー", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101702, 0, 4, 636, 0, "ハイターイ！　元気な\nあいさつが一番さ～！", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101702, 0, 101, 637, 0, "キミと踊ってる時が\n一番幸せさ～\nキミもそう思ってたら\nうれしいさ～", 8, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101702, 0, 102, 638, 0, "海に沈む夕日は\nとっても綺麗さ～！\n今度一緒に見ようね～ ", 8, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101702, 0, 1, 639, 1, "気持ちのいい朝さー！\nひとっ走り\nしてくるさー！", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101702, 0, 2, 640, 2, "午後も頑張るために\nお昼ご飯は\nてびちにするさー！ ", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101702, 0, 3, 641, 3, "故郷では見えない星が\n見られるんだねえ\nなんか不思議さぁ ", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101702, 0, 1, 642, 0, "夢中で踊ってたら\n髪の毛ぼさぼさに\nなってるさ～！？", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101702, 0, 2, 643, 0, "よく食べてよく踊って\nよく休む\nそれが元気の秘訣さ～", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101702, 0, 3, 644, 0, "ちゅらかーぎーねー！\nキミはどこから\n来たのー？ ", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101702, 0, 4, 645, 0, "いい香り～、って\n私もカオリだったさ！\nなんだか照れるね☆ ", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101702, 0, 5, 646, 0, "故郷はあんまり暖房を\n使わなかったから\n新鮮さー！", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101702, 0, 6, 647, 0, "徹夜で踊り続ける\nためにも、まずは\n体を鍛えるさ～", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101702, 0, 7, 648, 0, "もぐもぐ…もっと\nおかわりがほしいさー", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101702, 0, 8, 649, 0, "おおー、故郷の民話！\nこんなとこで見るとは\n懐かしいさぁ～！ ", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101801, 0, 1, 651, 0, "はあ…\nすてきな小説だった～\nキュンキュンしちゃう", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101801, 0, 2, 652, 0, "このお部屋\n女の子ばかりね。\n魔族の女子校を\n思い出しちゃう♪", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101801, 0, 3, 653, 0, "次のテスト\n恋愛小説から\n出題したら\nダメかしら…？", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101801, 0, 4, 654, 0, "キンコンカンコ～ン♪\n授業をはじめま～す", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101801, 0, 5, 655, 0, "はー…\nちょっと肩が\nこっちゃった…", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101801, 0, 101, 656, 0, "私、あの子のこと\n無意識に特別扱い\nしちゃってるかな…", 4, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101801, 0, 102, 657, 0, "物語だと生徒が先生を\n好きになるのにな…\n逆の場合は…", 8, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101801, 0, 1, 658, 0, "あん！　見ちゃダメよ\n明日のテストの問題を\n作ってるとこなの！", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101801, 0, 2, 659, 0, "勉強勉強で\n疲れたよね？\n膝枕してあげる！", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101801, 0, 3, 660, 0, "テストがんばったね～\nご褒美に先生が\n特別な授業\nしてあげるね？", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101801, 0, 4, 661, 0, "学院の外でも\n私は君の先生よ\nなんでも聞いてね♪", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101801, 0, 101, 662, 0, "今読んでるのは\nヒロインが年下の子と\n恋に落ちる話…あ、\n深い意味はないのよ～", 4, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101801, 0, 102, 663, 0, "生徒は全員可愛いけど\n君は少し特別…\nみんなには内緒よ？", 8, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101801, 0, 1, 664, 1, "学校に\n遅刻しないように\n早く準備しないとね", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101801, 0, 2, 665, 2, "男の子といっしょに\nお弁当を食べるの、\n夢なんだ～", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101801, 0, 3, 666, 3, "明日の予習はやった？\n先生も手伝うから\n寝る前にやりましょ♪", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101801, 0, 1, 667, 0, "もしかして…\n少し露出多いかしら？", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101801, 0, 2, 668, 0, "リラックスすると\nα波が出て、勉強が\nはかどるんですって", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101801, 0, 3, 669, 0, "かわいい♪　あなたも\n私の生徒になる？", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101801, 0, 4, 670, 0, "学院にもこういう\n安らぐ設備が\n必要かしらね？", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101801, 0, 5, 671, 0, "まだ寒いわぁ\n誰か隣に\n来てくれたら…", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101801, 0, 6, 672, 0, "運動部の顧問も\nできるかしら？", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101801, 0, 7, 673, 0, "学校の給食でも\n食べたいわぁ～", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101801, 0, 8, 674, 0, "いい参考書を\n見つけちゃった♪", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101802, 0, 1, 676, 0, "私の生徒に\n不良なんていないわ\nみーんないい子♪", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101802, 0, 2, 677, 0, "このお部屋\n女の子ばかりね\n魔族の女子校を\n思い出しちゃう♪", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101802, 0, 3, 678, 0, "あの子、まだかな…\n宿題も見てあげたいし\n早く来ないかしら？", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101802, 0, 4, 679, 0, "はあ…\nすてきな小説だった～\nキュンキュンしちゃう", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101802, 0, 5, 680, 0, "学校の図書室に\n恋愛小説を\n置いて欲しいなあ", 1, 1, 1);
