INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101802, 0, 102, 682, 0, "物語だと生徒が先生を\n好きになるのにな…\n逆の場合は…　", 8, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101802, 0, 1, 683, 0, "あん！　見ちゃダメよ\n明日のテストの問題を\n作ってるとこなの！", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101802, 0, 2, 684, 0, "勉強勉強で\n疲れたよね？\n膝枕してあげる！", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101802, 0, 3, 685, 0, "宿題はもうやった？\n苦手なところは\n先生が見てあげる♪", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101802, 0, 4, 686, 0, "んん？　なんだか\n元気がないみたい\n先生がぎゅぅ～って\nしてあげる…　", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101802, 0, 101, 687, 0, "君のお勉強も\nだいぶ進んできたね～\n補習卒業も近いかな？\n少し寂しいけど…", 8, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101802, 0, 102, 688, 0, "テストがんばったね～\nご褒美に先生が\n特別な授業\nしてあげるね？", 8, 2, 1);
