INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101802, 0, 2, 690, 2, "午後の授業って\n眠いでしょう？\n気持ちは分かるけど\nがんばってね♪", 1, 2, 1);
