INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101802, 0, 7, 698, 0, "あん！\nお胸にこぼしちゃった", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (101802, 0, 8, 699, 0, "あぁーん♪\nこういう物語みたいな\n恋愛あこがれる～　", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102001, 0, 1, 701, 0, "おにいちゃんは、\nにんじん食べられる？", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102001, 0, 2, 702, 0, "えへへ、\nお絵かきしよ～♪", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102001, 0, 3, 703, 0, "あのね、ミミと\nあそんでほしいな～", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102001, 0, 4, 704, 0, "さっきね、きれいな\nお花見つけたよ。\nおにいちゃんにも\n見せてあげたいな～", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102001, 0, 5, 705, 0, "うさぎさん、\nぴょんぴょん\nぴょーん♪", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102001, 0, 101, 706, 0, "おにいちゃんの絵、\nかいてあげるね♪", 4, 1, 1);
