INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102102, 0, 6, 797, 0, "え、演劇には\nた、体力も重要\nだから～", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102102, 0, 7, 798, 0, "残さず\n食べますね…！", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102102, 0, 8, 799, 0, "戯曲を読んで\n自分が演じるところを\n想像するの…", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102201, 0, 1, 801, 0, "ねえ、人見知りを\n治すための練習に\n付き合ってくれない？", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102201, 0, 2, 802, 0, "アカリみたいに\nかわいくなるには、\nどうすればいいのかな", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102201, 0, 3, 803, 0, "レアカードをゲット！\nねえねえ、\nすごいでしょ！？", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102201, 0, 4, 804, 0, "ア、アイツと同じ\n部屋にいるのよね…\nって、だから何よ！？", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102201, 0, 5, 805, 0, "楽しいゲームって\nやめどきが\n見つからないわ", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102201, 0, 101, 806, 0, "もっと女の子らしく\nなったらアイツも\n嬉しいかな…って、\n何考えてるの！？", 4, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102201, 0, 102, 807, 0, "うぅ…最近、\nアイツを見るとなんか\nドキドキするのよね", 8, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102201, 0, 1, 808, 0, "ゲームに負けた罰\nだからって、\nくすぐるのは…\nあはは…ダメだって！", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102201, 0, 2, 809, 0, "アンタと話すのは割と\n緊張しないのよね\n…なんでかな", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102201, 0, 3, 810, 0, "待ちきれない…\n新作ゲームの発売日が\n待ちきれない…", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102201, 0, 4, 811, 0, "アンタは人見知り\nしないタチよね？\nいいなあ～…", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102201, 0, 101, 812, 0, "ア、アンタってさ\n好きな人とかい…\nなな、何でもない！", 4, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102201, 0, 102, 813, 0, "アンタと…そ、その\nこうして話してるのも\nわわ、悪くないわね！", 8, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102201, 0, 1, 814, 1, "おおおおはよう！\n…よし！　ちゃんと\nあいさつできたわ！", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102201, 0, 2, 815, 2, "一緒にごはんとか…\nううん、やっぱいい！\nなんでもないからっ！", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102201, 0, 3, 816, 3, "夜はこれからよ！\n朝まで\nゲームするからね！", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102201, 0, 1, 817, 0, "アカリみたいに\n可愛くなるのは\n難しいかな", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102201, 0, 2, 818, 0, "寝っ転がりながら\nゲームができれば\n気分が良さそう", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102201, 0, 3, 819, 0, "可愛い…けど、\n私には似合わないかな", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102201, 0, 4, 820, 0, "はあ、リラックスして\nゲームができるわ", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102201, 0, 5, 821, 0, "ん…暖かくて、\n寝落ちしちゃいそう", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102201, 0, 6, 822, 0, "ゲーム感覚でやると、\n体動かすのも\n楽しいわね", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102201, 0, 7, 823, 0, "ゲームは頭使うから\nそのぶん\n栄養がいるのよ", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102201, 0, 8, 824, 0, "ふむふむ、なるほど\n友だちを作るには\n笑顔が大事っと…", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102202, 0, 1, 826, 0, "さあ、今日は\n何のゲームで\n遊ぼうかな？", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102202, 0, 2, 827, 0, "一緒にゲームができる\n人がいるっていうのも\n新鮮だな…", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102202, 0, 3, 828, 0, "レアカードをゲット！\nすごいでしょ！？", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102202, 0, 4, 829, 0, "アカリってどうやって\n友達作ってるんだろ？\nコツがあるのかな…", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102202, 0, 5, 830, 0, "楽しいゲームって\nやめどきが\n見つからないわ", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102202, 0, 101, 831, 0, "うぅ…最近\nアイツを見るとなんか\nドキドキするのよね", 8, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102202, 0, 102, 832, 0, "ね、ねえ\nもう少しその…\n一緒にゲームしない？", 8, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102202, 0, 1, 833, 0, "ねえ、人見知りを\n治すための練習に\n付き合ってくれない？", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102202, 0, 2, 834, 0, "欲しいゲームが\n見つからなくて…\n探すの手伝って！", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102202, 0, 3, 835, 0, "ねえ、見て！\n新しいゲームを\n買って来たの！", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102202, 0, 4, 836, 0, "ゲームに負けた罰\nだからって、くすぐる\nのは…あ、あはは！\nだ、ダメだって！", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102202, 0, 101, 837, 0, "ア、アンタってさ\n好きな人とかい…\nなな、何でもない！", 8, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102202, 0, 102, 838, 0, "あ、あのさ…\nもう少し、そこに\nいてくれてもいいわよ", 8, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102202, 0, 1, 839, 1, "おおおおはよう！\n…よし！　ちゃんと\nあいさつできたわ！", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102202, 0, 2, 840, 2, "一緒にごはんとか…\nううん、やっぱいい！\nなんでもないからっ！", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102202, 0, 3, 841, 3, "夜はこれからよ！\n朝まで\nゲームするからね！", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102202, 0, 1, 842, 0, "もっと女の子らしく\nなったらアイツも\n嬉しいかな…って、\n何考えてるの！？", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102202, 0, 2, 843, 0, "ふああ…\n徹夜明けには\n辛い誘惑だな…", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102202, 0, 3, 844, 0, "可愛い…けど、\n私には似合わないかな", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102202, 0, 4, 845, 0, "こういうのに詳しいと\n女の子っぽく\n見えたりするのかな", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102202, 0, 5, 846, 0, "寒い日は\nここでゲームするのが\n最高のぜいたくよね", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102202, 0, 6, 847, 0, "最近ゲームばかりだし\nたまにはいいかな？", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102202, 0, 7, 848, 0, "ゲームは頭使うから\nそのぶん\n栄養がいるのよ", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102202, 0, 8, 849, 0, "ふむふむ、なるほど\n友だちを作るには\n笑顔が大事っと…", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102301, 0, 1, 851, 0, "じゃんけんぽん！\nあっち向いてほい！\nあ～ん、負けたぁ\n　『勝ったぜ！』", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102301, 0, 2, 852, 0, "ぷうきちがいれば、\nなんでも出来ちゃうよ", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102301, 0, 3, 853, 0, "発声練習しよっと\nぷうきち\nフルスイ～ング！", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102301, 0, 4, 854, 0, "ここで、ぷうきち\nフルスイングは、\nまずいかな～？", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102301, 0, 5, 855, 0, "この部屋、ぷうきちを\n置く場所はないのかな", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102301, 0, 101, 856, 0, "お兄ちゃんって…\nちょっとぷうきちに\n似てるかも♪", 4, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102301, 0, 102, 857, 0, "お兄ちゃんにも、\nぷうきちのこと\n好きになって\nもらいたいな～", 8, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102301, 0, 1, 858, 0, "おやつは、\n私とぷうきちの\n二人分用意してね♪", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102301, 0, 2, 859, 0, "ぷうきちが、お腹\nペコペコだって～\n何かちょうだい！", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102301, 0, 3, 860, 0, "『アヤネに手出したら\n…坊ちゃんでも、\n分かってんな？』", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102301, 0, 4, 861, 0, "ぷうきちとの\n連携を磨いてるの！\nもっと仲良くなるよ！", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102301, 0, 101, 862, 0, "特別だからね？\nもっと触っていいよ\n…ぷうきちのこと", 4, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102301, 0, 102, 863, 0, "お兄ちゃんのこと、\n「ぷう兄」って\n呼ぼうかな？", 8, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102301, 0, 1, 864, 1, "お兄ちゃんって\n朝早いんだね～\n私はまだ眠いよ～", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102301, 0, 2, 865, 2, "ふぁ～…ぷうきちと\nお昼寝してくるね…", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102301, 0, 3, 866, 3, "ぷうきちと\nおしゃべりしてたら\nもうこんな時間…", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102301, 0, 2, 868, 0, "『座り心地がいいな』\nぷうきちは座れない\nでしょ？", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102301, 0, 3, 869, 0, "かわいい～！\nぷうきちのお友達に\nしちゃおうかな？", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102301, 0, 4, 870, 0, "いいニオイ～♪\nぷうきちも\n気に入ったみたい！", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102301, 0, 5, 871, 0, "ぽわぽわする～\nぷうきちも\n気持ちいい？", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102301, 0, 7, 873, 0, "おいしい～\nぷうきちも\n食べてみる？", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102301, 0, 8, 874, 0, "ぷうきち！\nこの本、読んで～！", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102501, 0, 1, 901, 0, "どうして失敗ばっかり\nこうなったら\n腹を切るしか～！", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102501, 0, 2, 902, 0, "あれ？　あれ？\nお部屋の中で迷子に\nなっちゃいました～", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102501, 0, 3, 903, 0, "どうしたら転ばなく\nなるんでしょうか？", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102501, 0, 4, 904, 0, "わわわわ～～っ\nふう…\nきゃ、きゃあっ！", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102501, 0, 5, 905, 0, "私のドジで、\nこのお部屋を\n壊しちゃったら\nどうしましょう～～", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102501, 0, 101, 906, 0, "おいしい料理で\nお出迎えしなくちゃ！", 4, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102501, 0, 102, 907, 0, "一生懸命\nご奉仕しますよ～♪", 8, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102501, 0, 1, 908, 0, "耳かきしましょうか？\nえ？　こわい？", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102501, 0, 2, 909, 0, "お疲れでしたら、\nお昼寝しませんか？\n膝枕もしますよ？", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102501, 0, 3, 910, 0, "おかえりなさいませ\nご主人さま♪", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102501, 0, 4, 911, 0, "すみません\nすみません\nすみません～～", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102501, 0, 101, 912, 0, "メイドの私が、\nこんな気持ちを持って\nいいんでしょうか…", 4, 2, 1);
