INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102501, 0, 8, 924, 0, "物語のメイドさんって\n御主人さまを好きに\nなるんですよね…", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102502, 0, 1, 926, 0, "失敗したら、この\nお部屋追い出され\nちゃうかも～？", 1, 1, 1);
