INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102701, 0, 5, 955, 0, "毒にも薬にもならない\n存在なんて嫌ですわ\nどうせならとびきりの\n毒でありたい…", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102701, 0, 101, 956, 0, "クスクス…\nあの方とデートする\nイメトレ中ですわ\nクスクスクスクス…", 4, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102701, 0, 102, 957, 0, "記憶を戻す研究より\n記憶をねつ造する研究\nのほうが良いかしら？\n伴侶という設定で…", 8, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102701, 0, 1, 958, 0, "運命を信じますか？\nもちろん絶対当然\n信じますわよね？", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102701, 0, 2, 959, 0, "この薬は起床時就寝前\n食前食後食間に、毎日\n服用してください。\n惚れ薬ですわ", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102701, 0, 3, 960, 0, "お願いがあるのです。\n新しい研究の実験台に\n…", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102701, 0, 4, 961, 0, "こっそり栽培している\n毒きのこが毒々しく\n育ちましたわ…", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102701, 0, 101, 962, 0, "あなた様の前では\n普通の女の子…\n少し毒薬づくりが\n得意なだけの…", 4, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102701, 0, 102, 963, 0, "私の身も心も、全てが\nあなた様のもの。\nあなた様の全ても…", 8, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102701, 0, 1, 964, 1, "おはようございます\n一晩中、あなた様の\n寝顔を見ていましたわ", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102701, 0, 2, 965, 2, "お昼を用意しましたの\n何も入れていませんわ\nクスクス…", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102701, 0, 3, 966, 3, "あなた様が眠るまで\nお傍で見守りますわ。\n…あの、どうして\n眠らないのですか？", 1, 2, 1);
