INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102702, 0, 4, 995, 0, "このお部屋は\n良い香りがしますわ\nはっ、あなた様の匂い\nなのでは…！？", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102702, 0, 5, 996, 0, "素敵なぬくもり…\nまるであの方に\n抱きしめられたような", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102702, 0, 6, 997, 0, "この運動エネルギー\n何かにうまく\n利用できないかしら？", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102702, 0, 7, 998, 0, "美味しい。あの方にも\n差し上げましょう…\n薬を入れて…", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102702, 0, 8, 999, 0, "あの方を虜にする\n方法がどこかに\n載ってるはずですわ", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102801, 0, 1, 1001, 0, "子供たちの遊びって\nあたしが子供の頃と\nあまり変わらないのね", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102801, 0, 2, 1002, 0, "使えるものは\n再利用しないと\n…あ、これとか\nまだ使えるわね", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102801, 0, 3, 1003, 0, "贅沢は敵！　とはいえ\n気持ちに余裕のない\n暮らしはダメよね", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102801, 0, 4, 1004, 0, "お腹空いたわね、\n残り物で\n何か作ろうかしら", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102801, 0, 5, 1005, 0, "商売はノンゼロサムが\n理想だなぁ。誰も損を\nしないってことだもの", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102801, 0, 101, 1006, 0, "あんたが手伝って\nくれるから、仕事も\n前より楽しいのよね♪", 4, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102801, 0, 102, 1007, 0, "焼きそばパンが\n食べたいな～\nあいつと…", 8, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102801, 0, 1, 1008, 0, "ねぇあんた、どこか\n安いお店知らない？", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102801, 0, 2, 1009, 0, "あたし、エレガントに\n振る舞えてる？\n…そう？　嬉しい♪", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102801, 0, 3, 1010, 0, "いっぱい働いて遊んで\nよく休んでまた働く。\nそれが健全な生活よね", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102801, 0, 4, 1011, 0, "一緒に部屋の\n整頓でもする？\nハマると楽しいわよ♪", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102801, 0, 101, 1012, 0, "あんたがいると\n子供たちも喜ぶわ♪\nもちろんあたしもね！", 4, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102801, 0, 102, 1013, 0, "ね、あんた今、幸せ？\n何びっくりしてるのよ\n大事なことじゃないの", 8, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102801, 0, 1, 1014, 1, "面倒な用事は午前中に\n片づけてしまうのが、\n時間の使い方のコツよ", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102801, 0, 2, 1015, 2, "午後のティータイム\nとかどう？\n良いカフェがあるの♪", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102801, 0, 3, 1016, 3, "さ、エレガントな夜を\n楽しみましょ。\nモダンな音楽でも\nかけましょうか？", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102801, 0, 1, 1017, 0, "レディたるもの\n身嗜みはしっかりとね\n見本にならないと♪", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102801, 0, 2, 1018, 0, "これで仕事は一段落ね\nちょっと休憩…", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102801, 0, 3, 1019, 0, "うふふ、\nかわいいものを\n見るのは幸せね～", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102801, 0, 4, 1020, 0, "部屋中がいい香り…\nなんだかすごい贅沢\nしてる気分♪", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102801, 0, 5, 1021, 0, "風邪を引かないように\n身体を温めなくちゃ", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102801, 0, 6, 1022, 0, "さっ！　家の中でも\n効率的に\nトレーニングよ！", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102801, 0, 7, 1023, 0, "もぐもぐ…はっ、\n口の周り、\n汚れてないわよね？", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102801, 0, 8, 1024, 0, "知識から過去を学んで\n未来に活かすわよ", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102802, 0, 1, 1026, 0, "安売りのお店の情報\n整理しないと…", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102802, 0, 2, 1027, 0, "どんなに苦労してても\nそう見せないのが真の\nエレガントなレディよ", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102802, 0, 3, 1028, 0, "安く済んであいつが\n喜ぶ料理とか…\nないかしら", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102802, 0, 4, 1029, 0, "子供たちの遊びって\nあたしが子供の頃と\nあまり変わらないのね", 1, 1, 1);
