INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102802, 0, 101, 1031, 0, "焼きそばパンが\n食べたいな～\nあいつと…", 8, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102802, 0, 102, 1032, 0, "あいつと一緒に…\n色々なところに\nいきたいな", 8, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102802, 0, 1, 1033, 0, "今度買い物に\n付き合いなさい\nセールがあるのよ", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102802, 0, 2, 1034, 0, "あんたがいると子供\nたちも喜ぶわ♪\nもちろんあたしもね♪", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102802, 0, 3, 1035, 0, "いっぱい働いて遊んで\nよく休んでまた働く\nそれが健全な生活よね", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102802, 0, 4, 1036, 0, "あんたとこうして\nのんびりするのも\nいいものね", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102802, 0, 101, 1037, 0, "ねえ、今度遊びに\n行きたいな\nもちろん一緒に、よ", 8, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102802, 0, 102, 1038, 0, "あんたが手伝って\nくれるから、仕事も\n前より楽しいのよね♪", 8, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102802, 0, 1, 1039, 1, "面倒な用事は午前中に\n片づけてしまうのが\n時間の使い方のコツよ", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102802, 0, 2, 1040, 2, "ランチにして午後の\n仕事に備えましょ！\nやることは一杯よ♪", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102802, 0, 3, 1041, 3, "ふわあ～\nもうこんな時間\n子供たちは\nもう寝たかしら", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102802, 0, 1, 1042, 0, "忙しい時こそ\n身だしなみは隙なく\nぴしっとね！", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102802, 0, 2, 1043, 0, "これで仕事は一段落ね\nちょっと休憩…", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102802, 0, 3, 1044, 0, "ふふふ\nずっと眺めてたく\nなっちゃうわね", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102802, 0, 4, 1045, 0, "部屋中がいい香り…\nなんだかすごい贅沢\nしてる気分♪", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102802, 0, 5, 1046, 0, "風邪を引かないように\n身体を温めなくちゃ", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102802, 0, 6, 1047, 0, "さっ！　家の中でも\n効率的に\nトレーニングよ！", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102802, 0, 7, 1048, 0, "しっかり食べて\n栄養補給、栄養補給！", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102802, 0, 8, 1049, 0, "知識から過去を学んで\n未来に活かすわよ", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102901, 0, 1, 1051, 0, "可愛い子が\nいっぱいいるなぁ…\n誰か私とユニット\n組んでくれないかな？", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102901, 0, 2, 1052, 0, "今度のライブ会場、\nいつもより大きいから\n楽しみなんだ～！", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102901, 0, 3, 1053, 0, "新曲の衣装、もうすぐ\n出来あがるんだって！\n楽しみだな～", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102901, 0, 4, 1054, 0, "彼とデュエット\nしてみたいな～♪", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102901, 0, 5, 1055, 0, "キラキラ無病息災、\nドキドキ一日一善…\n作詞って難しい～", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102901, 0, 101, 1056, 0, "ファンはもちろん\n大事だけど…\n彼のことは、もっと\n大切にしたいな…", 4, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102901, 0, 102, 1057, 0, "写真集見なくても、\n本物がここに\nいるのにな～", 8, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102901, 0, 1, 1058, 0, "握手会の練習、\n付き合ってくれない？", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102901, 0, 2, 1059, 0, "写真集のポーズ\n練習中～！\nど、どうかな？", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102901, 0, 3, 1060, 0, "何て書いてあるか\n読めないって？\nもー！\nこれは私のサイン！", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102901, 0, 4, 1061, 0, "ね、次のライブ\n観に来てくれる？\nいい席とっとくから！", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102901, 0, 101, 1062, 0, "ねぇ、台本読むの\nつきあってくれる？\n恋人役に\nなってほしいんだ", 4, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102901, 0, 102, 1063, 0, "新曲、聞いてくれた？\nキミとのことを\nテーマにしたんだよ。\nどこかは秘密だけど♪", 8, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102901, 0, 1, 1064, 1, "朝の発声練習に\n行くけど、キミも\n一緒に来ない？", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102901, 0, 2, 1065, 2, "この時間は\nお腹すくけど…\n水着撮影があるから\nお昼抜いてるんだ", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102901, 0, 3, 1066, 3, "夜の帳が二人を包み\n…こういう方向性の\n歌詞も悪くないなぁ。", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102901, 0, 1, 1067, 0, "身だしなみオッケー！\nアイドルはいつでも\n完璧でいなくちゃ！", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102901, 0, 2, 1068, 0, "ふぅ…\nこんな姿、ファンには\n見せられないな～", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102901, 0, 3, 1069, 0, "かわいい～♪\nこれは強力な\nライバル登場かな！", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102901, 0, 4, 1070, 0, "ステキな香り～♪\n歌詞のイメージが\n浮かんできそう！", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102901, 0, 5, 1071, 0, "体も暖まったし！\nダンスレッスンに\n行こうかな♪", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102901, 0, 6, 1072, 0, "ベストタイム更新！\nライブに向けて\n体力つけないと…", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102901, 0, 7, 1073, 0, "太っちゃったら\nどうしよう…？\nでも手が止まらない～", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102901, 0, 8, 1074, 0, "ステキなお話…\n新曲の作詞の\n参考になりそう♪", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102902, 0, 1, 1076, 0, "このお部屋すてき！\nファンに追われた時の\n隠れ家にピッタリね！", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102902, 0, 2, 1077, 0, "そろそろイメチェン\nしてみようかな…\nそれともこの路線で\nもう少し…？", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102902, 0, 3, 1078, 0, "みんながくれた\nファンレター…\n私の大事な宝物…", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102902, 0, 4, 1079, 0, "彼とデュエット\nしてみたいな～♪", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102902, 0, 5, 1080, 0, "疾風怒濤、威風堂々\n獅子奮迅、唯我独尊…\nラブソングの作詞って\n難しいな～", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102902, 0, 101, 1081, 0, "ファンはもちろん\n大事だけど…\n彼のことは、もっと\n大切にしたいな…", 8, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102902, 0, 102, 1082, 0, "トップアイドルの\nさらに先…\n彼とならその景色も\n見られるかな", 8, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102902, 0, 1, 1083, 0, "新曲の衣装、もうすぐ\n出来あがるんだって！\n楽しみだな～", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102902, 0, 2, 1084, 0, "アイドルノゾミだけ\nじゃなくて、私の事も\nちゃんと見てね？", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102902, 0, 3, 1085, 0, "キミに響かないなら\nファンにも響くわけ\nないよ！　練習練習！", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102902, 0, 4, 1086, 0, "衣装のスカートは\n鉄壁なんだよ！\n残念でした～", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102902, 0, 101, 1087, 0, "新曲、聞いてくれた？\nキミとのことを\nテーマにしたんだよ\nどこかは秘密だけど♪", 8, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102902, 0, 102, 1088, 0, "ねぇ、私がトップ\nアイドルになっても\nちゃんと隣にいてね\n遠くに行かないでね？", 8, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102902, 0, 1, 1089, 1, "朝の発声練習に\n行くけど、キミも\n一緒に来ない？", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102902, 0, 2, 1090, 2, "お昼ごはん行かない？\n誰にも知られてない\nお店があるんだけど！", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102902, 0, 3, 1091, 3, "夜の帳が二人を包み\n…こういう方向性の\n歌詞も悪くないなぁ", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102902, 0, 1, 1092, 0, "身だしなみオッケー！\nアイドルはいつでも\n完璧でいなくちゃ！", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102902, 0, 2, 1093, 0, "ふぅ…\nこんな姿、ファンには\n見せられないな～", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102902, 0, 3, 1094, 0, "かわいい～♪\nこれは強力な\nライバル登場かな！", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102902, 0, 4, 1095, 0, "みんなのイメージする\nアイドルの香りは\nこんな感じかな～", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102902, 0, 5, 1096, 0, "体も暖まったし！\nダンスレッスンに\n行こうかな♪", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102902, 0, 6, 1097, 0, "ベストタイム更新！\nライブに向けて\n体力つけないと…", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102902, 0, 7, 1098, 0, "太っちゃったら\nどうしよう…？\nでも手が止まらない～", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (102902, 0, 8, 1099, 0, "この恋愛小説ステキ！\n私もアイドルじゃ\nなかったら…", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103001, 0, 1, 1101, 0, "手裏剣の数が\n合わないデス！\nどこかに落とした\nデスか…？", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103001, 0, 2, 1102, 0, "クセモノ、隠れても\nむだデス！\n…気のせいデシタ", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103001, 0, 3, 1103, 0, "金は天下の\nマワリモノ！\nところで、\nどういう意味デス？", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103001, 0, 4, 1104, 0, "むむ、アクダイカンの\n気配がするデース", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103001, 0, 5, 1105, 0, "クニトリ合戦、\n楽しみデース！", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103001, 0, 101, 1106, 0, "ショーグンと\n出会えて、\nワタシは果報者デス！", 4, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103001, 0, 102, 1107, 0, "最近、ショーグンの\nそばにいると\nドキドキするデス…", 8, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103001, 0, 1, 1108, 0, "なんでも\n命令するデス！\nショーグンの命は\n絶対デス！", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103001, 0, 2, 1109, 0, "あっ、ショーグン、\n出陣はいつで\nゴザルデス！？", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103001, 0, 3, 1110, 0, "さ、さすが\nショーグンデス…\nいつの間に背後を！？", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103001, 0, 4, 1111, 0, "出陣の合図に\n備えるデス！\nほら貝は\nどこデスか！？", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103001, 0, 101, 1112, 0, "見つめられると\nドキドキするデス…\n新しい忍法デスか？", 4, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103001, 0, 102, 1113, 0, "ショーグン！\nえへへ…これからも\nおそばに置いて\nクダサイね？", 8, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103001, 0, 1, 1114, 1, "おはようデス！\n今日も一日\n忍法日和デス！", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103001, 0, 2, 1115, 2, "そろそろお腹\n空いてきたデス。\n昼ごはんにするデス！", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103001, 0, 3, 1116, 3, "明日もきっと\nいい日デスよ、\nショーグン！", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103001, 0, 1, 1117, 0, "ちょんまげに\nしてみるのも\n面白いかもデス！", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103001, 0, 2, 1118, 0, "心地よくて\n眠くなってきマス…\nもしやテキコクの\n罠デスか！？", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103001, 0, 3, 1119, 0, "これを変わり身の\n術に…かわいそう\nだから止めるデス", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103001, 0, 4, 1120, 0, "ワビサビというもの\nデスね！\n素晴らしいデス！", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103001, 0, 5, 1121, 0, "身体を暖めて\nイクサに向かうデス！", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103001, 0, 6, 1122, 0, "アクロバットな忍法を\n使うには、体を\n鍛えないと危険デス！", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103001, 0, 7, 1123, 0, "うーん、\n美味しいデス…\n幸せデス～", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103001, 0, 8, 1124, 0, "これは…\nＢＵＳＨＩＤＯ…\nデスか？", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103002, 0, 1, 1126, 0, "ふんふんふ～ん\n武器の手入れは\n念入りにしマスよ～", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103002, 0, 2, 1127, 0, "クセモノ、隠れても\nむだデス！\n…気のせいデシタ", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103002, 0, 3, 1128, 0, "金は天下の\nマワリモノ！\nところで\nどういう意味デス？", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103002, 0, 4, 1129, 0, "むむ、アクダイカンの\n気配がするデース", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103002, 0, 5, 1130, 0, "クニトリ合戦\n楽しみデース！", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103002, 0, 101, 1131, 0, "最近、ショーグンの\nそばにいると\nドキドキするデス…", 8, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103002, 0, 102, 1132, 0, "ショーグンの身は\n何があってもワタシが\n守るデス！", 8, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103002, 0, 1, 1133, 0, "あっ、ショーグン\n出陣はいつで\nゴザルデス！？", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103002, 0, 2, 1134, 0, "イクサの前に\nホネ休めも大事デスよ\nゆっくりするデース！", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103002, 0, 3, 1135, 0, "さ、さすがショーグン\nデス…いつの間に\n背後を！？", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103002, 0, 4, 1136, 0, "一緒に忍法考える\nデス！\nショーグン用の\n忍法も必要デスね！", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103002, 0, 101, 1137, 0, "見つめられると\nドキドキするデス…\n新しい忍法デスか？", 8, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103002, 0, 102, 1138, 0, "ショーグン！\nえへへ…これからも\nおそばに置いて\nクダサイね？", 8, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103002, 0, 1, 1139, 1, "いい朝デス！\nトーゴク風の朝ゴハン\n作るデス！", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103002, 0, 2, 1140, 2, "もうお昼デス\nお昼寝するデスか？", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103002, 0, 3, 1141, 3, "そろそろおネム\nデース…\n一緒に寝るデス？", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103002, 0, 1, 1142, 0, "服の乱れは心の乱れ\nデス！　きっちり\nしないといけないデス", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103002, 0, 2, 1143, 0, "心地よくて眠く\nなってきマス…\nもしやテキコクの\n罠デスか！？", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103002, 0, 3, 1144, 0, "これを変わり身の\n術に…かわいそう\nだから止めるデス", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103002, 0, 4, 1145, 0, "ワビサビというもの\nデスね！\n素晴らしいデス！", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103002, 0, 5, 1146, 0, "身体を暖めてイクサに\n向かうデス！", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103002, 0, 6, 1147, 0, "ショーグンのお役に\n立つため、修行\nあるのみデス！", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103002, 0, 7, 1148, 0, "美味しいデス…\n酢昆布を和えて\nみたいデス", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103002, 0, 8, 1149, 0, "物事を知って\n来たるべきイクサに\n活かすデス", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103101, 0, 1, 1151, 0, "お父さん、そろそろ\n子離れを…しない？\nそうですか…", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103101, 0, 2, 1152, 0, "父は、いつも男性に\nきびしいですね", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103101, 0, 3, 1153, 0, "占ってほしい人は、\nいつでも言って\nください", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103101, 0, 4, 1154, 0, "あなたの手相は…\nて、手を握るのって\n緊張しますね", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103101, 0, 5, 1155, 0, "この部屋、風水的にも\n運気が上がりそうです", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103101, 0, 101, 1156, 0, "あの人とのこと占って\nみようかな…\n好き…嫌い…\n好き♪…き、嫌い", 4, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103101, 0, 102, 1157, 0, "私との相性ですか？\nそれなら占う必要は\nありませんよ…\nふふっ♪", 8, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103101, 0, 1, 1158, 0, "女難の相が\n出ていますね…\n気をつけてください", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103101, 0, 2, 1159, 0, "今日のあなたの運勢は\n大きょ…\n大吉です", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103101, 0, 3, 1160, 0, "父と散歩中です。\n毎日決まったコースを\n散歩させないと、\nストレスが溜まるので", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103101, 0, 4, 1161, 0, "小僧！\nシノブに触るとは\nいい度胸じゃねぇか！", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103101, 0, 101, 1162, 0, "あなたに触られると、\n動悸が早くなります\nもしかして心霊現象？", 4, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103101, 0, 102, 1163, 0, "今日もたくさんの幽霊\nに慕われていますね。\n素敵です", 8, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103101, 0, 1, 1164, 1, "おはようございます\n今日の運勢を\n占いましょうか？", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103101, 0, 2, 1165, 2, "今日のラッキー\nアイテムはドクロです\n良ければ父を\nお貸ししましょうか？", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103101, 0, 3, 1166, 3, "今日のあなたの運気は\n最高潮でした。\nもうすぐ今日は\n終わりますが", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103101, 0, 1, 1167, 0, "お父さん。\nいつも、身だしなみに\n気を使ってるけど、\nあまり変わってないよ", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103101, 0, 2, 1168, 0, "一休みしましょう。\n憑かれて疲れました", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103101, 0, 3, 1169, 0, "かわいい…\n付喪神が宿らないかな", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103101, 0, 4, 1170, 0, "お父さん、\nリラックスし過ぎると\n成仏しちゃうよ？", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103101, 0, 5, 1171, 0, "気持ちいいです…\n極楽浄土みたい…", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103101, 0, 6, 1172, 0, "はぁはぁ…\nお、お父さん、\n代わりに運動して…", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103101, 0, 7, 1173, 0, "お父さんも\n食べたいの？\n死んでるのに？", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103101, 0, 8, 1174, 0, "この本…\nもしかして予言書？", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103102, 0, 1, 1176, 0, "ここにいる幽霊は\nみんな幸せそうです", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103102, 0, 2, 1177, 0, "お父さんが\n女の子ばかりに\n声をかけるので\n困っています", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103102, 0, 3, 1178, 0, "成仏はうれしいこと\nです…でも友達が\n減るのは寂しいですね", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103102, 0, 4, 1179, 0, "幽霊に好かれる人は\n素敵な方です\nもちろん、あなたも", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103102, 0, 5, 1180, 0, "お父さん、そろそろ\n子離れを…しない？\nそうですか…", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103102, 0, 101, 1181, 0, "生きている人に\n好かれるには、\nどうすればいい\nのでしょう？", 8, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103102, 0, 102, 1182, 0, "お父さん、どうしたら\nあの人のことを\n認めてくれますか？", 8, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103102, 0, 1, 1183, 0, "この部屋、風水的にも\n運気が上がりそうです", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103102, 0, 2, 1184, 0, "シノブには指一本\n触れさせん！\nガブッ！！", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103102, 0, 3, 1185, 0, "あなたの手相は…\nて、手を握るのって\n緊張しますね", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103102, 0, 4, 1186, 0, "ラップ音？\nいえ、父のいびきです", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103102, 0, 101, 1187, 0, "私との相性ですか？\nそれなら占う必要は\nありませんよ…\nふふっ♪", 8, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103102, 0, 102, 1188, 0, "前世って信じますか？\n私とあなたは\nもしかすると…", 8, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103102, 0, 1, 1189, 1, "今日のラッキー\nアイテムはドクロです\n良ければ父を\nお貸ししましょうか？", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103102, 0, 2, 1190, 2, "明るい時間は\nあまり得意では\nないんです…", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103102, 0, 3, 1191, 3, "夜のお散歩に\n行きませんか？\n知り合いの幽霊に\n挨拶して回るんです", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103102, 0, 1, 1192, 0, "お父さん\nいつも、身だしなみに\n気を使ってるけど\nあまり変わってないよ", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103102, 0, 2, 1193, 0, "うとうと…\nあれ…\n走馬灯が見える…", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103102, 0, 3, 1194, 0, "かわいい…\nお父さんみたい…", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103102, 0, 4, 1195, 0, "お父さん\nリラックスし過ぎると\n成仏しちゃうよ？", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103102, 0, 5, 1196, 0, "快適です…\n幽霊もどんどん\n集まってきますね…", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103102, 0, 6, 1197, 0, "はぁはぁ…\nお、お父さん、\n代わりに運動して…", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103102, 0, 7, 1198, 0, "お父さんの分も、\n私が食べるね", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103102, 0, 8, 1199, 0, "この本の占い\n私の占いと違う…", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103201, 0, 1, 1201, 0, "私だって、一人で\n買い物ぐらい\nできますわ…きっと", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103201, 0, 2, 1202, 0, "そろそろ新しい\n案件を始めないと\nいけませんわね", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103201, 0, 3, 1203, 0, "一日も早く、\n一人前の商人に\nなりたいものです", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103201, 0, 4, 1204, 0, "平和を買うのには\nどれほどのお金が\n必要なのかしら？", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103201, 0, 5, 1205, 0, "ここももう少し広いと\n落ち着くのですけど。\n改築しませんこと？", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103201, 0, 101, 1206, 0, "あの方はお金で雇った\nわけではないのに\n私といてくださる…", 4, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103201, 0, 102, 1207, 0, "たとえ私が間違っても\nきっとあの方が\n正してくれますわ…", 8, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103201, 0, 1, 1208, 0, "私と、庶民向けの\nお店へ一緒に\n参りませんか？", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103201, 0, 2, 1209, 0, "資産運用？\nお幾らほど\n増やしたいのです？", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103201, 0, 3, 1210, 0, "どんぶり勘定だとよく\n言われるのですが…\n例えが小さすぎます！", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103201, 0, 4, 1211, 0, "お金で解決するのは\n悪いことでは\nありませんわよね？", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103201, 0, 101, 1212, 0, "その、よろしければ\n私とお散歩など\nいかがです？", 4, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103201, 0, 102, 1213, 0, "もしあなたさまが\n道に迷われるなら、\n私もお供いたします！", 8, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103201, 0, 1, 1214, 1, "早起きは三ルピの徳と\n申しますが…\nもっとお得ですわね", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103201, 0, 2, 1215, 2, "昼食は町の屋台で\n立ったまま食べて\nみたいですわ！", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103201, 0, 3, 1216, 3, "よろしければ\nお休み前のミルクを\n御一緒にいかが？", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103201, 0, 1, 1217, 0, "だらしない身なりでは\n取引相手に信用して\nいただけませんものね", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103201, 0, 2, 1218, 0, "この感触と安らぎ…\n素晴らしい\n費用対効果ですわ…", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103201, 0, 3, 1219, 0, "何の見返りも求めず\nただひたすら愛でて\nいたいのです！", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103201, 0, 4, 1220, 0, "目に見えないものに\nお金をかけるのが\n真の贅沢ですわ！", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103201, 0, 5, 1221, 0, "誰ひとり寒い思いを\nしない町にするために\n私にできる事は…", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103201, 0, 6, 1222, 0, "健康はお金では\n買えませんが\n運動器具は別ですわ！", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103201, 0, 7, 1223, 0, "なかなか\n美味ですわ\nシェフはどちらに？", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103201, 0, 8, 1224, 0, "経済の仕組みを\nしっかりと\n学ばなくては…", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103202, 0, 1, 1226, 0, "そろそろ新しい\n案件を始めないと\nいけませんわね", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103202, 0, 2, 1227, 0, "ええと、これは\nおいくらで買ったん\nでしたっけ？", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103202, 0, 3, 1228, 0, "お金を見せたら\n態度が変わる人は\n信用できませんわ", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103202, 0, 4, 1229, 0, "地図が読めずとも\n道に迷わない方って\nいらっしゃるのですね", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103202, 0, 5, 1230, 0, "この狭さも\n慣れれば\n快適ですわ♪", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103202, 0, 101, 1231, 0, "あの方はお金で雇った\nわけではないのに\n私といてくださる…", 8, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103202, 0, 102, 1232, 0, "たとえ私が間違っても\nきっとあの方が\n正してくれますわ…", 8, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103202, 0, 1, 1233, 0, "一人前の商人に\n相応しい居振る舞いを\nしたいものですわ", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103202, 0, 2, 1234, 0, "幼い頃\nお店屋さんごっこを\nなさいましたか？", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103202, 0, 3, 1235, 0, "資産運用？\nお幾らほど\n増やしたいのです？", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103202, 0, 4, 1236, 0, "今度一緒に…\nパーティにでも\n行きませんこと？", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103202, 0, 101, 1237, 0, "その、よろしければ\n私とお散歩など\nいかがです？", 8, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103202, 0, 102, 1238, 0, "あなたさまの優しさは\nお金には\n代えられませんわね", 8, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103202, 0, 1, 1239, 1, "なぜだか\n眠り足りないような\n感じがしますわ…", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103202, 0, 2, 1240, 2, "昼食は町の屋台で\n立ったまま食べて\nみたいですわ！", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103202, 0, 3, 1241, 3, "よろしければ\nお休み前のミルクを\n御一緒にいかが？", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103202, 0, 1, 1242, 0, "ドレスは汚れていない\nようですが…やだ\n髪がほつれてますわ！", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103202, 0, 2, 1243, 0, "こういったものに\n腰を沈めると…\nき、気が緩んで…", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103202, 0, 3, 1244, 0, "何の見返りも求めず\nただひたすら愛でて\nいたいのです！", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103202, 0, 4, 1245, 0, "香りは質のいいものと\nそうでないものが\nはっきり分かりますわ", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103202, 0, 5, 1246, 0, "誰ひとり寒い思いを\nしない町にするために\n私にできる事は…", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103202, 0, 6, 1247, 0, "目標は遠くに\n置いた方が励みに\nなりますわ！", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103202, 0, 7, 1248, 0, "なかなか\n美味ですわ\nシェフはどちらに？", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103202, 0, 8, 1249, 0, "商人って、物語では\n悪役にされやすいの\nですね…", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103301, 0, 1, 1251, 0, "家の中のものを使って\n物ボケ、考えるべ！", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103301, 0, 2, 1252, 0, "毎日の日課…それは\n炊事、洗濯、大喜利\nだべ！", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103301, 0, 3, 1253, 0, "うちに塀ができたら、\nへー、って言いたい！", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103301, 0, 4, 1254, 0, "オラが作る料理は\nこの後スタッフで\n美味しくいただくべよ", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103301, 0, 5, 1255, 0, "エリザベスも\n連れてきてぇ～", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103301, 0, 101, 1256, 0, "あんちゃんも、\nエリザベスと同じ位\nめんこいべ♪", 4, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103301, 0, 102, 1257, 0, "オラとあんちゃん、\n息がぴったりに\nなってきたべ…！", 8, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103301, 0, 1, 1258, 0, "おぉ！　そんな急に\nツッコまれたら、\nびっくらこくべやー！", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103301, 0, 2, 1259, 0, "ネタはオラが書くけど\nあんちゃんが\n書きたかったら\n書いてもいいんだぞ？", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103301, 0, 3, 1260, 0, "あんちゃん、オラには\n容赦なんかせずに\nツッコんでくれて\nいいから！", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103301, 0, 4, 1261, 0, "押すなよ、\n絶対押すなよ\n…なんて\n言ってないべさ？", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103301, 0, 101, 1262, 0, "うう…あんちゃんに\nツッコまれたくて\nうずうずしてきたべ～", 4, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103301, 0, 102, 1263, 0, "将来的には夫婦漫才\nってのも…あ、ひ、\nひとりごとだべさ～！", 8, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103301, 0, 1, 1264, 1, "おはよう！　一緒に\n牛の乳しぼりいくべ？", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103301, 0, 2, 1265, 2, "お昼だ～腹が減っては\n笑いはできぬ～！", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103301, 0, 3, 1266, 3, "今日もよく働いたべ！\nさ、今からは\nお笑いの練習だ～！", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103301, 0, 1, 1267, 0, "舞台に上がる前に\n身なりは整えねぇと\nだめだべ～！", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103301, 0, 2, 1268, 0, "ナイスなイスに\n座りたい、\nなんちゃって！", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103301, 0, 3, 1269, 0, "めんこいのは、\nスベっても許される\nからずるいべな～？", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103301, 0, 4, 1270, 0, "必要以上に\n癒されちまうべや～", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103301, 0, 5, 1271, 0, "ぽかぽかだ…\nウケた会場みてぇだ", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103301, 0, 6, 1272, 0, "お笑い芸人は、\n体が資本だべさ～！", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103301, 0, 7, 1273, 0, "もぐもぐ…\nボケることも\n忘れちまう美味さだ～", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103301, 0, 8, 1274, 0, "ネタ作りのヒントが\n載ってねぇか探すべ", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103302, 0, 1, 1276, 0, "さぁ今日も張り切って\n漫才の練習するべさ！", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103302, 0, 2, 1277, 0, "この部屋でも、商品\n売っていいんだべか？", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103302, 0, 3, 1278, 0, "眼鏡を頭の上に乗せて\n「メガネメガネ～」\nって言う練習した方が\nいいべか？", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103302, 0, 4, 1279, 0, "漫才を持ち時間に\n収めるには、何度も\n練習するしかねぇべ", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103302, 0, 5, 1280, 0, "エリザベスも\n連れてきてぇ～", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103302, 0, 101, 1281, 0, "うう…あんちゃんに\nツッコまれたくて\nうずうずしてきたべ～", 8, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103302, 0, 102, 1282, 0, "あんちゃんに\n採れたての食材を\n食わしてやりてぇ～", 8, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103302, 0, 1, 1283, 0, "押すなよ\n絶対押すなよ\n…なんて\n言ってないべさ？", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103302, 0, 2, 1284, 0, "あんちゃん、百連\nツッコミ特訓だ～！", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103302, 0, 3, 1285, 0, "あんちゃん、オラには\n容赦なんかせずに\nツッコんでくれて\nいいから！", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103302, 0, 4, 1286, 0, "「なんでだべ～！」\nって言いながら\nツッコむといいべ！", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103302, 0, 101, 1287, 0, "あはは～あんちゃんの\nツッコミはやっぱり\n気持ちいいなぁ～", 8, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103302, 0, 102, 1288, 0, "将来的には夫婦漫才\nってのも…あ、ひ\nひとりごとだべさ～！", 8, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103302, 0, 1, 1289, 1, "おはよう！\n今日もいい\nネタ作り日和だ！", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103302, 0, 2, 1290, 2, "お昼だな\nご飯を食べたら、\nネタの練習するべ！", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103302, 0, 3, 1291, 3, "もうこんな時間だべ\nネタ作りは\n明日にするべ…", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103302, 0, 1, 1292, 0, "うーん…衣装、\nずれてねぇべか？", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103302, 0, 2, 1293, 0, "ナイスなイスに\n座りたい\nなんちゃって！", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103302, 0, 3, 1294, 0, "はう～\nなまらめんこいべや～", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103302, 0, 4, 1295, 0, "必要以上に\n癒されちまうべや～", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103302, 0, 5, 1296, 0, "笑いをとることを\n忘れるほど\nあったけぇべ～", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103302, 0, 6, 1297, 0, "たくさん運動して\n動きでも笑いがとれる\nように頑張るべ～！", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103302, 0, 7, 1298, 0, "もぐもぐ…\nボケることも\n忘れちまう美味さだ～", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103302, 0, 8, 1299, 0, "幅広く教養を\n身につけて\nいいネタ作るべ！", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103401, 0, 1, 1301, 0, "通りにいる\nカップル…爆発\nすればいいのに…", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103401, 0, 2, 1302, 0, "う～ん、仕事なんて\nしないで、\n飲んでたいな～", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103401, 0, 3, 1303, 0, "今日は一日、\n外に出ない！\nもう決めたわ！", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103401, 0, 4, 1304, 0, "お腹空いた…\nけど、外に出るのは\n面倒だなぁ", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103401, 0, 5, 1305, 0, "うぅ…頭がガンガン\nする…昨日、\n飲み過ぎたかな…", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103401, 0, 101, 1306, 0, "デートとか…\n誘っても嫌がられ\nないかな…？", 4, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103401, 0, 102, 1307, 0, "もしかして運命の\n相手だったり…\nするのかな", 8, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103401, 0, 1, 1308, 0, "やっぱり一杯目が\n一番おいしいわ～！\n…え、見てた！？", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103401, 0, 2, 1309, 0, "頼れるお姉さんだって\nところ、もっと見せて\nあげるからね", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103401, 0, 3, 1310, 0, "ねえ、一緒に買い物に\n行かない？　その…\nの、飲み物とか…", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103401, 0, 4, 1311, 0, "ひっく、うふふ…\nもっとお姉さんと\nお話しようよ～", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103401, 0, 101, 1312, 0, "極力人付き合いは\nしない主義なんだけど\nキミは例外かも…", 4, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103401, 0, 102, 1313, 0, "いつか…\nキミと一緒に飲めたら\n楽しいだろうな", 8, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103401, 0, 1, 1314, 1, "おはよう…朝は\nちょっとつらいな…\n低血圧なのよ…", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103401, 0, 2, 1315, 2, "おっひるー♪\nなに食べよっかな～♪", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103401, 0, 3, 1316, 3, "ふわあ…\n眠る前にちょっと\n一杯…冗談よ", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103401, 0, 1, 1317, 0, "うう…\n髪がボサボサ…\nちゃんとしなきゃ…", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103401, 0, 2, 1318, 0, "自然体でゴロゴロ\nするのって\n幸せだなぁ…", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103401, 0, 3, 1319, 0, "こういうの男の子に\nプレゼントして\nもらいたいな…", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103401, 0, 4, 1320, 0, "うわあ、\nこれ、私の好きな\n香りだー！", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103401, 0, 5, 1321, 0, "ぽっかぽか…\nあとは熱い一杯が\nあれば…！", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103401, 0, 6, 1322, 0, "家でゴロゴロして\n付いちゃったお肉を\n落とさないと…！", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103401, 0, 7, 1323, 0, "もぐもぐ…\nおーいしー！\n…太らないよね？", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103401, 0, 8, 1324, 0, "大人の女らしく\n色んなことを学んで\nおかなくっちゃ", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103402, 0, 1, 1326, 0, "仕事終わりの一杯の\nために、今日も\n頑張らないと", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103402, 0, 2, 1327, 0, "今日は\nのんびり\n過ごそうかな～", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103402, 0, 3, 1328, 0, "あ…\nお皿、洗って\nなかった…", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103402, 0, 4, 1329, 0, "もう少し、大人の\n魅力が出せるように\nなりたいな", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103402, 0, 5, 1330, 0, "素敵な彼氏…\n欲しいなぁ…", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103402, 0, 101, 1331, 0, "彼と私\n釣り合うかな…\n大丈夫よね？", 8, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103402, 0, 102, 1332, 0, "今後のために\nちょっとは花嫁修業\nしてみようかな…？", 8, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103402, 0, 1, 1333, 0, "何事も\nお姉さんと一緒に\n楽しく頑張りましょ？", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103402, 0, 2, 1334, 0, "ね、ねえ！\nキミは年上と年下\nどっちが好きかな？", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103402, 0, 3, 1335, 0, "もう…\n構ってくれないと\n泣いちゃうぞ～？", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103402, 0, 4, 1336, 0, "お姉さんが～\n可愛がって\nあげようか～？", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103402, 0, 101, 1337, 0, "極力人付き合いは\nしない主義なんだけど\nキミは例外かも…", 8, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103402, 0, 102, 1338, 0, "お姉さんとチュー\nする？　ふふふ～\n冗談で～す！", 8, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103402, 0, 1, 1339, 1, "さーて、今日も\nいっちょやるぞー！", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103402, 0, 2, 1340, 2, "後でのんびり\nストレッチでも\nしようっと♪", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103402, 0, 3, 1341, 3, "一日よく働いた～！\n自分にごほうび\nあげたいなー♪", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103402, 0, 1, 1342, 0, "仕事の前に、髪とか\n整えないと…", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103402, 0, 2, 1343, 0, "自然体でゴロゴロ\nするのって\n幸せだなぁ…", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103402, 0, 3, 1344, 0, "もう大人だし…\nあんまり可愛いのは\n似合わないかな…？", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103402, 0, 4, 1345, 0, "うわあ\nこれ、私の好きな\n香りだー！", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103402, 0, 5, 1346, 0, "あったかーい…\nだ、だらけ\nちゃいそう…", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103402, 0, 6, 1347, 0, "地道な努力が\nリア充への\n第一歩！", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103402, 0, 7, 1348, 0, "おーいしっ♪\nこのために\n生きてるわねえ", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103402, 0, 8, 1349, 0, "大人の女らしく\n色んなことを学んで\nおかなくっちゃ", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103801, 0, 1, 1351, 0, "大切な人に\n本をすすめるのって\nけっこう難しいなぁ", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103801, 0, 2, 1352, 0, "き、きらーん☆\nお姉ちゃんに\n似てるかな…？", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103801, 0, 3, 1353, 0, "そろそろ好きな\n作家さんの新刊が\n出るから楽しみ…", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103801, 0, 4, 1354, 0, "ちょっと、休憩…\n本でも読もうかな", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103801, 0, 5, 1355, 0, "もっとたくさんの本に\n囲まれて生活したいな", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103801, 0, 101, 1356, 0, "むかしむかしある所に\nシオリと\nあの人が…", 4, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103801, 0, 102, 1357, 0, "この本の主人公、\nあの人に\n似ているかも…\nかっこいいな", 8, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103801, 0, 1, 1358, 0, "ケホケホ…\nコンコン…\nゴホッゴホッ…\nへ、平気ですよ？", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103801, 0, 2, 1359, 0, "あ…お姉ちゃんと\n約束があったんだ…", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103801, 0, 3, 1360, 0, "しおりを探している？\nあ、本に挟む方ですね\nドキドキしました…", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103801, 0, 4, 1361, 0, "最近調子がいいのは\n牧場の牛乳やチーズの\nおかげでしょうか？", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103801, 0, 101, 1362, 0, "読みたい本も\nあなたと行きたい所も\n増えるばっかりです", 4, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103801, 0, 102, 1363, 0, "私の病気、\n悪化してるのかも！？\nすごく胸がドキドキ\nするんです…", 8, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103801, 0, 1, 1364, 1, "えっと、朝食の前には\nこの薬を飲んで…\n食後にはこの薬を…", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103801, 0, 2, 1365, 2, "ふぅ…午前中\n動いたので体力が…\nすこし休みますね", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103801, 0, 3, 1366, 3, "早く寝ないと\n体に良くないですよね\nでも気になっている\n本があって…", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103801, 0, 1, 1367, 0, "寝てばかりなので、\n髪が乱れてないかな\n…？", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103801, 0, 2, 1368, 0, "少し休もう\n…やっぱり\nたくさん休もう", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103801, 0, 3, 1369, 0, "かわいいものが\n枕元にあると\n安心できるんですよ", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103801, 0, 4, 1370, 0, "いい香り…\n病は気からと言うので\n雰囲気も大事ですね", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103801, 0, 5, 1371, 0, "こうして温まって\nいると、病気も\n治る気がします", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103801, 0, 6, 1372, 0, "運動…イメージ\nトレーニングだけじゃ\nだめでしょうか…", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103801, 0, 7, 1373, 0, "少食ですけど\nこれならたくさん\n食べられそうです", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103801, 0, 8, 1374, 0, "続きが気になって\n読むのを止める\nタイミングが…", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103802, 0, 1, 1376, 0, "しっかり休まないと\n早く回復するように", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103802, 0, 2, 1377, 0, "き、きらーん☆\nお姉ちゃんに\n似てるかな…？　", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103802, 0, 3, 1378, 0, "病室よりも…\nずっとこの部屋に\nいたいですね…　", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103802, 0, 4, 1379, 0, "そろそろ好きな\n作家さんの新刊が\n出るから楽しみ…", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103802, 0, 5, 1380, 0, "大切な人に\n本をすすめるのって\nけっこう難しいなぁ", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103802, 0, 101, 1381, 0, "むかしむかしある所に\nシオリと\nあの人が…", 8, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103802, 0, 102, 1382, 0, "あの人の\nためにも早く\n病気を治さないと…", 8, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103802, 0, 1, 1383, 0, "ケホケホ…\nコンコン…\nゴホッゴホッ…\nへ、平気ですよ？", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103802, 0, 2, 1384, 0, "新記録です！\n一週間連続、\n平熱を保ちました！", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103802, 0, 3, 1385, 0, "あ…お薬の時間…\n苦いけど我慢しなきゃ　", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103802, 0, 4, 1386, 0, "最近調子がいいのは\n牧場の牛乳やチーズの\nおかげでしょうか？", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103802, 0, 101, 1387, 0, "しおりを探している？\nあ、本に挟む方ですね\nドキドキしました…", 8, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103802, 0, 102, 1388, 0, "私の病気\n悪化してるのかも！？\nすごく胸がドキドキ\nするんです…", 8, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103802, 0, 1, 1389, 1, "えっと、朝食の前には\nこの薬を飲んで…\n食後にはこの薬を…", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103802, 0, 2, 1390, 2, "…お昼の薬？\nあ、飲み忘れてました\nありがとうございます", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103802, 0, 3, 1391, 3, "この薬を飲むと\n眠くなっちゃうんです\nでもまだ、あなたと\nお話したいし～", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103802, 0, 1, 1392, 0, "寝てばかりなので、\n髪が乱れてないかな\n…？　", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103802, 0, 2, 1393, 0, "少し休もう\n…やっぱり\nたくさん休もう", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103802, 0, 3, 1394, 0, "この子がいれば\n病室に一人でも\n寂しくないですね", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103802, 0, 4, 1395, 0, "いろんな効能が？\n健康になる香りも\nあるのでしょうか？", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103802, 0, 5, 1396, 0, "こうして温まって\nいると、病気も\n治る気がします", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103802, 0, 6, 1397, 0, "私も、少しは\n体力をつけないと\nいけませんよね　", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103802, 0, 7, 1398, 0, "いっぱい食べて\n健康な体にならないと", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (103802, 0, 8, 1399, 0, "続きが気になって\n読むのを止める\nタイミングが…", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104001, 0, 1, 1401, 0, "友達ができたら、\n山の上でお弁当…\n夢が広がります…", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104001, 0, 2, 1402, 0, "あ、忘れずに\nお花に水を\nあげなきゃ…", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104001, 0, 3, 1403, 0, "お菓子を作って、\nみなさんに振る舞って\n友達作戦！　とか…", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104001, 0, 4, 1404, 0, "そろそろ新しい\n草人形を作って\n友達作りの特訓を…", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104001, 0, 5, 1405, 0, "植物のお世話って、\nぼっちにとっては、\n救い…なんです…", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104001, 0, 101, 1406, 0, "ＢＢ団の活動日…\nまだかな…\nうふふふっ", 4, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104001, 0, 102, 1407, 0, "もっともっとあの人の\nそばで、一緒に友達を\n作っていきたいな…", 8, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104001, 0, 1, 1408, 0, "あ、こんにちは！\n今日もＢＢ団で\nお世話になります！", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104001, 0, 2, 1409, 0, "あの…私の友達作りの\n練習方法、間違って\nませんよね…？", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104001, 0, 3, 1410, 0, "そうだ、今度、\n森に一緒に木の苗を\n植えに行きませんか？", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104001, 0, 4, 1411, 0, "人とお話するのって、\n私、大好きです！\n友達ふやすぞ～！", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104001, 0, 101, 1412, 0, "あなたと話してると、\nまるで私がぼっちじゃ\nないみたいです！", 4, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104001, 0, 102, 1413, 0, "友達もほしいけど…\nあなたに会えたことが\n私の一番の幸せです", 8, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104001, 0, 1, 1414, 1, "おはようございます！\n今日もＢＢ団、\nがんばるぞー！", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104001, 0, 2, 1415, 2, "なんだか、お腹が\n空いてきました。\nお昼ごはんー♪", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104001, 0, 3, 1416, 3, "もう眠いかもです…\n明日は友達、\nできるといいなあ…", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104001, 0, 1, 1417, 0, "私がもっと\n可愛ければ、\n友達できるかなあ？", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104001, 0, 2, 1418, 0, "ふわあ…のんびり\nしすぎないように\n気を付けなきゃです", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104001, 0, 3, 1419, 0, "かわいいなあ。\nこの子とも\n友達になりたい…", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104001, 0, 4, 1420, 0, "いい香りです…\n精神統一して\n友達のイメトレ…", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104001, 0, 5, 1421, 0, "灯りを見てると、\n心がなごみます～", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104001, 0, 6, 1422, 0, "もぐもぐ…\nおいしいです…！\nみなさんもぜひ！", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104001, 0, 7, 1423, 0, "おいしいです…\n私に友達がいたら、\n分けてあげたいな", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104001, 0, 8, 1424, 0, "友達作りの\n参考になりそうなこと\n書いてないかな～？", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104002, 0, 1, 1426, 0, "友達作りは、焦らず\n慌てず、じっくりと…", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104002, 0, 2, 1427, 0, "ひとつ屋根の下\nって感じ…\nいいですねえ…", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104002, 0, 3, 1428, 0, "煮汁、煮汁が\n噴きこぼれて\nいませんか～", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104002, 0, 4, 1429, 0, "今度、みなさんと\nピクニックとか…\nいってみたいな", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104002, 0, 5, 1430, 0, "ぼっちでもいい\nたくましく育てば\n…なんて", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104002, 0, 101, 1431, 0, "最近、毎日あの人の\nこと考えちゃいます\nどうしてだろう…", 8, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104002, 0, 102, 1432, 0, "もっともっとあの人の\nそばで、一緒に友達を\n作っていきたいな…", 8, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104002, 0, 1, 1433, 0, "あ！　ＢＢ団の活動\nですか？　嬉しくて\n飛び跳ねちゃいます！", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104002, 0, 2, 1434, 0, "あなたとのお話で友達\n作りの練習になるなら\n一石二鳥です！", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104002, 0, 3, 1435, 0, "私少しは人と話しても\nテンパらなくなって\nきましたかね…？", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104002, 0, 4, 1436, 0, "あの、友達作りの\n新しい作戦\n考えてみたんですっ！", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104002, 0, 101, 1437, 0, "あなたとお話する時の\nこの気持ち、私の宝物\nなんです…えへへ", 8, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104002, 0, 102, 1438, 0, "友達もほしいけど…\nあなたに会えたことが\n私の一番の幸せです", 8, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104002, 0, 1, 1439, 1, "朝ですね！\nこんないい朝は\n友達作りのチャンス！", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104002, 0, 2, 1440, 2, "あの…そろそろお腹\nすきませんか？\nお昼ごはんは…", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104002, 0, 3, 1441, 3, "もう眠いかもです…\n明日は友達\nできるといいなあ…", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104002, 0, 1, 1442, 0, "私…見た目\nそこまで変じゃ\nないですよね…？", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104002, 0, 2, 1443, 0, "ほわあ～…\nふかふかって\nいいものですね～", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104002, 0, 3, 1444, 0, "この子みたいな\nあいくるしさを\n目指したいです！", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104002, 0, 4, 1445, 0, "灯りを見てると\n心がなごみます～", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104002, 0, 5, 1446, 0, "ぬくぬくで、なんだか\n眠くなっちゃいますね", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104002, 0, 6, 1447, 0, "身体を鍛えて\nみなさんを\n守ります！", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104002, 0, 7, 1448, 0, "もぐもぐ…\nおいしいです…！\nみなさんもぜひ！", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104002, 0, 8, 1449, 0, "友達作りの\n参考になりそうなこと\n書いてないかな～？", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104201, 0, 1, 1451, 0, "唱喚の歌は、みだりに\n歌っていいものでは\nないんです", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104201, 0, 2, 1452, 0, "ここでアイドルの\n練習は、さすがに\n迷惑ですよね？", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104201, 0, 3, 1453, 0, "練習してもあんまり\n疲れなくなった…\nアイドルのおかげかな", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104201, 0, 4, 1454, 0, "人々の平和を守る旅に\n同行してくれる人を\n探していて…", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104201, 0, 5, 1455, 0, "あとで曲を復習して\nおかないと…", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104201, 0, 101, 1456, 0, "踊りを見られるのは\nまだ恥ずかしいけど\nあの人になら…", 4, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104201, 0, 102, 1457, 0, "浜辺で曲を書こうと\n思うんですけど…\n一緒に行きませんか？", 8, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104201, 0, 1, 1458, 0, "いつも応援ありがとう\nございます♪　これは\nあ、握手の練習です！", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104201, 0, 2, 1459, 0, "え、もう練習の時間？\nごめんなさい\nすぐ準備します！", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104201, 0, 3, 1460, 0, "守るべき人たちに\n笑顔でいてもらうのも\n大切なんですね", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104201, 0, 4, 1461, 0, "歌える曲を増やそうと\n思って。どんな曲が\n聞きたいですか？", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104201, 0, 101, 1462, 0, "～～♪　あれ？\n今私、歌ってました？\n気づきませんでした", 4, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104201, 0, 102, 1463, 0, "…歌って欲しい？\n喜んで♪\nどの歌にしましょう？", 8, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104201, 0, 1, 1464, 1, "おはようございます♪\n少し歌の練習を\nしてきますね", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104201, 0, 2, 1465, 2, "もうお昼なんですか？\n練習に夢中で\n気づきませんでした", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104201, 0, 3, 1466, 3, "明日の予定は…\nアイドルはやることが\n多いみたいで…", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104201, 0, 1, 1467, 0, "見た目については\n前よりも気をつけてと\nノゾミに言われてます", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104201, 0, 2, 1468, 0, "歌の調子をよくする\nためには休息も\n必要ですよね…", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104201, 0, 3, 1469, 0, "この子たちを\n相手に練習して\nみようかな…♪", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104201, 0, 4, 1470, 0, "…疲れてるのかな\n落ち着けているのが\n自分でもわかります", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104201, 0, 5, 1471, 0, "ここにいると\n穏やかな旋律が\n浮かんできます…", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104201, 0, 6, 1472, 0, "歌うためには\n体力づくりは\n欠かせません", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104201, 0, 7, 1473, 0, "歌った後は\nお腹が空いて\nしまうんです", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104201, 0, 8, 1474, 0, "音楽について\n理論も勉強\nしないと…", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104202, 0, 1, 1476, 0, "歌うことの楽しさ\n分かってきました\nもっと歌いたいな…", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104202, 0, 2, 1477, 0, "練習してもあんまり\n疲れなくなった…\nアイドルのおかげかな", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104202, 0, 3, 1478, 0, "精霊も強くなったし\nもっとたくさんの人を\n守りたいです", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104202, 0, 4, 1479, 0, "最近、よく頭の中に\n歌が浮かんできます\n不思議な感覚…", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104202, 0, 5, 1480, 0, "楽譜を買いました\n思いついた歌を\n書き留めておくために", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104202, 0, 101, 1481, 0, "浜辺で曲を書こうと\n思うんですけど…\n一緒に行きませんか？", 8, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104202, 0, 102, 1482, 0, "私の中にある\n私だけの歌…\n作ってみようかな？", 8, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104202, 0, 1, 1483, 0, "守るべき人たちに\n笑顔でいてもらうのも\n大切なんですね", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104202, 0, 2, 1484, 0, "歌える曲を増やそうと\n思って。どんな曲が\n聞きたいですか？", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104202, 0, 3, 1485, 0, "魔物退治ですか！？\n…違います？\n良かった…", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104202, 0, 4, 1486, 0, "いつも応援ありがとう\nございます♪　これは\nあ、握手の練習です！", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104202, 0, 101, 1487, 0, "…歌って欲しい？\n喜んで♪\nどの歌にしましょう？", 8, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104202, 0, 102, 1488, 0, "～～♪　あれ？\n今私、歌ってました？\n気づきませんでした", 8, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104202, 0, 1, 1489, 1, "呼吸を深く、大きく…\nこれでちゃんと\n目が覚めますよ", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104202, 0, 2, 1490, 2, "少し休んだら\n午後の練習に\n行ってきますね", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104202, 0, 3, 1491, 3, "曲を作っていたら\nもうこんな時間…\nおやすみなさい", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104202, 0, 1, 1492, 0, "見た目については\n前よりも気をつけてと\nノゾミに言われてます", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104202, 0, 2, 1493, 0, "あ～…♪\nよく体がほぐれます…\nいい歌が歌えそうです", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104202, 0, 3, 1494, 0, "かわいいと思う\n気持ちを、歌にしても\nいいかも知れませんね", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104202, 0, 4, 1495, 0, "…疲れてるのかな\n落ち着けているのが\n自分でもわかります", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104202, 0, 5, 1496, 0, "ここにいると\n穏やかな旋律が\n浮かんできます…", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104202, 0, 6, 1497, 0, "もっと体力をつけて\n最高のライブを\nファンに届けます！", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104202, 0, 7, 1498, 0, "これでも一応\n体形には気をつけて\n食べてるんですよ？", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104202, 0, 8, 1499, 0, "読書は歌に\n新しい風を\n吹き込んでくれます", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104301, 0, 1, 1501, 0, "店の新メニューでも\n考えるかな", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104301, 0, 2, 1502, 0, "あたしの店から、\n出前を届けようか？", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104301, 0, 3, 1503, 0, "パトロール、\nパトロール…\nここは異常なしかな？", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104301, 0, 4, 1504, 0, "腹へったら食う！\n悩んだら寝る！\nそれが一番だろ？", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104301, 0, 5, 1505, 0, "のんびりし過ぎるのも\n…性に合わねぇな", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104301, 0, 101, 1506, 0, "そろそろあいつ、\n腹すかしてるん\nじゃないかな？", 4, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104301, 0, 102, 1507, 0, "今日の献立は…\nあいつの好きなものに\nしてやるか", 8, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104301, 0, 1, 1508, 0, "強くなりたいなら、\nあたしがビシバシ\n鍛えてやるよ", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104301, 0, 2, 1509, 0, "天気がいい日は\n外で運動したく\nならねぇか？", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104301, 0, 3, 1510, 0, "腹減ったか？　なんか\n作ってやろうか？", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104301, 0, 4, 1511, 0, "ん？　なんだよ、\n相談事か？\nなんでも言ってくれ", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104301, 0, 101, 1512, 0, "お前が好きな飯って\nなんだ？\n今度、作ってやるよ", 4, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104301, 0, 102, 1513, 0, "お前って不思議だよな\n一緒にいるとなんだか\n…なんでもねーよ", 8, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104301, 0, 1, 1514, 1, "気持ちいい朝だな！\n体を動かしたい気分だ\n付き合ってくれるか", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104301, 0, 2, 1515, 2, "昼飯もう食ったか？\nまだなら、あたしが\nなんか作ってやるよ", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104301, 0, 3, 1516, 3, "日中たっぷり動いたら\nそのぶん夜はしっかり\n休んどけよな", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104301, 0, 1, 1517, 0, "た、たまにはその…\nかわいい格好とか\nしても…", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104301, 0, 2, 1518, 0, "ふわぁ、\n一休みすっかなぁ～", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104301, 0, 3, 1519, 0, "うっ！　か、かわいい\nじゃねぇか", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104301, 0, 4, 1520, 0, "へー　いい匂いだな\nあいつもこういうの\n好きなのかな…", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104301, 0, 5, 1521, 0, "ふわぁ〜！\n温かいと、つい\nウトウトしちまうな", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104301, 0, 6, 1522, 0, "オラオラオラーーー！\n燃えるぜーーー！", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104301, 0, 7, 1523, 0, "いい味してるぜ！\nうちの店でも\n出してみたいな", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104301, 0, 8, 1524, 0, "文字を読んでると\nどうしても眠く\nなっちまうんだよなぁ", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104302, 0, 1, 1526, 0, "パトロール終了～\n本日も異常なしっと！", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104302, 0, 2, 1527, 0, "あたしの店から\n出前を届けようか？", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104302, 0, 3, 1528, 0, "あいつ、早く\n帰ってこないかな", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104302, 0, 4, 1529, 0, "あいつ\n腹すかせてないと\nいいんだけど…", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104302, 0, 5, 1530, 0, "のんびりし過ぎるのも\n…性に合わねぇな", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104302, 0, 101, 1531, 0, "あいつの事を思うと\n胸があったかくなる？\nなんだコレ…？", 8, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104302, 0, 102, 1532, 0, "今日の献立は…\nあいつの好きなものに\nしてやるか", 8, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104302, 0, 1, 1533, 0, "ん？　なんだよ\n相談事か？\nなんでも言ってくれ", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104302, 0, 2, 1534, 0, "天気がいい日は\n外で運動したく\nならねぇか？", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104302, 0, 3, 1535, 0, "腹減ったか？　なんか\n作ってやろうか？", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104302, 0, 4, 1536, 0, "腹へったら食う！\n悩んだら寝る！\nそれが一番だろ？", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104302, 0, 101, 1537, 0, "あのさ…できれば\nもっとそばに…いや\nなんでもねぇ忘れろ！", 8, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104302, 0, 102, 1538, 0, "お前って不思議だよな\n一緒にいるとなんだか\n…なんでもねーよ", 8, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104302, 0, 1, 1539, 1, "気持ちいい朝だな！\n体を動かしたい気分だ\n付き合ってくれるか？", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104302, 0, 2, 1540, 2, "昼飯もう食ったか？\nまだなら、あたしが\nなんか作ってやるよ", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104302, 0, 3, 1541, 3, "あんま夜更しすんなよ\n明日に響いたら\n大変だろ？", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104302, 0, 1, 1542, 0, "た、たまにはその…\nかわいい格好とか\nしても…", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104302, 0, 2, 1543, 0, "ふぅ～♪\n疲れがとれるな～", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104302, 0, 3, 1544, 0, "うっ！　か、かわいい\nじゃねぇか", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104302, 0, 4, 1545, 0, "へー　いい匂いだな\nあいつもこういうの\n好きなのかな…", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104302, 0, 5, 1546, 0, "ふわぁ～！\n温かいと、つい\nウトウトしちまうな", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104302, 0, 6, 1547, 0, "オラオラオラーーー！\n燃えるぜーーー！", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104302, 0, 7, 1548, 0, "いい味してるぜ！\nうちの店でも\n出してみたいな", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104302, 0, 8, 1549, 0, "料理だけじゃなく\n経営術とかも\n学んどかねぇと…", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104501, 0, 1, 1551, 0, "最近なにも\nしてくれません…\n焦らしてるんですね", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104501, 0, 2, 1552, 0, "ぐふふ、じゅるり…\nい、いけない、また\nいつの間にかよだれが", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104501, 0, 3, 1553, 0, "好きな武器といえば\n剣より槍より\nやっぱり鞭…ぐふふ", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104501, 0, 4, 1554, 0, "「無数の触手が\nクウカの柔肌に\n食い込み」…ぐふふ", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104501, 0, 5, 1555, 0, "もしドＳさんが\nＭに転じたらと思うと\n身の毛がよだちます！", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104501, 0, 101, 1556, 0, "ドＳさんを登場\nさせるだけで、妄想に\n無限の広がりが！", 4, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104501, 0, 102, 1557, 0, "あっ…\nジロジロ見てたの\n気づいちゃいました？", 8, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104501, 0, 1, 1558, 0, "視線を感じると\n思ったら…やはり\nドＳさんでしたか", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104501, 0, 2, 1559, 0, "今日は\nどんなヒドイことを\nしてくれるんですか？\nぐふふふ", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104501, 0, 3, 1560, 0, "いつの間にそこに！？\nまさかクウカを監視\nしていたのですか！？", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104501, 0, 4, 1561, 0, "クウカに触るつもり\nですか？　触るつもり\nなのですねっ！？", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104501, 0, 101, 1562, 0, "クウカにナニをする\nつもりですか…\n期待してしまいます！", 4, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104501, 0, 102, 1563, 0, "ドＳさんの視界に入る\nだけでドキドキが…\nこれが恋！？", 8, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104501, 0, 1, 1564, 1, "おはようございます。\n朝からお盛んですね？\nぐふふ…", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104501, 0, 2, 1565, 2, "こんなにも日の高い\nうちからドＳさんは…\nじゅるり", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104501, 0, 3, 1566, 3, "夜はこれから、\nですよ…\nぐふ、ぐふふふ…", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104501, 0, 1, 1567, 0, "キレイにおめかし\nしたのに力ずくで…\nぐふふふ", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104501, 0, 2, 1568, 0, "ソファの背にクウカを\n押し付けて…ぐふふ\nいいですね\nソファドン", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104501, 0, 3, 1569, 0, "「見た目とは裏腹に\nそれは激しく攻め\nたてて」…ぐふふふ", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104501, 0, 4, 1570, 0, "香りを吸ったクウカの\n頬は紅潮し目はうるみ\nもはや抗う力も…！", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104501, 0, 5, 1571, 0, "ああ、身体が\n火照って…\nぐふ、ぐふふ…", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104501, 0, 6, 1572, 0, "はぁ、はぁ、こうして\nクウカの体力を\n奪ってから…ぐふふ", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104501, 0, 7, 1573, 0, "食欲が満たされたら\nつ、次はやはり…！\nそうなのですね！？", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104501, 0, 8, 1574, 0, "この本の下に、\nそういう本を\nカムフラージュ？", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104502, 0, 1, 1576, 0, "「ドＳさんと踊り子\n乱舞の一夜」…\n次の日記はこれで…", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104502, 0, 2, 1577, 0, "最近なにも\nしてくれません…\n焦らしてるんですね", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104502, 0, 3, 1578, 0, "も、妄想が\n足りません…\n禁断症状がっ…！", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104502, 0, 4, 1579, 0, "もしドＳさんが\nＭに転じたらと思うと\n身の毛がよだちます！", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104502, 0, 5, 1580, 0, "次はどんなドＳを\nされるのか、想像する\nだけで…ぐふふふ", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104502, 0, 101, 1581, 0, "じっとしてると\nドＳさんのことばかり\n考えてしまいますね", 8, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104502, 0, 102, 1582, 0, "妄想が止まらなくて\nクウカどうにかなって\nしまいそうです！", 8, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104502, 0, 1, 1583, 0, "ドＳさんといると体が\n火照って…クウカは\nいやらしい子です！", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104502, 0, 2, 1584, 0, "あっ…\nジロジロ見てたの\n気づいちゃいました？", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104502, 0, 3, 1585, 0, "ドＳさんのおかげで\n新しい扉が開けそう\nです…じゅるり", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104502, 0, 4, 1586, 0, "いやー！　ドＳさんに\n襲われ…襲って\nくれないんですか？", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104502, 0, 101, 1587, 0, "クウカはドＳさんに\n身も心も堕とされて\nしまったのですね", 8, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104502, 0, 102, 1588, 0, "ドＳさんがお望みなら\nクウカは\nどんなことだって！", 8, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104502, 0, 1, 1589, 1, "朝だからこそ\nできる妄想が\nあります…ぐふふ", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104502, 0, 2, 1590, 2, "こんな日の高いうち\nからドＳさんは…\nじゅるり", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104502, 0, 3, 1591, 3, "今夜は一晩中…\nなんですね？\nぐふふ…", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104502, 0, 1, 1592, 0, "意外と難しいんですよ\n正しい亀甲縛りと\nいうものは…", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104502, 0, 2, 1593, 0, "くつろいで油断\nしているスキに何を\nされるのでしょう！？", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104502, 0, 3, 1594, 0, "「見た目とは裏腹に\nそれは激しく攻め\nたてて」…ぐふふふ", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104502, 0, 4, 1595, 0, "これは禁断の香り…\nああっ一段階上の\n妄想が開花するうぅ！", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104502, 0, 5, 1596, 0, "ああっ、暖気がまるで\nロウソクのように\nクウカを攻めますっ！", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104502, 0, 6, 1597, 0, "激しい攻めを受けきる\nためには、鍛えないと\nいけないのですね", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104502, 0, 7, 1598, 0, "こんなにいっぱい\n入りません…\nお腹にっ！", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104502, 0, 8, 1599, 0, "どんな本も\nよく見ると\nいやらしいです…！", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104601, 0, 1, 1601, 0, "この前遊んだ猫、\nモッフモフ\nだったにゃあ…♪", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104601, 0, 2, 1602, 0, "ほっぺやお腹が、\n肉球ぐらいぷにぷに…\nこれはまずいにゃ…", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104601, 0, 3, 1603, 0, "大きな仕事が\n決まったにゃ！\n腕が鳴るにゃ～♪", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104601, 0, 4, 1604, 0, "おやつのたい焼き、\nつぶあんとこしあん、\nどっちに\nしようかにゃ？", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104601, 0, 5, 1605, 0, "うにゃー…\nもうちょっとお昼寝\nしたかったにゃ…\nむにゃむにゃ…", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104601, 0, 101, 1606, 0, "キミに呼ばれると、\n猫まっしぐらな気分に\nなっちゃうにゃ♪", 4, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104601, 0, 102, 1607, 0, "キミのおかげで\nギルドの仕事も\n大成功だにゃ♪\nありがとにゃ♪", 8, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104601, 0, 1, 1608, 0, "うずうず…その指、\nじゃれついても\nいいかにゃ？", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104601, 0, 2, 1609, 0, "にゃ？　あたしに何か\nご入り用かにゃ？\nお安くしとくにゃ～♪", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104601, 0, 3, 1610, 0, "うーん、なんだか\n退屈にゃ～\n遊んで、遊んで～？", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104601, 0, 4, 1611, 0, "素早い動きのコツ？\nタダじゃ教えて\nあげられないにゃ～♪", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104601, 0, 101, 1612, 0, "そんなにあたしを\nなでたいにゃ？\nキミならなでさせて\nあげてもいいかにゃー", 4, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104601, 0, 102, 1613, 0, "ふにゃあ、ねえねえ、\n一緒にゴロゴロ\nしようにゃあ？", 8, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104601, 0, 1, 1614, 1, "いい朝にゃ！\n顔を洗ってくるにゃ！", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104601, 0, 2, 1615, 2, "むーん、そろそろ\nお昼…猫たちにも\n猫缶を…", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104601, 0, 3, 1616, 3, "ふわあ…眠いにゃ…\n猫は布団で\n丸くなるにゃ", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104601, 0, 1, 1617, 0, "客商売では、\n外見のチェックも\n欠かせないのにゃ！", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104601, 0, 2, 1618, 0, "うにゃ～！\nお昼寝に\nぴったりにゃ", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104601, 0, 3, 1619, 0, "これ…量産したら\n子どもにバカ売れ\nしそうだにゃ！", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104601, 0, 4, 1620, 0, "この灯り、まるで\n猫みたいな\n癒し効果にゃ！", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104601, 0, 5, 1621, 0, "ふにゃあ、\nあったかいと猫は丸く\nなっちゃうのにゃあ…", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104601, 0, 6, 1622, 0, "本当は外で\n運動したいん\nだけどにゃ", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104601, 0, 7, 1623, 0, "むーん、\n美味しいにゃー！\nたい焼きに\n匹敵するにゃ！", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104601, 0, 8, 1624, 0, "ふむふむ…猫の\nしつけ方は、と…", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104602, 0, 1, 1626, 0, "かわいいネコの置物を\nゲットしたにゃ！\nどこに飾ろうか\nにゃ～♪", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104602, 0, 2, 1627, 0, "仕事の交渉がうまく\n行ったにゃ～♪\nいい気分だにゃ～♪", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104602, 0, 3, 1628, 0, "ねこじゃらしには\n食いついちゃうにゃ\nびしっ！　びしっ！", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104602, 0, 4, 1629, 0, "ほっぺやお腹が、肉球\nぐらいぷにぷに…\nこれはまずいにゃ…", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104602, 0, 5, 1630, 0, "さっきのたいやき\nおいしかったにゃ♪\nごろごろごろ…", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104602, 0, 101, 1631, 0, "手作りのたい焼き\nあいつは食べて\nくれるかにゃ…？", 8, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104602, 0, 102, 1632, 0, "あいつには、いつか\nたっぷり恩返しが\nしたいにゃ…", 8, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104602, 0, 1, 1633, 0, "にゃーに？　あたしの\nことがそんなに気に\nなるのかにゃ？", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104602, 0, 2, 1634, 0, "あたしのこと…\nしっかり捕まえ\nといてにゃ？", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104602, 0, 3, 1635, 0, "あたしの力が\n必要かにゃ？　猫の手\n貸してあげるにゃ♪", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104602, 0, 4, 1636, 0, "たいやきとクレープを\n合わせれば…\n新しい商売のニオイが\nするにゃ…！", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104602, 0, 101, 1637, 0, "ふにゃあ、ねえねえ\n一緒にゴロゴロ\nしようにゃあ？", 8, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104602, 0, 102, 1638, 0, "キミと一緒にいると\n楽しくなってつい\nじゃれちゃうにゃ！", 8, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104602, 0, 1, 1639, 1, "いい朝にゃ！\n顔を洗ってくるにゃ！", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104602, 0, 2, 1640, 2, "そろそろおっひる～\nにゃーんか\n食べたい…", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104602, 0, 3, 1641, 3, "うーん、眠くなって\nきたにゃ。あたしは\n夜行性じゃないのにゃ", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104602, 0, 1, 1642, 0, "にゃにゃ…\n髪が乱れてるにゃ", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104602, 0, 2, 1643, 0, "ふっかふかにゃ～\n猫がねころん\nじゃうにゃ", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104602, 0, 3, 1644, 0, "これ、猫と匹敵する\nぐらいかわいいにゃ！", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104602, 0, 4, 1645, 0, "猫でもこわくない\n優しい灯りにゃ！", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104602, 0, 5, 1646, 0, "ぽっかぽか\nしあわせな気分にゃ～", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104602, 0, 6, 1647, 0, "雨の日でも運動\nできるのは、濡れ\nなくて便利だにゃ！", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104602, 0, 7, 1648, 0, "美味しいにゃ～！\nこれ、朝昼晩\n食べたいにゃ！", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104602, 0, 8, 1649, 0, "本を読んで\n猫の気持ちを\n勉強するのにゃ！", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104801, 0, 1, 1651, 0, "内職手伝える人\nいないかしら？", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104801, 0, 2, 1652, 0, "次の依頼は、\nこういう計画で…\nうん、完璧ね！", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104801, 0, 3, 1653, 0, "次の特売セールに\n向けて、しっかり\n準備しないと！", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104801, 0, 4, 1654, 0, "配達の後に売り子\n次に採取のバイト…\nうん、効率的ね", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104801, 0, 5, 1655, 0, "一日に受ける\nバイトの数、もう少し\n増やしたいわね", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104801, 0, 101, 1656, 0, "師匠として\n頼りにされたいし\nもっと頑張らなきゃ！", 4, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104801, 0, 102, 1657, 0, "男の子と効率的に\n仲良くなる方法…\nこればっかりは難題ね", 8, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104801, 0, 1, 1658, 0, "あら、\nちょうどよかった\n今から一緒に\nバイトでもどう？", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104801, 0, 2, 1659, 0, "相談事かしら？\nそれくらいなら\n無料でいいわよ", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104801, 0, 3, 1660, 0, "背後の気配なら\nすぐ分かるわ\n試してみる？", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104801, 0, 4, 1661, 0, "道具やお金の管理は\n大丈夫？　手伝うから\nいつでも言って", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104801, 0, 101, 1662, 0, "暇ができたから\n働きたいけど…\nキミとの時間も大切ね", 4, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104801, 0, 102, 1663, 0, "ねえ、あの…\n私はキミにとって…\nいや、何でもないわ", 8, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104801, 0, 1, 1664, 1, "おはよう！\n今日も朝から\nバイトよ！", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104801, 0, 2, 1665, 2, "お昼時ね。\n少し休憩したら、\nまた働くわよ！", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104801, 0, 3, 1666, 3, "もうこんな時間！\n明日もバイトで\n朝早いのよ…", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104801, 0, 1, 1667, 0, "接客業の依頼が\nある時は、外見も\nキレイにしないとね", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104801, 0, 2, 1668, 0, "はぁ～バイトで\n疲れたから癒される～", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104801, 0, 3, 1669, 0, "これを内職で\n作ったら\n儲かるかしら？", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104801, 0, 4, 1670, 0, "リフレッシュできたわ\nこれでまた、バリバリ\n働けるわね！", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104801, 0, 5, 1671, 0, "風邪を引いたら効率が\n落ちちゃうから、\n暖めないと！", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104801, 0, 6, 1672, 0, "働き続けるには\n健康第一！　それには\n運動が一番よ", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104801, 0, 7, 1673, 0, "もぐもぐ…\n腹が減っては\n戦ができぬ、よ！", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104801, 0, 8, 1674, 0, "日々絶えず学んで、\n生活に活かすのよ！", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104802, 0, 1, 1676, 0, "家計簿をつけなきゃ！\n覚えている時に\nやっておかないとね", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104802, 0, 2, 1677, 0, "次の依頼は\nこういう計画で…\nうん、完璧ね！", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104802, 0, 3, 1678, 0, "ギルドの経費\nあとちょっとだけ\n削れそうね…", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104802, 0, 4, 1679, 0, "少しでも時給のいい\nバイトがしたいけど\nなかなか\n出会えないのよ", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104802, 0, 5, 1680, 0, "今度のバイトも\nなかなか大変そう\nね…腕が鳴るわ", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104802, 0, 101, 1681, 0, "男の子と効率的に\n仲良くなる方法…\nこればっかりは難題ね", 8, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104802, 0, 102, 1682, 0, "私、どうしたんだろ？\n仕事中もずっと\n彼のこと考えてる…", 8, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104802, 0, 1, 1683, 0, "この間はヘルプに\n来てくれてありがとう\n頼んで正解だったわ", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104802, 0, 2, 1684, 0, "見つけた！　ちょうど\n頼みたいことが\nあるんだけど、いい？", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104802, 0, 3, 1685, 0, "今から\n休憩するところなの\n一緒にどう？", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104802, 0, 4, 1686, 0, "ん？　どうしたの？\n私のことが\n気になるの？", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104802, 0, 101, 1687, 0, "…たまには甘えても\nいい？　私だって\n弱る時はあるのよ", 8, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104802, 0, 102, 1688, 0, "暇ができたから\n働きたいけど…\nキミとの時間も大切ね", 8, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104802, 0, 1, 1689, 1, "おはよう！\nいい朝ね。一緒に\nランニングでもいく？", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104802, 0, 2, 1690, 2, "お昼時ね\n少し休憩したら\nまた働くわよ！", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104802, 0, 3, 1691, 3, "もうこんな時間！\n明日もバイトで\n朝早いのよ…", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104802, 0, 1, 1692, 0, "女の子らしく…\nどうも忘れちゃう\nのよね", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104802, 0, 2, 1693, 0, "ここで少し\n休んだら\nまたバイトよ！", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104802, 0, 3, 1694, 0, "これを内職で\n作ったら\n儲かるかしら？", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104802, 0, 4, 1695, 0, "仕事から帰ってきて\nこういうのがあると\n素敵ね", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104802, 0, 5, 1696, 0, "しっかり身体を\n温めて、血流を\nよくしなくっちゃ", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (104802, 0, 6, 1697, 0, "これで休憩中も\n無駄なく\n鍛えられるわ♪", 1, 3, 1);
