INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105002, 0, 2, 1784, 0, "アタシに合う服\n雑誌で探そうっと！\nサイズじゃないわ\nデザインとかの話よ！", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105002, 0, 3, 1785, 0, "頭撫でないで！\nお子様みたいに\nあつかわないでって\n言ってるのに～！", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105002, 0, 4, 1786, 0, "壁ドンの練習…\nあれ？　壁ドンって\n男がするんだっけ？", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105002, 0, 101, 1787, 0, "ねーねー！\nアタシ、かわいい？\nかわいいわよね？", 8, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105002, 0, 102, 1788, 0, "これから出かけるの？\nしっかりアタシを\nエスコートしてね！", 8, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105002, 0, 1, 1789, 1, "レディの朝は\n忙しいのよ", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105002, 0, 2, 1790, 2, "お腹すいたー\nダイエット\nやめちゃおうかなー", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105002, 0, 3, 1791, 3, "エイジングケアを\nしないとねー", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105002, 0, 1, 1792, 0, "身だしなみチェック\nオッケー！　これで\nいつアイツが来ても\n大丈夫ね！", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105002, 0, 2, 1793, 0, "あ！　これ雑誌で\n見たことあるわ～\nだからきっと\nオシャレなのよ！", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105002, 0, 3, 1794, 0, "あたしがもっと\nオシャレにかわいく\nしてあげようか？", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105002, 0, 4, 1795, 0, "オシャレ～♪\n大人なレディって\n感じよね♪", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105002, 0, 5, 1796, 0, "あったかーい♪\nここで宿題\nしようかしら？", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105002, 0, 6, 1797, 0, "運動したら成長\nしないかしら？\n背とか胸とか…", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105002, 0, 7, 1798, 0, "レディは、食べ物にも\n気を使うのよ", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105002, 0, 8, 1799, 0, "おもしろーい！\n…でも絵本って\n子供っぽいかしら？", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105101, 0, 1, 1801, 0, "魔法と科学\nどっちも極めて\nみせるわ", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105101, 0, 2, 1802, 0, "仕事も片付いたし\n部屋で\n実験実験…♪", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105101, 0, 3, 1803, 0, "たまった治療代の\n請求書を\nまとめないと…", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105101, 0, 4, 1804, 0, "新薬が出来たし\nすぐに試したいけど…\n実験台がいないわ", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105101, 0, 5, 1805, 0, "部屋にこもりすぎると\n曜日の感覚が\nなくなるわね", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105101, 0, 101, 1806, 0, "あの子の記憶を\n取り戻せる薬…\n作れないものかしら", 4, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105101, 0, 102, 1807, 0, "好意に気づいて貰える\n薬って、どうすれば\n作れるのかしら", 8, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105101, 0, 1, 1808, 0, "あら、ヒマなら\n薬草の整理を\n手伝って", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105101, 0, 2, 1809, 0, "もしかして実験に\n付き合ってくれるの？\nえ？　違う？", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105101, 0, 3, 1810, 0, "具合が悪い？\nなら診てあげる\nまずは触診から…", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105101, 0, 4, 1811, 0, "植物に興味あるの？\nなら教えてあげるわ\n色々とね…", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105101, 0, 101, 1812, 0, "キミの傍にいるだけで\n高揚しちゃうのは\n何故かしら…？", 4, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105101, 0, 102, 1813, 0, "私はおしおきする\n側のことが多いけど…\nキミにはされたいかも", 8, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105101, 0, 1, 1814, 1, "お昼までに調合用の\n花を集めたいの\n手伝ってくれる？", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105101, 0, 2, 1815, 2, "あら、もうお昼？\nそろそろ一息\n入れましょうか", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105101, 0, 3, 1816, 3, "夜も更けて来たわね\n私は朝まで実験だけど\n見学していく？", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105101, 0, 1, 1817, 0, "医者たるもの\nいつだって\n清潔に…♪", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105101, 0, 2, 1818, 0, "いいわねこれ\n仮眠の時に\n使わせてもらうわ", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105101, 0, 3, 1819, 0, "ぬいぐるみなら\n動物も…", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105101, 0, 4, 1820, 0, "いい香り…\n仕事場にも\n置いてみようかしら", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105101, 0, 5, 1821, 0, "ここにいると\nついウトウト\nしちゃうわ…", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105101, 0, 6, 1822, 0, "座ってる時間が多いと\n運動不足になるし\nたまにはいいわね", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105101, 0, 7, 1823, 0, "研究していると\nつい食べるのを\n忘れちゃうのよね", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105101, 0, 8, 1824, 0, "実験もいいけど\nいつか成果を\n本にしないと…", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105102, 0, 1, 1826, 0, "徹夜はツラいけど\n集中すると時間の\n感覚がなくなって…", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105102, 0, 2, 1827, 0, "痛みに悶える顔って\nどうしてあんなに\n興奮するのかしら？", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105102, 0, 3, 1828, 0, "彼に罵られたら\nどんな気分かしら\n意外と快感かも？", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105102, 0, 4, 1829, 0, "毒を薄めると\n薬になるのって\n今でも不思議だわ", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105102, 0, 5, 1830, 0, "綺麗なバラには\nトゲがあるけど\n魅力の一つよね", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105102, 0, 101, 1831, 0, "カレをさりげなく\n惚れさせる方法を\n研究するわよ…♪", 8, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105102, 0, 102, 1832, 0, "好意に気づいて貰える\n薬って、どうすれば\n作れるのかしら", 8, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105102, 0, 1, 1833, 0, "構って欲しいの？\nじゃあ、君の体を\n調べさせて？", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105102, 0, 2, 1834, 0, "実験に付き合って\n大丈夫よ、試すのは\nただの栄養剤だから", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105102, 0, 3, 1835, 0, "欲しい薬が\nあったら言ってね\n調合してみるから", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105102, 0, 4, 1836, 0, "おしおきされたいの？\nキミにだったら\nいつでもしてあげる", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105102, 0, 101, 1837, 0, "キミの傍にいるだけで\n高揚しちゃうのは\n何故かしら…？", 8, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105102, 0, 102, 1838, 0, "私はおしおきする\n側のことが多いけど…\nキミにはされたいかも", 8, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105102, 0, 1, 1839, 1, "朝しか取れない\n植物があるの\n採取しに行くわよ", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105102, 0, 2, 1840, 2, "お昼時ってやけに\n眠たくなるわよね\n…調べたくなるわ", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105102, 0, 3, 1841, 3, "今日くらいは\nゆっくり眠りたいわ\n…キミと一緒にね", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105102, 0, 1, 1842, 0, "身なりについて\n研究するのも\n楽しそうね", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105102, 0, 2, 1843, 0, "休息に必要な道具は\n妥協せずに選ぶ！\nこれだけは譲れないわ", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105102, 0, 3, 1844, 0, "たまにはこういう\n可愛らしいものに\n触れないとね♪", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105102, 0, 4, 1845, 0, "いい香り…\n仕事場にも\n置いてみようかしら", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105102, 0, 5, 1846, 0, "仕事がなかったら\nこのまますーっと\n眠りたいのに…", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105102, 0, 6, 1847, 0, "運動しないと\n体型の維持が…", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105102, 0, 7, 1848, 0, "適度な食事も\n大切よね", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105102, 0, 8, 1849, 0, "まさか私が\n恋愛読本を読む\n日が来るなんて…", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105201, 0, 1, 1851, 0, "ぺっぺっ！\n…い、いけないっ！", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105201, 0, 2, 1852, 0, "いっけな～い！\nひづめで床に傷が！", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105201, 0, 3, 1853, 0, "太っちゃったかも～！\n伐採でダイエット\nしなくっちゃ！", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105201, 0, 4, 1854, 0, "きちんと生活すると\n毛づやが良くなる\nみたいなのよね～", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105201, 0, 5, 1855, 0, "みんな、体重\nどれくらいなのかな？", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105201, 0, 101, 1856, 0, "体力なら自信\nあるんだけど…\n恋にはちょっぴり\n臆病になっちゃう", 4, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105201, 0, 102, 1857, 0, "いつか人間の女の子\nみたいなデート、\nしてみたいな～♪", 8, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105201, 0, 1, 1858, 0, "私ね「どこにでもいる\n普通の女の子」って\n言葉が憧れなんだ～♪", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105201, 0, 2, 1859, 0, "チャームポイント？\n自分では分からない\nけど…まつげかなぁ？", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105201, 0, 3, 1860, 0, "リマリマ～♪\nはっ！？\nわ、私ったら今何を？", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105201, 0, 4, 1861, 0, "力仕事ならまかせて！", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105201, 0, 101, 1862, 0, "人間になりたいな～\nそれか、あなたが\n私みたいな姿に…♪", 4, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105201, 0, 102, 1863, 0, "しょうがないなあ、\nこんなにモフらせるの\nあなただけだよ？", 8, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105201, 0, 1, 1864, 1, "今朝は寝癖がひどいわ\nしっぽの辺りとか～", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105201, 0, 2, 1865, 2, "ランチは\nおしゃれなカフェで\nしてみたいな～♪", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105201, 0, 3, 1866, 3, "お肌のケアに\nパックしながら\n寝ようかな～", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105201, 0, 1, 1867, 0, "やだっ、\nまた寝ぐせついてる！\n直さないと！", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105201, 0, 2, 1868, 0, "ミシッっていった！\n私、重すぎるかも！？", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105201, 0, 3, 1869, 0, "あ！　あなたも\n美人さんね～♪", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105201, 0, 4, 1870, 0, "はぁ、まったり…\n四足歩行しちゃい\nそうだわ…", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105201, 0, 5, 1871, 0, "あったか～い♪\n…私、毛皮だから\n暑すぎるかも", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105201, 0, 6, 1872, 0, "な、なんだか野生に\n帰っちゃいそう！", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105201, 0, 7, 1873, 0, "ぱくぱく、もぐもぐ、\nぺっぺっ…\nやだ、はしたない！", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105201, 0, 8, 1874, 0, "きゃーーー！\nこの恋愛小説\nとっても\nドキドキしちゃう～", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105202, 0, 1, 1876, 0, "素敵ね～、居るだけで\n女子力があがりそうな\nお部屋だわ～", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105202, 0, 2, 1877, 0, "私、大丈夫かしら？\n他の子に怖がられたり\nしないかな…？", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105202, 0, 3, 1878, 0, "動物苑…って\n嫌なギルド名ね…", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105202, 0, 4, 1879, 0, "好き、嫌い、好き…\nあっ！　うっかりして\n花占いのお花\n食べちゃった！", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105202, 0, 5, 1880, 0, "ここ、女の子らしい\nものでいっぱいね～！\nときめいちゃうわ～♪", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105202, 0, 101, 1881, 0, "体力なら自信\nあるんだけど…\n恋にはちょっぴり\n臆病になっちゃう", 8, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105202, 0, 102, 1882, 0, "あの人が\n来てくれるまで薪割り\nでもしてよっかな？", 8, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105202, 0, 1, 1883, 0, "一緒にお出かけ？\nうれしいけど…\n心の準備が！\nはわわわ！", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105202, 0, 2, 1884, 0, "どうしたの？\n急に抱きついてきて…\n癒されたいの？", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105202, 0, 3, 1885, 0, "ぺっぺっ！\n…い、いけないっ！", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105202, 0, 4, 1886, 0, "メタモルアップルを\nここで食べたら、\nみんな驚くわよね", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105202, 0, 101, 1887, 0, "しょうがないなあ、\nこんなにモフらせるの\nあなただけだよ？", 8, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105202, 0, 102, 1888, 0, "人間になりたいな～\nそれか、あなたが\n私みたいな姿に…♪", 8, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105202, 0, 1, 1889, 1, "今朝は寝癖がひどいわ\nしっぽの辺りとか～", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105202, 0, 2, 1890, 2, "ダイエット中だから\nお昼は抜いてるの～", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105202, 0, 3, 1891, 3, "お肌のケアに\nパックしながら\n寝ようかな～", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105202, 0, 1, 1892, 0, "ちゃんとかわいく\nしてないと刈られ…\n狩られちゃうもんね！", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105202, 0, 2, 1893, 0, "ミシッっていった！\n私、重すぎるかも！？", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105202, 0, 3, 1894, 0, "あ！　あなたも\n美人さんね～♪", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105202, 0, 4, 1895, 0, "いい香り～\n森の中にいるみたいに\nとっても落ち着く～", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105202, 0, 5, 1896, 0, "素敵～♪　…\nきゃっ！　私の毛が\nたいへんなことに～！", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105202, 0, 6, 1897, 0, "な、なんだか野生に\n帰っちゃいそう！", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105202, 0, 7, 1898, 0, "何を食べるかより\n誰と食べるかが\n大事なのよね", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105202, 0, 8, 1899, 0, "きゃーーー！\nこの恋愛小説\nとっても\nドキドキしちゃう～", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105301, 0, 1, 1901, 0, "ヴァイスフリューゲル\nを盛り上げる方法、\n共に語らおう！", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105301, 0, 2, 1902, 0, "私は子供ではない…！\nが、お菓子は\nいただこう！", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105301, 0, 3, 1903, 0, "部屋はきちんと\n片づけておかなくては\n部屋の汚れは\n心の汚れだ", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105301, 0, 4, 1904, 0, "むむむ…明日\n目が覚めたら、身長が\n伸びていないものか…", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105301, 0, 5, 1905, 0, "お菓子が常備されて\nいる部屋。そんな\nものが、私の好みだ…", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105301, 0, 101, 1906, 0, "あいつとの新たな\n鍛錬が\n待ち遠しいな…！", 4, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105301, 0, 102, 1907, 0, "最近、あいつと\n過ごしていて、毎日が\n充実しているな…", 8, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105301, 0, 1, 1908, 0, "軍人の本分は日々の\n研鑽にあるからな。\n今日も鍛錬に\n行かないか？", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105301, 0, 2, 1909, 0, "毎日牛乳を飲んでいる\nのだが、なかなか背が\n伸びないものだな…", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105301, 0, 3, 1910, 0, "貴公にも、飴をくれて\nやろうか？\nあまいぞ～！\n美味しいぞ～！", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105301, 0, 4, 1911, 0, "私ともっと高く、\n共に飛ぼう！", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105301, 0, 101, 1912, 0, "貴公は、本当に頼りに\nなる男だな…いや、\nふとそう思っただけだ", 4, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105301, 0, 102, 1913, 0, "貴公と共にいられる\nことは私の喜びだ。\nこれからも\nよろしく頼むぞ", 8, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105301, 0, 1, 1914, 1, "うむ、いい朝だ。\n本国の夜明けを\n思い出してしまうな！", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105301, 0, 2, 1915, 2, "そろそろ昼食に\nするか。腹が減っては\n戦はできないからな", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105301, 0, 3, 1916, 3, "ふわああ…私は子供\nではないが…眠い時は\n眠いのだ…", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105301, 0, 1, 1917, 0, "身だしなみは、心の\n在りようを映すから\n気をつけなくてはな", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105301, 0, 2, 1918, 0, "ふう…軍人たるもの\n常日頃、油断は\nできないのだが…\nお、落ち着くな…", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105301, 0, 3, 1919, 0, "うむ…このかわいさ…\n本国に持って\n帰りたい…", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105301, 0, 4, 1920, 0, "このともし火、戦いに\n明け暮れる我らの心を\n癒すものだな…", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105301, 0, 5, 1921, 0, "はあ～、ぽかぽか…\nい、いや！\n気を緩めないぞっ！", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105301, 0, 6, 1922, 0, "うおおおおおっ！\n鍛錬、鍛錬、\n鍛錬っ！！", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105301, 0, 7, 1923, 0, "いつ戦いになっても\n困らぬように、\n腹八分目に\nしておくのだ！", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105301, 0, 8, 1924, 0, "これを読んで、新たな\n戦術を編み出すぞっ！", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105302, 0, 1, 1926, 0, "たまには掃除でも\nするか。部屋が\n綺麗になるのは\n喜ばしいことだ", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105302, 0, 2, 1927, 0, "部屋の中では\n剣を振り回せないのが\n玉に瑕だな…", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105302, 0, 3, 1928, 0, "お菓子が常備されて\nいる部屋。そんな\nものが、私の好みだ…", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105302, 0, 4, 1929, 0, "毎日牛乳を飲んでいる\nのだが、なかなか背が\n伸びないものだな…", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105302, 0, 5, 1930, 0, "家庭を守れない者が\n国を守れるはずが\nない。この部屋も\n私が守るぞ！", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105302, 0, 101, 1931, 0, "あいつは今頃、何を\nしているのだろうな…\nふふっ", 8, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105302, 0, 102, 1932, 0, "あいつと会える日が\nこんなにも待ち遠しく\nなるとはな…", 8, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105302, 0, 1, 1933, 0, "軍人の本分は日々の\n研鑽にあるからな\n今日も鍛錬に\n行かないか？", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105302, 0, 2, 1934, 0, "おお、今日も鍛錬の\n時間だったか！\nすまないな\nすぐ行く！", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105302, 0, 3, 1935, 0, "私は子供ではない！\nが…たまには童心に\n帰りたい時もある…", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105302, 0, 4, 1936, 0, "貴公にも、飴を\nくれてやろうか？\nあまいぞ～！\n美味しいぞ～！", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105302, 0, 101, 1937, 0, "私と歩きたいのか…？\nそうだな。二人で\n散歩でも行こうか", 8, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105302, 0, 102, 1938, 0, "貴公と共にいられる\nことは私の喜びだ\nこれからも\nよろしく頼むぞ", 8, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105302, 0, 1, 1939, 1, "うむ、いい朝だ\n本国の夜明けを\n思い出してしまうな！", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105302, 0, 2, 1940, 2, "そろそろ昼時だな\n鍛錬の手を緩めて\n昼食にするか", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105302, 0, 3, 1941, 3, "寝る前に…あと少し\nだけ、鍛錬を…", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105302, 0, 1, 1942, 0, "ヴァイス\nフリューゲルの一員と\nして恥ずかしくないよう\n身なりを整えなくては", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105302, 0, 2, 1943, 0, "ふう…軍人たるもの\n常日頃、油断は\nできないのだが…\nお、落ち着くな…", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105302, 0, 3, 1944, 0, "うむ…このかわいさ…\n本国に持って\n帰りたい…", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105302, 0, 4, 1945, 0, "このともし火\n戦いに明け暮れる\n我らの心を\n癒すものだな…", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105302, 0, 5, 1946, 0, "はあ～、ぽかぽか…\nい、いや！\n気を緩めないぞっ！", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105302, 0, 6, 1947, 0, "うおおおおおっ！\n鍛錬、鍛錬\n鍛錬っ！！", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105302, 0, 7, 1948, 0, "いつ戦いになっても\n困らぬように\n腹八分目に\nしておくのだ！", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105302, 0, 8, 1949, 0, "うむ…脳細胞が\n活性化してきたぞ\n読書は大事だな", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105701, 0, 1, 1951, 0, "私たちの騎空艇\nどんなデザインに\nしよう…？", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105701, 0, 2, 1952, 0, "団員募集の\nポスター\n作ろうっと♪", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105701, 0, 3, 1953, 0, "父さん…\n今頃どこで\n何してるんだろ？", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105701, 0, 4, 1954, 0, "騎空艇に乗って、\n誰もいったことがない\n場所を見つけたいな", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105701, 0, 5, 1955, 0, "父さん以上の\n世界一の\n騎空士になりたい！", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105701, 0, 101, 1956, 0, "仲間と行く空の旅…\n何度想像しても\nドキドキするなぁ", 4, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105701, 0, 102, 1957, 0, "父さん…\n私にもね、大切な\n仲間ができたよ", 8, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105701, 0, 1, 1958, 0, "仕事の依頼？\nすぐに支度\nしてくるね！", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105701, 0, 2, 1959, 0, "ねえねえ！\n団員募集のポスター\n一緒に作ろうよ！", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105701, 0, 3, 1960, 0, "なーにー？\nどうしたの？\n今呼んだよね？", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105701, 0, 4, 1961, 0, "今は休憩中だよ？\nだからあなたも\nゆっくりしてて", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105701, 0, 101, 1962, 0, "私にとってあなたは\nとっても頼りになる、\n自慢の団員だよ！", 4, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105701, 0, 102, 1963, 0, "お父さんに会えたら、\n大切な仲間として\nあなたを紹介するね！", 8, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105701, 0, 1, 1964, 1, "おはよう！\n体の調子はどう？\n今日は頑張れそう？", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105701, 0, 2, 1965, 2, "ねえねえ、お昼\n食べに行こうよ！\nお腹空いちゃった", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105701, 0, 3, 1966, 3, "…あれ？\nおしゃべりしてたら\nもうこんな時間？", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105701, 0, 1, 1967, 0, "どうせならオシャレな\n騎空士って\n呼ばれたいな", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105701, 0, 2, 1968, 0, "ふぅ、疲れた～…\nちょっとだけ\n休んじゃおっと", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105701, 0, 3, 1969, 0, "よし！　キミは\n今から団長代理だ！\n…なんてね♪", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105701, 0, 4, 1970, 0, "いい香り～♪\n気持ちが\n落ち着く～…", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105701, 0, 5, 1971, 0, "これって\n騎空艇に\n置けるかな…？", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105701, 0, 6, 1972, 0, "どんな仕事でも\nこなせるような\n体力がないとね…！", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105701, 0, 7, 1973, 0, "あむっ…うん！\n美味しい～！\n今度作ってみよう♪", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105701, 0, 8, 1974, 0, "空を冒険するには\n色んなことを\n知っておかないと…", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105702, 0, 1, 1976, 0, "…戻らなくなったよ\nどうすれば元に\n戻る…？", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105702, 0, 2, 1977, 0, "暇だよ…\nどこかに強い人は\nいないか？", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105702, 0, 3, 1978, 0, "ジョブチェンジ！\n…叫んでも\n戻らないよ", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105702, 0, 4, 1979, 0, "今のうちに、\n魔物退治の\n仕事に行くね！", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105702, 0, 5, 1980, 0, "ハァ～…\nうん、気の巡りは\n問題ないね！", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105702, 0, 101, 1981, 0, "私の力の源は\n仲間との\n絆だよ！", 8, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105702, 0, 102, 1982, 0, "いつかこの拳を\n空を越えた、星まで\n届かせたいね", 8, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105702, 0, 1, 1983, 0, "功夫が足りない人は\nどうすればいいか？\nもちろん修行だよ！", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105702, 0, 2, 1984, 0, "私はいつも通りに\n喋ってるつもり\nだよ？", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105702, 0, 3, 1985, 0, "どうした？\nもしかして手合せ\nしたいか？", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105702, 0, 4, 1986, 0, "騎空士としても\n拳法家としても\n目指すは世界一ね！", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105702, 0, 101, 1987, 0, "背中はあなたに\n預けたよ！\nよろしくね！", 8, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105702, 0, 102, 1988, 0, "あなたと一緒なら\nきっと、今よりもっと\n強くなれるよ！", 8, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105702, 0, 1, 1989, 1, "ちゃんと起きてるか？\n寝ぼけてたら\nダメよ？", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105702, 0, 2, 1990, 2, "腹減ったね…\nなにか食べに\n行くよ！", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105702, 0, 3, 1991, 3, "修行のしすぎは\n良くないよ？\n気をつけるね", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105702, 0, 1, 1992, 0, "美しい技は\n美しい体が\nあってこそ！", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105702, 0, 2, 1993, 0, "よいしょっと…\nちょっとのんびり\nさせてもらうよ", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105702, 0, 3, 1994, 0, "…練習に\n使うのはさすがに\n可哀そうね", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105702, 0, 4, 1995, 0, "これは…\n瞑想の時にも\n使ってみたいね", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105702, 0, 5, 1996, 0, "出来ればもっと\n熱いほうが\n嬉しいよ", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105702, 0, 6, 1997, 0, "ここでも\n修行出来るなんて\n最高だよ！", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105702, 0, 7, 1998, 0, "気力の源は体力！\n体力の源は\n健康とご飯！", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105702, 0, 8, 1999, 0, "んー…\n難しいことは\nわかんないよ！", 1, 3, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105801, 0, 1, 2001, 0, "わたしたちの\nギルドハウスへ\nようこそ～！", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105801, 0, 2, 2002, 0, "ぐるるる～…\nあ、お腹が\n鳴っちゃいました…", 1, 1, 1);
