INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105801, 0, 5, 2005, 0, "この部屋が、わたしの\n帰る場所です♪", 1, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105801, 0, 101, 2006, 0, "あの人を見ると\n胸が満たされます～\nお腹は空いてますが！", 4, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105801, 0, 102, 2007, 0, "あの人、\n大食いの子って\n嫌いかな？", 8, 1, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105801, 0, 1, 2008, 0, "食べもの、くれるん\nですか？　わーい！\n恋しちゃいそう♪", 1, 2, 1);
