INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105801, 0, 1, 2014, 1, "朝ごはんも終わったし\n早速ブランチを\n食べませんか？", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105801, 0, 2, 2015, 2, "お昼ご飯、\nおいしかったですね～\nそれじゃあ、そろそろ\nおやつにしましょう☆", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105801, 0, 3, 2016, 3, "お夕飯も、晩ごはんも\n食べたし…さぁ！\nお夜食の時間ですよ☆", 1, 2, 1);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105801, 0, 1, 2017, 0, "身だしなみ、\n身だしなみ…\nきちんとしないとね♪", 1, 3, 1);
