INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (105802, 0, 1, 2033, 0, "ごはんに行きますか？\n作りましょうか？\n店屋物もいいですね～", 1, 2, 1);
