INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 100202, 1, "おはよう\nよく眠れた？　今日も\n一日、頑張ろうね♪", 1, 1, 1, 89, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 100202, 1, "お腹空いちゃった…\nそうだ。騎士クンも\n一緒にお昼食べない？", 2, 2, 1, 90, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 100202, 1, "もうこんな時間…\nそろそろ寝なくちゃ\nおやすみなさい", 3, 3, 1, 91, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 100202, 1, "よし…\nこれなら\n褒めてもらえるかな？", 1, 0, 1, 92, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 100202, 1, "がんばることも\n必要だけど\nお休みも大切だよね", 2, 0, 1, 93, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 100202, 1, "かわいい～♪\n騎士クンは\nこういうのって好き？", 3, 0, 1, 94, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 100202, 1, "戦う前にもこうやって\n落ち着けたら\nいいのに", 4, 0, 1, 95, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 100202, 1, "あの…！\nよ、良かったら\n一緒に…！", 5, 0, 1, 96, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 100202, 1, "騎士クンと一緒に\n冒険するなら\n体力がないと…！", 6, 0, 1, 97, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 100202, 1, "最近服のサイズが\n合わないんだけど…\n食べ過ぎなのかな？", 7, 0, 1, 98, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 100202, 1, "…やっぱり\n月刊恋愛指南は\nためになる…", 8, 0, 1, 99, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 100301, 1, "装備の手入れでも\nしようかな…", 1, 0, 1, 101, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 100301, 1, "次に鍛えるべきは\n肉体面か精神面か\nどちらにしよう…？", 2, 0, 1, 102, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 100301, 1, "ツムギが作ってくれた\nこの服…一体\nいつサイズを…？", 3, 0, 1, 103, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 100301, 1, "仲間なんていらない\nそう思ってた頃も\nあったな…", 4, 0, 1, 104, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 100301, 1, "考えごとをしていると\nつい部屋中を\n歩いてしまうな", 5, 0, 1, 105, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 100301, 1, "彼は\nどこまで強くなるのか\n私も負けられないな", 101, 0, 4, 106, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 100301, 1, "私が一本を取られる\nなんてね…願い事は\nもう決まったのかな？", 102, 0, 8, 107, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 100301, 1, "ああ、キミだったのか\n人の気配がしたから\n誰かと思ったよ", 1, 0, 1, 108, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 100301, 1, "斬りかかるつもりなら\nちゃんとかかってきて\n…呼んだだけ？", 2, 0, 1, 109, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 100301, 1, "なに？　私に相談？\n戦いについてなら\n喜んで", 3, 0, 1, 110, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 100301, 1, "今のが突き？\n軽い！　もっと鋭く！\nさあ、もう一回！", 4, 0, 1, 111, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 100301, 1, "ひゃっ！　ど、どこを\n触ってるの！？\n…油断した", 101, 0, 4, 112, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 100301, 1, "キミにはこれからも\n私のそばに…\nいや、なんでもない", 102, 0, 8, 113, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 100301, 1, "おはよう\n今日も充実した\n一日にしていこう", 1, 1, 1, 114, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 100301, 1, "そろそろ\nお茶の時間か\nキミも一緒にどう？", 2, 2, 1, 115, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 100301, 1, "遅くまで大変だね\nまだ仕事があるなら\n手伝おうか？", 3, 3, 1, 116, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 100301, 1, "戦いを経てもなお\n美しくあるように…\nこれでよし♪", 1, 0, 1, 117, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 100301, 1, "戦ってばかりで\n休まないと、また\nユイに怒られるな", 2, 0, 1, 118, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 100301, 1, "ヒヨリみたいに\n練習相手に…\nいや、やめておこう", 3, 0, 1, 119, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 100301, 1, "これはいい…\n戦いの前の精神統一に\nもってこいじゃないか", 4, 0, 1, 120, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 100301, 1, "時々は落ち着いた\n時間を過ごすのも\n悪くない…", 5, 0, 1, 121, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 100301, 1, "彼に負けないよう\n私も更に強くなろう\n共に戦うために！", 6, 0, 1, 122, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 100301, 1, "食事は大切だけど\n食べ過ぎには\n注意しておこう…", 7, 0, 1, 123, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 100301, 1, "ユイ…！\nこ、こんな本を\n読んでいたのか…！", 8, 0, 1, 124, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 100302, 1, "海だけじゃなくて\n川でも釣りがしたいな\n道具も新調しよう", 1, 0, 1, 126, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 100302, 1, "ツムギが作ってくれた\nこの服…一体\nいつサイズを…？", 2, 0, 1, 127, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 100302, 1, "彼は\nどこまで強くなるのか\n私も負けられないな", 3, 0, 1, 128, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 100302, 1, "そろそろ修行の\n内容を新しくしよう\nより厳しいものにね", 4, 0, 1, 129, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 100302, 1, "強くなって…誰にも\n妨げられない自由な\n生き方がしたい", 5, 0, 1, 130, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 100302, 1, "まさか風邪を引くとは\n彼の看病のおかげで\nすっかり良く\nなったけど", 101, 0, 8, 131, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 100302, 1, "私が一本を取られる\nなんてね…願い事は\nもう決まったのかな？", 102, 0, 8, 132, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 100302, 1, "どうしたの？\nなんでもない？\nそれなら良かった", 1, 0, 1, 133, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 100302, 1, "あんまり突っつくと\nお返しするよ？\n覚悟は出来てる？", 2, 0, 1, 134, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 100302, 1, "ねぇ、今度また\n釣りに行かない？\n大きいのを狙おう", 3, 0, 1, 135, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 100302, 1, "修行がしたいの？\nそれを早く言ってよ\nすぐに出発しよう！", 4, 0, 1, 136, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 100302, 1, "ひょっとして\n今のも一本だなんて\n言わないよね？", 101, 0, 8, 137, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 100302, 1, "キミにはこれからも\n私のそばに…\nいや、なんでもない", 102, 0, 8, 138, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 100302, 1, "今日は仕事？\nそれとも修行？\n良ければ付き合うよ", 1, 1, 1, 139, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 100302, 1, "休息も時には\n必要だと、仲間から\n教わったよ", 2, 2, 1, 140, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 100302, 1, "遅くまで大変だね\nまだ仕事があるなら\n手伝おうか？", 3, 3, 1, 141, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 100302, 1, "髪の長さを少し\n変えてみようかな…\nわ、私は何を…！", 1, 0, 1, 142, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 100302, 1, "休息があってこそ\n万全な状態で\n戦えるんだ", 2, 0, 1, 143, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 100302, 1, "ヒヨリみたいに\n練習相手に…\nいや、やめておこう", 3, 0, 1, 144, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 100302, 1, "体と同様に\n心もほぐれるね…\nはぁ…", 4, 0, 1, 145, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 100302, 1, "休む時は休む\nヒヨリもそろそろ\n覚えて欲しいな…", 5, 0, 1, 146, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 100302, 1, "彼に負けないよう\n私も更に強くなろう\n共に戦うために！", 6, 0, 1, 147, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 100302, 1, "修行のせいか\n最近食べる量が\n増えてきたような…", 7, 0, 1, 148, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 100302, 1, "ユイ…！\nこ、こんな本を\n読んでいたのか…！", 8, 0, 1, 149, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 100401, 1, "お宝の予感！\n部屋中を\n探検しちゃうぞ～", 1, 0, 1, 151, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 100401, 1, "落とし穴…どこに\n作ったんだっけ？", 2, 0, 1, 152, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 100401, 1, "うえ～ん！\n…嘘泣きしたら\n相手は油断するかな", 3, 0, 1, 153, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 100401, 1, "ハニートラップって\nすごく甘いお菓子で\nイタズラするのかな？", 4, 0, 1, 154, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 100401, 1, "かくれんぼする人\nこの指、と～まれ！", 5, 0, 1, 155, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 100401, 1, "ミソギ以外の人に\nイタズラされちゃ\nダメだよ！", 101, 0, 4, 156, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 100401, 1, "ミソギと遊ぶ？\nミソギに遊ばれる？", 102, 0, 8, 157, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 100401, 1, "いいこと思いついた！\nい、イタズラとかじゃ\nないよ～", 1, 0, 1, 158, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 100401, 1, "うわっ！？\n　にいちゃんが、\nミソギにイタズラ？", 2, 0, 1, 159, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 100401, 1, "にいちゃんの\n通りそうなところに\nバナナの皮を…", 3, 0, 1, 160, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 100401, 1, "にいちゃんのオデコと\nホッペと鼻と目と耳に\n落書きを…", 4, 0, 1, 161, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 100401, 1, "ここ、ミソギ探検隊の\n基地にしようよ！", 101, 0, 4, 162, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 100401, 1, "だ～れだ…って？\nにいちゃんの声くらい\nわかるよ！\n大好きだもん♪", 102, 0, 8, 163, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 100401, 1, "にいちゃん、おはよ！\n目覚まし時計を\n止めておいたのに、\nよく起きられたね～♪", 1, 1, 1, 164, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 100401, 1, "にいちゃんが\n昼寝したら、\nイタズラし放題♪", 2, 2, 1, 165, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 100401, 1, "夜の探検も\nおもしろそう！\nねぇ出かけちゃダメ？", 3, 3, 1, 166, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 100401, 1, "イタズラには\nお色気も必要…\nうん、完璧！", 1, 0, 1, 167, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 100401, 1, "座ったら、ブーッって\nいう音が出たら、\nおもしろいな～♪", 2, 0, 1, 168, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 100401, 1, "かわいい！\nミソギ探検隊の\nマスコットにしよう♪", 3, 0, 1, 169, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 100401, 1, "吸うとくしゃみが\n止まらなくなるお香を\nここに…くしゅっ！", 4, 0, 1, 170, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 100401, 1, "にひひ…イタズラの\n証拠、ここで\n燃やしちゃおう～！", 5, 0, 1, 171, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 100401, 1, "探検家は\n体力勝負だよー！", 6, 0, 1, 172, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 100401, 1, "みんなに分けて\nあげようかな～\n激辛ソースも\n入れちゃうけどね！", 7, 0, 1, 173, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 100401, 1, "こういう所に\n宝の地図が\n隠れてるんだよね～", 8, 0, 1, 174, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 100402, 1, "お宝の予感！\n部屋中を\n探検しちゃうぞ～", 1, 0, 1, 176, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 100402, 1, "うぅ、探検に出たい！\nじっとしてなんか\nいられないよ～！", 2, 0, 1, 177, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 100402, 1, "うえ～ん！\n…嘘泣きしたら\n相手は油断するかな", 3, 0, 1, 178, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 100402, 1, "ハニートラップって\nすごく甘いお菓子で\nイタズラするのかな？", 4, 0, 1, 179, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 100402, 1, "かくれんぼする人\nこの指、と～まれ！", 5, 0, 1, 180, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 100402, 1, "にいちゃんのオデコと\nホッペと鼻と目と耳に\n落書きを…", 101, 0, 8, 181, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 100402, 1, "にいちゃんが喜ぶ\nイタズラ、何かない\nかな～？", 102, 0, 8, 182, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 100402, 1, "ミソギがしかけた\nイタズラ\n見つけられるかな？", 1, 0, 1, 183, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 100402, 1, "探検する？\n冒険する？\nそれとも…\nイタズラされる？", 2, 0, 1, 184, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 100402, 1, "将来の夢は探検家！\n探したい物はないけど", 3, 0, 1, 185, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 100402, 1, "にいちゃんに宿題！\n落とし穴を10個\n掘ること！", 4, 0, 1, 186, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 100402, 1, "だ～れだ…って？\nにいちゃんの声くらい\nわかるよ！\n大好きだもん♪", 101, 0, 8, 187, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 100402, 1, "ここ、ミソギ探検隊の\n基地にしようよ！", 102, 0, 8, 188, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 100402, 1, "にいちゃん\n探検に行こうよ！\n今からなら遭難しても\n夜には帰れるよ！", 1, 1, 1, 189, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 100402, 1, "にいちゃんが昼寝したら\nイタズラし放題♪", 2, 2, 1, 190, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 100402, 1, "夜の探検も\nおもしろそう！\nねぇ出かけちゃダメ？", 3, 3, 1, 191, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 100402, 1, "イタズラには\nお色気も必要…\nうん、完璧！", 1, 0, 1, 192, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 100402, 1, "ゆっくりまったりして\n次のイタズラを\n考えよ～！", 2, 0, 1, 193, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 100402, 1, "かわいい！\nミソギ探検隊の\nマスコットにしよう♪", 3, 0, 1, 194, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 100402, 1, "吸うとくしゃみが\n止まらなくなるお香を\nここに…くしゅっ！", 4, 0, 1, 195, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 100402, 1, "にひひ…イタズラの\n証拠、ここで\n燃やしちゃおう～！", 5, 0, 1, 196, 0);
