INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 100701, 1, "プリン、プリン、\nプリン、プリン、\nプリン、プリン～♪", 1, 0, 1, 251, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 100701, 1, "う～ら～め～し～や～\nなの～♪", 2, 0, 1, 252, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 100701, 1, "プリンがないなら\n死んだほうがマシなの\nあ、もう死んでたの～", 3, 0, 1, 253, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 100701, 1, "幽霊の正体見たり、\nミヤコなの～", 4, 0, 1, 254, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 100701, 1, "ミヤコがいきなり\n消えたら、みんな\n驚くかもなの～♪", 5, 0, 1, 255, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 100701, 1, "アイツがいて、\nプリンもある。\nきっとここが天国なの", 101, 0, 4, 256, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 100701, 1, "アイツはプリンセス\nナイトじゃなくて\nプリンナイトなの～", 102, 0, 8, 257, 0);
