INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 100701, 1, "もし成仏した先に\nプリンがなかったら\n地獄なの～…", 2, 0, 1, 259, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 100701, 1, "右にプリン左にプリン\n後ろにもプリン♪\n前にはオマエなの～！", 3, 0, 1, 260, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 100701, 1, "死んだ人には\nお供え物をするの～\nミヤコにも、プリンを\n毎日お供えするの～", 4, 0, 1, 261, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 100701, 1, "たくさんオマエと\n遊べて、\n成仏しそうなくらい\nうれしいの～！", 101, 0, 4, 262, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 100701, 1, "オマエのこと\n気に入ったからずっと\nとり憑いてやるの～", 102, 0, 8, 263, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 100701, 1, "う～　眠いの…\n二度目の二度寝を\nするの…", 1, 1, 1, 264, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 100701, 1, "お昼ご飯はプリンなの\n３時のおやつも、\nプリンなの～", 2, 2, 1, 265, 0);
