INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 100702, 1, "右にプリン左にプリン\n後ろにもプリン♪\n前にはオマエなの～！", 1, 0, 1, 283, 0);
