INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 100801, 1, "ボクの誕生とともに\n全ての美しさは過去の\n遺物になったんだよ！", 4, 0, 1, 304, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 100801, 1, "うわっ昨日より可愛く\nなってる。明日は\nどうなっちゃう\nのかな？", 5, 0, 1, 305, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 100801, 1, "このボクがいるだけで\nあの人への\nおもてなしだよね", 101, 0, 4, 306, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 100801, 1, "ボクの可愛さは、\nふふ…あの人の\nためのものさ", 102, 0, 8, 307, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 100801, 1, "ねぇ～、ボクって\n可愛い？\nふふ、知ってる☆", 1, 0, 1, 308, 0);
