INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 100801, 1, "ふふ、ボクに触ると\nキミも美しく\nなっちゃうかもよ？", 3, 0, 1, 310, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 100801, 1, "どうかな、\n今日のボク。\n美しい？　可愛い？\nねえ、どっち？", 4, 0, 1, 311, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 100801, 1, "あの人も今、\n可愛いボクのこと\n考えてるかな…", 101, 0, 4, 312, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 100801, 1, "美しい…\nキミの瞳に写るボクは\nより一層きれいだ", 102, 0, 8, 313, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 100801, 1, "美しいボクの\n美しい一日が\n始まるよ！", 1, 1, 1, 314, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 100801, 1, "さーて、午後からも\n可愛く美しく\n華麗に優雅に♪", 2, 2, 1, 315, 0);
