INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 100801, 1, "鏡よ、鏡。この世で\n一番可愛いボクに\nお似合いの人はだれ？", 1, 0, 1, 317, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 100801, 1, "ボクが座ったソファは\n「美しいソファ」に\nランクアップした！", 2, 0, 1, 318, 0);
