INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 100901, 1, "そうだ、ナナカに\nこの間借りた本を\n返さないと…", 4, 0, 1, 354, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 100901, 1, "まずは包帯をとって\n手から龍の形の\n炎を出して…", 5, 0, 1, 355, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 100901, 1, "シグルド…\n今頃何処で何を\nしているのだろう…", 101, 0, 4, 356, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 100901, 1, "最近、彼の姿が\nまぶしく見える…\nどうして…？", 102, 0, 8, 357, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 100901, 1, "辞書を知らないか？\n…探している\n理由は聞くな", 1, 0, 1, 358, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 100901, 1, "そうだ！\n貴様の技にも\n名前をつけてやろう！", 2, 0, 1, 359, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 100901, 1, "私に触れても力を\n奪われないのは\n貴様くらいだぞ？", 3, 0, 1, 360, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 100901, 1, "何事だ？\nまさか我が瞳に\n魅入られたのか？", 4, 0, 1, 361, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 100901, 1, "貴様がそばにいると\nとても安心できる…\n…不思議だ", 101, 0, 4, 362, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 100901, 1, "い、いきなり目の前に\n現れるな！　ドキドキ\nするだろうがぁ！", 102, 0, 8, 363, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 100901, 1, "「覚醒」めたか…\n行くぞ、悪を討つ\n戦いに出発だ！", 1, 1, 1, 364, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 100901, 1, "昼間は「ヤツら」も\n襲ってはこない…\n束の間の休息だ", 2, 2, 1, 365, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 100901, 1, "どうした？　夜はまだ\nこれから…ふぁ…\nね、眠くなどないぞ！", 3, 3, 1, 366, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 100901, 1, "何か足りないな…\n暗黒騎士の紋章を\n作ろうかな", 1, 0, 1, 367, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 100901, 1, "くっ…！\n私を眠りの国へ\n誘うつもりか！", 2, 0, 1, 368, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 100901, 1, "このようなものに\n懐柔されるものか！\n…されるものか！", 3, 0, 1, 369, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 100901, 1, "これを使えば\nもっとそれっぽい\n雰囲気が出そうだ", 4, 0, 1, 370, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 100901, 1, "これが我らの求めた\n安寧か…\n悪くない…", 5, 0, 1, 371, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 100901, 1, "この程度\n前世での激闘に\n比べれば…！", 6, 0, 1, 372, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 100901, 1, "最近金欠でな…\nいやぁありがたい…", 7, 0, 1, 373, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 100901, 1, "ここにも\n前世の記憶が\n眠っていたとは…", 8, 0, 1, 374, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 100902, 1, "「第１３部１章\n閃光のシグルド」\n…ついに登場だな", 1, 0, 1, 376, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 100902, 1, "何故だ…何故\nこうもあっさり金欠に\n逆戻りするのだ！？", 2, 0, 1, 377, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 100902, 1, "やはり転生者という\n設定はカッコイイ…\n天才だな、私！", 3, 0, 1, 378, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 100902, 1, "そうだ、ナナカに\nこの間借りた本を\n返さないと…", 4, 0, 1, 379, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 100902, 1, "そろそろ今日の分の\n魔力を溜めないと\nまた魔力切れが…", 5, 0, 1, 380, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 100902, 1, "最近奴がそばに\nいるだけで、鼓動が\n早くなるのは何故だ？", 101, 0, 1, 381, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 100902, 1, "シ、シグルド…！\nだめだ…妄想でも\n上手く言えない…", 102, 0, 8, 382, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 100902, 1, "なんだ？　新たな\n賞金の情報を\n持ってきたのか？", 1, 0, 8, 383, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 100902, 1, "名乗りやポーズは\nその強度で相手を\n圧倒する技術だ", 2, 0, 1, 384, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 100902, 1, "いいか、魔法の\n詠唱中は絶対に\n声をかけるなよ？", 3, 0, 1, 385, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 100902, 1, "おお、ちょうど今\n貴様専用の設定集を\n作成中だぞ！", 4, 0, 1, 386, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 100902, 1, "貴様がそばにいると\nとても安心できる…\n…不思議だ", 101, 0, 1, 387, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 100902, 1, "い、いきなり目の前に\n現れるな！　ドキドキ\nするだろうがぁ！", 102, 0, 8, 388, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 100902, 1, "夜更かしのせいか\nまだ眠気が…いや！\nこれは組織の罠だ！", 1, 1, 8, 389, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 100902, 1, "平穏な時に\n出来るだけ魔力を\n溜めておかねば…", 2, 2, 1, 390, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 100902, 1, "夜になると、力が\n増した気分になるな\n…なりませんか？", 3, 3, 1, 391, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 100902, 1, "何か足りないな…\n暗黒騎士の紋章を\n作ろうかな", 1, 0, 1, 392, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 100902, 1, "お、おのれ\nヒュプノス…\n私は負けないぞ…", 2, 0, 1, 393, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 100902, 1, "くっくっくっ…\n貴様は特別団員に\nしてやろう", 3, 0, 1, 394, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 100902, 1, "これを使えば\nもっとそれっぽい\n雰囲気が出そうだ", 4, 0, 1, 395, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 100902, 1, "これが我らの求めた\n安寧か…\n悪くない…", 5, 0, 1, 396, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 100902, 1, "新たな戦いに備えて\n魔力だけでなく\n体力も鍛えねば！", 6, 0, 1, 397, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 100902, 1, "食事は魔力に\n変換する体力の\n元になるのだ！", 7, 0, 1, 398, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 100902, 1, "フフフ…\nこれを覚えれば\nもっと強く…！", 8, 0, 1, 399, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 101001, 1, "王子はんほんまに\nはんなりしてて、\n王子はんって感じやわ", 1, 0, 1, 401, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 101001, 1, "この絵本の王子はん、\n王子はんみたいで\nええなぁ♪", 2, 0, 1, 402, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 101001, 1, "うちがガラスの靴を\n落としたら、王子はん\n見つけてくれるやろか", 3, 0, 1, 403, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 101001, 1, "マホマホ王国\n次期国王の王子はん\n…見てみたいわ～", 4, 0, 1, 404, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 101001, 1, "王子はんのために\n糸を紡いで、\n編み物でもしまひょか", 5, 0, 1, 405, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 101001, 1, "マホ姫と王子はん、\n絵本の世界より\nベストカップルや", 101, 0, 4, 406, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 101001, 1, "王子はんと\n一緒にいると\n胸がせわしのー\nうごいてかなん", 102, 0, 8, 407, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 101001, 1, "うちら並んでたら\nお雛様みたいに\n見えると思いまへん？", 1, 0, 1, 408, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 101001, 1, "王子はんを\n国に連れて帰ったら\nみんな大歓迎して\nくれるやろなぁ", 2, 0, 1, 409, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 101001, 1, "王子はんは、\n絵本から出てきた人\nみたいやね～", 3, 0, 1, 410, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 101001, 1, "うちの話し方\nのんびりしてます\nやろか？", 4, 0, 1, 411, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 101001, 1, "マホ姫の手は\nつべたいから、\n王子はんが温めて？", 101, 0, 4, 412, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 101001, 1, "うちがもし悪い魔法に\nかかったら、王子はん\n口づけしてくれはる？", 102, 0, 8, 413, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 101001, 1, "朝は小鳥はんも\nおなかがすいてるから\nよう飛び回りはるねえ", 1, 1, 1, 414, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 101001, 1, "お茶請けはお大福が\nよろしおすけど\nクッキーもええなあ", 2, 2, 1, 415, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 101001, 1, "うち、普段から\n夢見がちやけど…\nほんに眠い時間やわぁ", 3, 3, 1, 416, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 101001, 1, "王子はんに\n会いに行くなら、\nおめかしせな\nあかんなぁ", 1, 0, 1, 417, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 101001, 1, "はんなりした\nええお椅子やわぁ\nほっこりしますなあ", 2, 0, 1, 418, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 101001, 1, "かいらしもんが\nいっぱいで、\nマホマホ王国みたい\nやわぁ", 3, 0, 1, 419, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 101001, 1, "心地ええどす。まるで\nお花畑にいるようやね", 4, 0, 1, 420, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 101001, 1, "ぽかぽかどす。\n心まであったかく\nなりますわぁ", 5, 0, 1, 421, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 101001, 1, "はぁはぁ、お姫はんは\n激しい運動は\nしぃひんもんどす～", 6, 0, 1, 422, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 101001, 1, "もぐもぐ…あんまり\n速う食べられへんけど\n美味しいわぁ", 7, 0, 1, 423, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 101001, 1, "絵本、絵本～\n幸せやわぁ♪", 8, 0, 1, 424, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 101002, 1, "王子はんにとって\n自慢の姫になるえ～", 1, 0, 1, 426, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 101002, 1, "カボチャの馬車に\nガラスの靴…\n欲しいなぁ", 2, 0, 1, 427, 0);
