INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 101002, 1, "絵本読むんも\nええなぁ…", 4, 0, 1, 436, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 101002, 1, "うちがもし悪い魔法に\nかかったら、王子はん\n口づけしてくれはる？", 101, 0, 8, 437, 0);
