INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 101002, 1, "夜に飛んだり鳴いたり\nする鳥はんもおるんよ\n昼は寝てはるんかねぇ", 3, 3, 1, 441, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 101002, 1, "王子はんに\n会いに行くなら\nおめかしせな\nあかんなぁ", 1, 0, 1, 442, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 101002, 1, "王子はんも一緒に\nゆっくりしていっても\nええんよ？", 2, 0, 1, 443, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 101002, 1, "かいらしい子やねぇ\nうちとお友だちに\nなってくれるん？", 3, 0, 1, 444, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 101002, 1, "癒しアイテムって\nほんま、好っきゃわぁ", 4, 0, 1, 445, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 101002, 1, "うちのお城にも\nほしいわぁ。帰ったら\nさっそく準備せな\nあかんなぁ", 5, 0, 1, 446, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 101002, 1, "部屋の中で運動\nできるのも、なかなか\n面白いもんやわぁ", 6, 0, 1, 447, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 101002, 1, "おいひいわ～\nマホマホ王国には\nない味やわぁ", 7, 0, 1, 448, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 101002, 1, "しっかり知識を\n身につけんと\n魔法も使えへん\nからなぁ", 8, 0, 1, 449, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 101101, 1, "１、２、３、４…\nあれ？　いま、\n何段目まで\n編んだっけ？", 1, 0, 1, 451, 0);
