INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 101202, 1, "キミを抱き枕に…\nじょ、冗談冗談！", 102, 0, 8, 532, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 101202, 1, "忘れ物しちゃった\nま、いっか！\nえ、ダメ？", 1, 0, 1, 533, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 101202, 1, "なに？　困ってる？\nちょうど良かった！\n今は体力満タンだよ☆", 2, 0, 1, 534, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 101202, 1, "一緒に\nお出かけしたいなー\nデートしよっ！", 3, 0, 1, 535, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 101202, 1, "うんうん、そうだねっ\n困ってる人がいたら\n見過ごせないよねっ！", 4, 0, 1, 536, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 101202, 1, "むむ～…どう？\nテレパシーで、私の\n気持ちを送ったよ！", 101, 0, 8, 537, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 101202, 1, "う～だんだん眠く…\nねぇ一緒に寝よ？", 102, 0, 8, 538, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 101202, 1, "キミって早起きだね！\n私は寝ようかな～", 1, 1, 1, 539, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 101202, 1, "適度なお昼寝は\n健康にいいんだって！\n８時間くらいかな？", 2, 2, 1, 540, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 101202, 1, "どんなにお昼寝しても\n夜は眠いんだ～", 3, 3, 1, 541, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 101202, 1, "ん～、また寝癖…\nいっつも\n寝てるからかなぁ？", 1, 0, 1, 542, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 101202, 1, "一休み、一休み…\nどうせなら百休み\nくらいしようかな…", 2, 0, 1, 543, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 101202, 1, "これ…可愛い！\nシオリンに\n届けてあげたい！", 3, 0, 1, 544, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 101202, 1, "ん～…気分爽快！\nそれじゃ、おやすみ～\n…すやぁ…", 4, 0, 1, 545, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 101202, 1, "うう～ん、なんだか\nいつもより眠ーく\nなってきたよ…", 5, 0, 1, 546, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 101202, 1, "よーし\n困ってる人のために\nパワーアップ☆", 6, 0, 1, 547, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 101202, 1, "食べてすぐ寝ると\n牛になるんだっけ？\nまぁ、いいかぁ～", 7, 0, 1, 548, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 101202, 1, "睡眠学習って\n本を読みながら\n寝るんだっけ？", 8, 0, 1, 549, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 101601, 1, "モデルは楽しいよ！\nファンの子みんな\n応援してくれるしー！", 1, 0, 1, 551, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 101601, 1, "今日のおやつは\n駄菓子に決定～！\nいただきまーす！", 2, 0, 1, 552, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 101601, 1, "んー、今日のおやつは\n何にしよう？\nやっぱアメ玉かな！", 3, 0, 1, 553, 0);
