INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 101701, 1, "キミへの気持ちを\n踊りで表現\nしてみるさ～", 102, 0, 8, 607, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 101701, 1, "海でひと泳ぎ\nしたいさ～\nキミもどう？", 1, 0, 1, 608, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 101701, 1, "あぎひゃっ！\n急に声かけたら、\nビックリするさ～", 2, 0, 1, 609, 0);
