INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 101701, 1, "空手の型の\n練習するさー\nキミもどう？", 4, 0, 1, 611, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 101701, 1, "よ～し、手をつないで\n踊るさ～！\n恥ずかしがること\nないさ～", 101, 0, 4, 612, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 101701, 1, "キミと一緒だから、\n毎日楽しいこと\nばっかりさー", 102, 0, 8, 613, 0);
