INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 101701, 1, "故郷では見えない星が\n見られるんだねえ。\nなんか不思議さぁ", 3, 3, 1, 616, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 101701, 1, "夢中で踊ってたら、\n髪の毛ぼさぼさに\nなってるさ～！？", 1, 0, 1, 617, 0);
