INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 102102, 1, "戯曲を読んで\n自分が演じるところを\n想像するの…", 8, 0, 1, 799, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 102201, 1, "ねえ、人見知りを\n治すための練習に\n付き合ってくれない？", 1, 0, 1, 801, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 102201, 1, "アカリみたいに\nかわいくなるには、\nどうすればいいのかな", 2, 0, 1, 802, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 102201, 1, "レアカードをゲット！\nねえねえ、\nすごいでしょ！？", 3, 0, 1, 803, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 102201, 1, "ア、アイツと同じ\n部屋にいるのよね…\nって、だから何よ！？", 4, 0, 1, 804, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 102201, 1, "楽しいゲームって\nやめどきが\n見つからないわ", 5, 0, 1, 805, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 102201, 1, "もっと女の子らしく\nなったらアイツも\n嬉しいかな…って、\n何考えてるの！？", 101, 0, 4, 806, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 102201, 1, "うぅ…最近、\nアイツを見るとなんか\nドキドキするのよね", 102, 0, 8, 807, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 102201, 1, "ゲームに負けた罰\nだからって、\nくすぐるのは…\nあはは…ダメだって！", 1, 0, 1, 808, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 102201, 1, "アンタと話すのは割と\n緊張しないのよね\n…なんでかな", 2, 0, 1, 809, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 102201, 1, "待ちきれない…\n新作ゲームの発売日が\n待ちきれない…", 3, 0, 1, 810, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 102201, 1, "アンタは人見知り\nしないタチよね？\nいいなあ～…", 4, 0, 1, 811, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 102201, 1, "ア、アンタってさ\n好きな人とかい…\nなな、何でもない！", 101, 0, 4, 812, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 102201, 1, "アンタと…そ、その\nこうして話してるのも\nわわ、悪くないわね！", 102, 0, 8, 813, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 102201, 1, "おおおおはよう！\n…よし！　ちゃんと\nあいさつできたわ！", 1, 1, 1, 814, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 102201, 1, "一緒にごはんとか…\nううん、やっぱいい！\nなんでもないからっ！", 2, 2, 1, 815, 0);
