INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 102201, 1, "ふむふむ、なるほど\n友だちを作るには\n笑顔が大事っと…", 8, 0, 1, 824, 0);
