INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 102301, 1, "ぷうきちがいれば、\nなんでも出来ちゃうよ", 2, 0, 1, 852, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 102301, 1, "発声練習しよっと\nぷうきち\nフルスイ～ング！", 3, 0, 1, 853, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 102301, 1, "ここで、ぷうきち\nフルスイングは、\nまずいかな～？", 4, 0, 1, 854, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 102301, 1, "この部屋、ぷうきちを\n置く場所はないのかな", 5, 0, 1, 855, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 102301, 1, "お兄ちゃんって…\nちょっとぷうきちに\n似てるかも♪", 101, 0, 4, 856, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 102301, 1, "お兄ちゃんにも、\nぷうきちのこと\n好きになって\nもらいたいな～", 102, 0, 8, 857, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 102301, 1, "おやつは、\n私とぷうきちの\n二人分用意してね♪", 1, 0, 1, 858, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 102301, 1, "ぷうきちが、お腹\nペコペコだって～\n何かちょうだい！", 2, 0, 1, 859, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 102301, 1, "『アヤネに手出したら\n…坊ちゃんでも、\n分かってんな？』", 3, 0, 1, 860, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 102301, 1, "ぷうきちとの\n連携を磨いてるの！\nもっと仲良くなるよ！", 4, 0, 1, 861, 0);
