INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 102301, 1, "ふぁ～…ぷうきちと\nお昼寝してくるね…", 2, 2, 1, 865, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (2, 102301, 1, "ぷうきちと\nおしゃべりしてたら\nもうこんな時間…", 3, 3, 1, 866, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 102301, 1, "『座り心地がいいな』\nぷうきちは座れない\nでしょ？", 2, 0, 1, 868, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 102301, 1, "かわいい～！\nぷうきちのお友達に\nしちゃおうかな？", 3, 0, 1, 869, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 102301, 1, "いいニオイ～♪\nぷうきちも\n気に入ったみたい！", 4, 0, 1, 870, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 102301, 1, "ぽわぽわする～\nぷうきちも\n気持ちいい？", 5, 0, 1, 871, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 102301, 1, "おいしい～\nぷうきちも\n食べてみる？", 7, 0, 1, 873, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 102301, 1, "ぷうきち！\nこの本、読んで～！", 8, 0, 1, 874, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 102501, 1, "どうして失敗ばっかり\nこうなったら\n腹を切るしか～！", 1, 0, 1, 901, 0);
