INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 102702, 1, "この運動エネルギー\n何かにうまく\n利用できないかしら？", 6, 0, 1, 997, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 102702, 1, "美味しい。あの方にも\n差し上げましょう…\n薬を入れて…", 7, 0, 1, 998, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (3, 102702, 1, "あの方を虜にする\n方法がどこかに\n載ってるはずですわ", 8, 0, 1, 999, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 102801, 1, "子供たちの遊びって\nあたしが子供の頃と\nあまり変わらないのね", 1, 0, 1, 1001, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 102801, 1, "使えるものは\n再利用しないと\n…あ、これとか\nまだ使えるわね", 2, 0, 1, 1002, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 102801, 1, "贅沢は敵！　とはいえ\n気持ちに余裕のない\n暮らしはダメよね", 3, 0, 1, 1003, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 102801, 1, "お腹空いたわね、\n残り物で\n何か作ろうかしら", 4, 0, 1, 1004, 0);
INSERT INTO `v1_224027f751057102ecc0a386bfaa31ea8dbf13cd879b6addb5e712bebbbba447` VALUES (1, 102801, 1, "商売はノンゼロサムが\n理想だなぁ。誰も損を\nしないってことだもの", 5, 0, 1, 1005, 0);
